INSERT INTO `jos_content` VALUES (1, 'Đội hợp ca của Siu Black chia tay trong nước mắt', 'doi-hop-ca-cua-siu-black-chia-tay-trong-nuoc-mat', '', '<p>Sau một tuần bình chọn kể từ đêm thi thứ 2, đêm công bố kết quả của Hợp ca tranh tài đã diễn ra vào tối thứ 6 ngày 23/3/2012, trực tiếp trên VTV3 - Đài truyền hình Việt Nam với sự chia tay của đội Siu Back.\r\n', '\r\n</p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Thời  gian luyện tập và sống chung trong ngôi nhà Hợp ca tranh tài của các  đội ngày càng dài vì vậy việc phải chia tay một đội ngày càng khó khăn  cho chính đội “ra đi” và cho các đội còn lại. Trong đêm công bố kết quả,  dù được đánh giá là có cố gắng rất nhiều và đem đến một phần trình diễn  ấn tượng trong đêm thi thứ 2 nhưng đội hợp ca Buôn Ma Thuột của ca sĩ  Siu Black vẫn phải nói lời chia tay khán giả. Đây là đội thứ 2 chia tay  chương trình sau đội hợp ca Thanh Hóa của ca sĩ Phương Thanh.</span></p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Mở  đầu đêm công bố kết quả lần thứ 2 là một phần biểu diễn kết hợp đặc  biệt của 3 đội hợp ca Huế, thành phố Hồ Chí Minh, Hà Nội. Ca khúc “Huế,  Sài Gòn, Hà Nội” của cố nhạc sĩ Trịnh Công Sơn được giám đốc âm nhạc -  nhạc sĩ Thanh Tâm phối khí lại khá ấn tượng. Ba đội hợp ca đã đại diện  cho ba miền Bắc - Trung - Nam hát vang ca khúc để tưởng nhớ cố nhạc sĩ  Trịnh Công Sơn. </span></p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;"> </span></p>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca1_e9ad7.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">Siu Black đã thoả được "ước mơ" nâng bổng của mình</span></div>\r\n<p><span style="font-size: 10pt;"> </span></p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Sau  phần biểu diễn của 3 đội hợp ca Huế - Thành phố Hồ Chí Minh - Hà Nội là  sự xuất hiện máu lửa của đội hợp ca Buôn Ma Thuột với ca khúc <span style="font-style: italic;">Ly ca phê Ban Mê</span> của nhạc sĩ Nguyễn Cường. Đây là ca khúc đã tạo nên tên tuổi ca sĩ Siu  Black và với phần thể hiện độc đáo của đội hợp ca Buôn Ma Thuột ca khúc  này lại gây bất ngờ với khán giả một lần nữa. </span></p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Trong đêm thi thứ 2, sau phần thi của đội hợp ca Sài Gòn với ca khúc <span style="font-style: italic;">Vũ điệu hoang dã</span>,  ca sĩ Siu Black đã từng thổ lộ rất mong muốn được “nâng bổng” lên giống  một thành viên nữ trong đội hợp ca này nhưng “có lẽ tôi cần giảm cân  chứ nguyên giàn nam không thể bưng tôi nổi”. Trong tiết mục biểu diễn  ngày hôm nay Siu Black đã thực sự được thỏa mãn mong ước của mình. </span></p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Đức  Tuấn tỏ ra rất hài hước khi nhận xét về phần biểu diễn của đội Buôn Ma  Thuột: “Phải công nhận tiết mục vừa rồi thể hiện sự khỏe khoắn của núi  rừng như thế nào…chúc mừng chị Siu đã có được trong tay đội hình rất  mạnh, cả nghĩa đen lẫn nghĩa bóng”.</span></p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;"> </span></p>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca10_65ecf.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">Nhưng đội của chị đã bị loại</span></div>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Tuy  nhiên, những nhận xét tốt của các ca sĩ cũng không giúp được đội của  Siu Black ở lại với cuộc thi. Những giọt nước mắt trên gương mặt những  cô gái và những cái quệt tay gạt nước mắt của những chàng trai Hợp ca  Buôn Ma Thuột khi mỉm cười hát: “Cùng nhau ta hát, hát cho cuộc đời.  Cùng nhau ta hát, hát cho ngày mai. Cùng nhau ta hát, hát cho bao đam  mê, hát cho bao khát vọng, hát cho quê hương tôi và hát cho mọi người”  đã khép lại đêm công bố kết quả đội thứ 2.</span></p>\r\n<p class="MsoNormal" style="text-align: left;"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Đội  của Siu Black đã bị loại, theo BTC thì toàn bộ số tiền mà đội hợp ca  Buôn ma Thuột có được sau 2 tuần bình chọn qua cổng thông tin điện tử  nhân đạo quốc gia sẽ được dùng làm từ thiện cho các em nhỏ mồ côi, người  già neo đơn tại chùa Bửu Thắng - Buôn Ma Thuột - quê hương của “họa mi  núi rừng” và các thành viên của đội.</span></p>\r\n<div class="MsoNormal" style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca2_c256a.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div class="MsoNormal" style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">Đội của Siu Black rất tự tin sau khi hoàn thành phần thi</span></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca5_53469.jpg" border="0" width="400" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">Trong khi chờ đợi kết quảng, Siu Black tranh thủ khiêu vũ với Đức Tuấn</span></div>\r\n<div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca9_16c72.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n</div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca6_b1860.jpg" border="0" width="400" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">Đội hợp ca Buôn Ma Thuột hồi hộp đợi kết quả</span></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca3_6dc01.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca4_c5ecc.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca7_79e2d.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca8_5b383.jpg" border="0" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">Nụ cười của đội thắng</span></div>\r\n<div style="text-align: center;"></div>\r\n<div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca11_facb7.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca12_63820.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca13_36771.jpg" border="0" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">...và nước mắt của đội thua.</span></div>\r\n</div>\r\n<p> </p>\r\n<p class="MsoNormal" style="text-align: right;"><strong>Theo Dantri.com</strong><span style="font-weight: bold;"></span></p>', 1, 4, 0, 29, '2012-03-24 13:35:56', 62, '', '2012-03-24 13:42:11', 62, 0, '0000-00-00 00:00:00', '2012-03-24 13:35:56', '0000-00-00 00:00:00', 'hopca10_65ecf.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 7, 0, 7, '', '', 0, 1, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (2, 'Rihanna hẹn hò với Ashton Kutcher', 'rihanna-hen-ho-voi-ashton-kutcher', '', '<p>Nữ ca sỹ da màu Rihanna đã bị bắt gặp khi rời nhà Ashton Kutcher lúc nửa đêm…\r\n', '\r\n</p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;">Cách  đây ít ngày, cánh săn tin đã bắt gặp Rihanna tới nhà nam diễn viên vừa  bỏ vợ Ashton Kutcher từ tối và rời đi một cách bí mật vào lúc 4 giờ sáng  hôm sau.</span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;">Nhiều  nguồn tin cho hay, Rihanna và nam diễn viên điển trai 34 tuổi đã bí mật  hẹn hò với nhau 8 tuần nay và Rihanna mêm mẩn ngôi sao phim Yêu là  cưới.</span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></p>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;">“Rihanna  và Ashton gặp gỡ trong một bữa tiệc. Họ cho nhau số điện thoại và sau  đó thì nhắn tin và gọi điện cho nhau thường xuyên. Cách đây vài tuần, họ  hẹn hò với nhau tại một khách sạn ở Santa Monica. Họ chỉ đơn giản là  hai người độc thân bắt đầu tìm hiểu nhau,” một nguồn tin cho hay.</span></div>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"></div>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/3/4/riha2431210_e76a5.jpg" border="0" /><br /><span style="font-family: Tahoma; font-size: 10pt;">Rihanna hẹn hò bí mật với Ashton Kutcher<br /></span></span></div>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;">“Rihanna  nói với bạn bè rằng Ashton Kutcher là người hài hước và thú vị. Tuy  nhiên, lúc này cả hai người ấy đều quá bận rộn nên chưa tính tới chuyện  nghiêm túc hơn. Rihanna cũng đang bắt đầu theo đạo Kabbalah cùng Ashton  Kutcher.”</span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;">Một  nguồn tin khác thân cận với Ashton Kutcher lại nói, nam diễn viên 34  tuổi chưa muốn công khai chuyện tình rộng rãi vì không muốn sức khỏe của  vợ cũ Demi Moore bị ảnh hưởng khi mà cô chỉ vừa trở về từ trung tâm cai  nghiện và điều trị sức khỏe.</span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></p>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;">Demi  Moore suy sụp nặng nề sau khi Ashton Kutcher bị tố ngoại tình. Kể từ  đó, cô phải đi điều trị tâm lý và sức khỏe sau vụ đi cấp cứu hồi tháng  1.</span></div>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"></div>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/3/4/riha2431211_8c1cb.jpg" border="0" /></span></div>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: Tahoma; font-size: 10pt;">Ashton - Demi ngày còn hạnh phúc</span></div>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></div>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></p>\r\n<p style="text-align: right;"><strong>Theo Dantri.com</strong><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt; font-weight: bold;"></span></p>', 1, 4, 0, 29, '2012-03-24 13:50:34', 62, '', '2012-03-24 13:53:13', 62, 0, '0000-00-00 00:00:00', '2012-03-24 13:50:34', '0000-00-00 00:00:00', 'riha2431210_e76a5.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 6, '', '', 0, 1, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (3, 'Candice Swanepoel khoe vóc dáng siêu quyến rũ', 'candice-swanepoel-khoe-voc-dang-sieu-quyen-ru', '', '<p>Thiên thần của Victoria''s Secret Candice Swanepoel mặc gì cũng đẹp...\r\n', '\r\n</p>\r\n<div><span style="font-family: Tahoma; font-size: 10pt;"> </span></div>\r\n<div><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331210_d25d8.jpg" border="0" /><br />Candice Swanepoel quảng cáo sản phẩm mới của Victoria''s Secret tại Dallas </span></div>\r\n<div><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331211_df151.jpg" border="0" /><br />Siêu mẫu Nam Phi diện váy trắng hiệu Herve Leger </span></div>\r\n<div><span style="font-family: Tahoma; font-size: 10pt;"> </span></div>\r\n<div><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331212_53254.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></span></div>\r\n<div><span style="font-family: Tahoma; font-size: 10pt;">Chân dài 24 tuổi dành nhiều thời gian chụp hình với người hâm mộ</span></div>\r\n<div><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331213_60580.jpg" border="0" /><br />Candice Swanepoel khoe vóc dáng siêu quyến rũ</span></div>\r\n<div><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331214_b7e5d.jpg" border="0" /><br />Siêu mẫu tóc vàng tự tin và tạo dáng rất thoải mái</span></div>\r\n<div><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331215_1b5dd.jpg" border="0" /><br /><br /><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2