hor=');
INSERT INTO `jos_content` VALUES (46, 'Panasonic Eluga lên kệ từ tháng 4 năm nay', 'panasonic-eluga-len-ke-tu-thang-4-nam-nay', '', '<p>Máy sẽ được cập nhật Android Ice Cream Sandwich ngay trong quý II.', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Eluga được đánh giá rất cao về thiết kế. Ảnh: Pocket-lint." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5C/panasonic-eluga-pictures-hands-on-7.jpg" border="1" height="296" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Eluga được đánh giá rất cao về thiết kế. Ảnh: Pocket-lint.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Panasonic giới thiệu smartphone <a href="http://sohoa.vnexpress.net/sh/dien-thoai/smartphone/2012/02/3b9b5955/Panasonic-tiet-lo-ve-smartphone-Eluga-cho-chau-Au" class="Normal"><span style="color: #0000ff;" color="#0000ff">Eluga</span></a> tại MWC 2012, đánh dấu bước trở lại ngành công nghiệp di động của hãng tại thị trường ngoài Nhật Bản. Đây không phải là model có sức cuốn hút nhất, nhưng phong cách thiết kế của Panasonic đã nhận được rất nhiều lời khen ngợi.</p>\r\n<p class="Normal">Hãng vừa thông báo sản phẩm sẽ có mặt tại Nhật Bản vào ngày 29 tháng này, do nhà mạng NTT DoCoMo phân phối. Trang web của Panasonic châu Âu cũng xác nhận khu vực sẽ có máy trong tháng 4 tới.</p>\r\n<p class="Normal">Sản phẩm sử dụng bộ xử lý lõi kép OMAP 4430 1GHz của Texas Instruments, chạy Android 2.3.5 Gingerbread và sẽ nâng cấp lên Ice Cream Sandwich trong quý II. Máy trang bị camera 8 Megapixel và diện tích dùng để hiển thị chiếm tới 66% toàn bộ thân. Eluga cũng được tích hợp công nghệ NFC đang có mặt trên các smartphone cao cấp hiện nay.</p>\r\n<p class="Normal" align="right"><strong>Anh Quân</strong></p>', 1, 6, 0, 22, '2012-03-24 16:50:02', 62, '', '2012-03-24 16:50:47', 62, 0, '0000-00-00 00:00:00', '2012-03-24 16:50:02', '0000-00-00 00:00:00', 'panasonic-eluga-pictures-hands-on-7.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 4, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (47, 'Sky Vega 4G lõi kép, màn HD giá 9 triệu', 'sky-vega-4g-loi-kep-man-hd-gia-9-trieu', '', '<p>Model tới từ Hàn Quốc chạy Android 2.3 với cấu hình lõi kép 1,5GHz, màn hình HD 4,5 inch và đi kèm giá bán khá hấp dẫn ở thị trường xách tay.', '</p>\r\n<p class="Normal">Tên đầy đủ của model này là Sky Vega 4G LTE IM-A800S và được sản xuất bởi Pantech (Hàn Quốc). Tuy nhiên, model này có thể sử dụng tốt với tất cả các mạng di động GSM ở Việt Nam, hỗ trợ cả 3G. Việc có nguồn gốc xuất xứ từ nhà mạng tại Hàn Quốc và về Việt Nam theo dạng xách tay nên máy giá rẻ, anh Dũng đại diện của cửa hàng Sinh Thủy (Thanh Xuân, Hà Nội) chia sẻ.</p>\r\n<p class="Normal">Sky Vega 4G được trang bị màn hình 4,5 inch với độ phân giải HD 1.280 x 800 pixel, sử dụng công nghệ LCD IPS, mật độ điểm ảnh 335 ppi, cao hơn iPhone 4S và thấp hơn một chút so với Xperia S. Về cấu hình, model được trang bị vi xử lý Snapdragon APQ8060 lõi kép tốc độ 1,5 GHz, bộ nhớ RAM 1GB, bộ nhớ trong 16GB nhưng vẫn có thể mở rộng với thẻ nhớ microSD.</p>\r\n<p class="Normal">Những trang bị ấn tượng khác ở Sky Vega IM-A800S là camera 8 Megapixel hỗ trợ quay video Full HD. Giống như những model Galaxy S II hay Galaxy Note của Samsung, Pantech cũng tích hợp cho IM-A800S bộ giải mã DivX cho phép xử lý tốt các nội dung HD. Tuy đi kèm với một bộ thu tín hiệu truyền hình số DMB-T nhỏ gắn thêm vào cổng microUSB nhưng tính năng này đã bị vô hiệu ở Việt Nam.</p>\r\n<p class="Normal"><strong>Dưới đây là những hình ảnh thực tế về Sky Vega 4G LTE IM-A800S giá 9 triệu.</strong></p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Vega 4G LTE IM-A800 S được phân phối bởi nhà mạng Sky Hàn Quốc và do Pantech sản xuất. Hộp đựng máy thiết kế đơn giản và gọn." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5F/Sky-Vega-4G-15.jpg" border="1" height="344" width="500" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Vega 4G LTE IM-A800 S được phân phối bởi nhà mạng Sky Hàn Quốc và do Pantech sản xuất. Hộp đựng máy thiết kế đơn giản và gọn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Một số tính năng trình chiếu HD nổi bật được giới thiệu ở mặt sau của hộp như hỗ trợ DTS 2.0, DivX HD, Dolby Digital Plus." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5F/Sky-Vega-4G-2.jpg" border="1" height="345" width="500" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một số tính năng trình chiếu HD nổi bật được giới thiệu ở mặt sau của hộp như hỗ trợ DTS 2.0, DivX HD, Dolby Digital Plus.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Ngoài sạc, tai nghe Sky Vega 4G IM-A800S còn được tặng kèm thêm 1 pin, 1 sạc rời và một thiết bị nhỏ gọn có nhiệm vụ thu sóng truyền hình số DMB-T." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5F/Sky-Vega-4G-4.jpg" border="1" height="343" width="500" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ngoài sạc, tai nghe Sky Vega 4G IM-A800S còn được tặng kèm thêm một pin, một sạc rời và một thiết bị nhỏ gọn có nhiệm vụ thu sóng truyền hình số DMB-T.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Sky Vega 4G IM-A800S được trang bị vi xử lý lõi kép tốc độ 1,5 GHz, RAM 1GB, bộ nhớ trong 16GB và chạy Android 2.3 với giao diện đã được tùy biến. Mức giá 9 triệu đồng được cho là rất hấp dẫn so với cấu hình." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5F/Sky-Vega-4G-11.jpg" border="1" height="334" width="500" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sky Vega 4G IM-A800S được trang bị vi xử lý Snapdragon APQ8060 lõi kép tốc độ 1,5 GHz, RAM 1GB, bộ nhớ trong 16GB và chạy Android 2.3 với giao diện đã được tùy biến. Mức giá 9 triệu đồng cho một model mới tinh tỏ ra hấp dẫn so với cấu hình và tính năng sản phẩm mang lại.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Màn hình công nghệ LCD IPS với kích thước 4,5 inch và độ phân giải HD 1.280 x 800 pixel là điểm nổi bật trên IM-A800S." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5F/Sky-Vega-4G-13.jpg" border="1" height="334" width="500" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Màn hình công nghệ LCD IPS với kích thước 4,5 inch và độ phân giải HD 1.280 x 800 pixel là điểm nổi bật khác trên IM-A800S.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Ba nút cảm ứng nằm ở bên dưới, trong đó nút Home ẩn bên dưới logo Sky." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5F/Sky-Vega-4G-12.jpg" border="1" height="333" width="500" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ba nút cảm ứng nằm ở bên dưới, trong đó nút Home ẩn bên dưới logo Sky.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Phía trên màn hình là cảm biến và camera phụ. Một tính năng thú vị trên Sky Vega 4G là Motion Recognition cho phép người dùng không cần chạm mà chỉ cần quét tay qua màn hình để chuyển nhạc, chuyển ảnh hay từ chối cuộc gọi..." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5F/Sky-Vega-4G-14.jpg" border="1" height="338" width="500" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Phía trên màn hình là cảm biến và camera phụ. Một tính năng thú vị trên Sky Vega 4G là Motion Recognition cho phép người dùng không cần chạm mà chỉ cần quét tay qua màn hình để chuyển nhạc, chuyển ảnh hay từ chối cuộc gọi...</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Mặt sau được làm từ nhựa và chăm chút khá kỹ càng. Viền bao quanh Camera 8 Megapixel bằng kim loại với các chấm nhỏ bao quanh." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5F/Sky-Vega-4G-10.jpg" border="1" height="334" width="500" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mặt sau được làm từ nhựa và chăm chút khá kỹ càng. Viền bao quanh Camera 8 Megapixel bằng kim loại với các chấm nhỏ bao quanh.</td>\r\n</tr>\r\n</tbody>\r\n</table>', 1, 6, 0, 22, '2012-03-24 16:55:47', 62, '', '2012-03-24 16:56:47', 62, 0, '0000-00-00 00:00:00', '2012-03-24 16:55:47', '0000-00-00 00:00:00', 'sky-vega-4g-4.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (48, 'LG Optimus LTE mới lộ diện', 'lg-optimus-lte-moi-lo-dien', '', '<p>Hiện chưa rõ thị trường mà LG nhắm đến cho sản phẩm mới chưa hề được công bố của mình.', '</p>\r\n<p class="Normal">LG vừa có thêm một thành viên mới trong gia đình Optimus LTE của mình, với tên gọi LG Optimus LTE P936. Mẫu điện thoại chưa được công bố chính thức, tuy nhiên trang <em>GSM Dome</em> đã cho đăng ảnh thực tế của smartphone này.</p>\r\n<p class="Normal">LG Optimus LTE P936 trông khá giống với LG Optimus LTE SU640 (bán tại thị trường Hàn Quốc), cả về cấu hình lẫn thiết kế. Máy sử dụng màn hình LCD công nghệ AH-IPS, độ phân giải 720p, bộ xử lý lõi kép Snapdragon 1,5GHz, camera 8MP quay phim 1080p.</p>\r\n<p class="Normal">Theo dự đoán, máy sẽ hướng đến thị trường Mỹ. Tuy nhiên, cũng có ý kiến cho rằng P936 sẽ là phiên bản quốc tế của mẫu Optimus LTE, chủ yếu cho thị trường châu Âu do nền mạng LTE khu vực này vẫn nhận được nhiều sự quan tâm của LG.</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Điểm benchmark của máy khá cao." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/69/p936.jpg" border="1" height="375" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Điểm benchmark của máy khá cao.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Thiết bị sử dụng hệ điều hành Android 2.3 khi ra mắt." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/69/p936-1.jpg" border="1" height="375" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Thiết bị sử dụng hệ điều hành Android 2.3 khi ra mắt.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Màn hình LCD, độ phân giải HD 720p." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/69/p936-3.jpg" border="1" height="400" width="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Màn hình LCD, độ phân giải HD 720p.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Mặt lưng có thiết kế giống với chất liệu carbon." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/69/p936-2.jpg" border="1" height="375" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mặt lưng có thiết kế giống với chất liệu carbon.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong>Anh Quân</strong></p>', 1, 6, 0, 22, '2012-03-24 16:59:29', 62, '', '2012-03-24 17:00:40', 62, 0, '0000-00-00 00:00:00', '2012-03-24 16:59:29', '0000-00-00 00:00:00', 'p936.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (49, 'Regal Black - điện thoại thuyền siêu tốc từ Gresso', 'regal-black-dien-thoai-thuyen-sieu-toc-tu-gresso', '', '<p>Thân máy được làm từ titanium nguyên khối, hai mặt trước sau bọc kính, nhưng chỉ sử dụng S40 của Nokia, thay vì có hệ điều hành.', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Sản phẩm lấy cảm hứng từ thuyền siêu tốc. Ảnh: Gresso." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/6B/regal_black.jpg" border="1" height="284" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sản phẩm lấy cảm hứng từ thuyền siêu tốc. Ảnh: Gresso.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Gresso, công ty sản xuất điện thoại siêu sang vừa giới thiệu thêm một model vào bộ sưu tập Avantgarde của mình. Mẫu điện thoại mới làm trên cảm hứng từ một chiếc thuyền siêu tốc, và có lẽ mức giá đưa ra cũng chỉ những ai đang sở hữu một món đồ như vậy mới "chịu" được.</p>\r\n<p class="Normal">Chiếc Gresso Regal Black được tạo thành từ một miếng titanium, là sản phẩm đầu tiên của hãng có thiết kế thân nguyên khối. Bề mặt của sản phẩm được sơn đen và có thiết kế sần mờ.</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Thân làm bằng titanium nguyên khối." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/6B/regal_black1.jpg" border="1" height="275" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Thân làm bằng titanium nguyên khối.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chỉ có khoảng 9% khối kim loại ban đầu được tạo thành thân máy sau quá trình cắt gọt cơ khí. Mặt trước và sau của điện thoại được làm từ kính khoáng nhuộm đen nhờ khâu thiết kế đặc biệt dành riêng cho dòng Regal.</p>\r\n<p class="Normal">Regal Black có kích cỡ 121 x 46 x 12, nặng 130g. Máy sử dụng giao diện S40 của Nokia, thay vì trở thành một smartphone như xu hướng hiện nay. Điểm đặc biệt nhất là Gresso chỉ sản xuất 333 chiếc Regal Black. Máy dự kiến ra mắt vào tháng 4 năm nay, thông qua các cửa hàng bán lẻ ủy quyền của Gresso hoặc bán hàng online. Mỗi chiếc Regal Black có giá 5.000 USD.</p>\r\n<p class="Normal" align="right"><strong>Anh Quân</strong></p>', 1, 6, 0, 22, '2012-03-24 17:05:40', 62, '', '2012-03-24 17:06:32', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:05:40', '0000-00-00 00:00:00', 'regal_black.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 1, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (50, 'Cựu giám đốc ngân hàng Nga nguy kịch sau khi bị bắn ở London', 'cuu-giam-doc-ngan-hang-nga-nguy-kich-sau-khi-bi-ban-o-london', '', '<p>Một cựu giám đốc ngân hàng Nga đang trong tình trạng nguy kịch tại bệnh viện sau khi bị bắn ở phía đông London, Anh.', '</p>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/6DQQJ7yW5QPfG6EzuGal/Image/2012/03/1_d5f1e.jpg" /><br /><span style="font-family: Tahoma; font-size: 10pt;">Ông German Gorbuntsov đã bị trúng vài phát đạn.</span></div>\r\n<div>&nbsp;</div>\r\n<div>Ông German Gorbuntsov bị bắn bởi một người đàn ông mang súng tiểu liên khi ông này đi đi vào một căn hộ chung cư trên phố Byng, đảo Isle of Dogs, phía đông London, hôm 20/3.</div>\r\n<p>Cảnh sát London xem vụ nổ súng là một mưu ám sát.</p>\r\n<p>Nghi phạm là một tay súng da trắng, người đã tẩu thoát theo đường Westferry. Ông Tình trạng sức khoẻ của Gorbuntsov được cho là nguy kịch nhưng đã ổn định và hiện đang được canh gác cẩn mật tại bệnh viện.</p>\r\n<p>Ông Gorbuntsov, trong độ tuổi 40, trước đây từng sở hữu các ngân hàng tại Nga và Moldova, theo báo chí Nga.</p>\r\n<p>Ông này đang trên đường về nhà thì kẻ tấn công nổ súng, tờ <span style="font-style: italic;">Kommersant</span> của Nga đưa tin.</p>\r\n<p>Cảnh sát London từ chối tiết lộ nơi ông Gorbuntsov đang được điều trị. Chưa nghi phạm nào bị bắt giữ.</p>\r\n<p>Đã xuất hiện tin đồn trên báo chí Nga rằng ông Gorbuntsov từng là nhân chứng của một âm mưu ám sát nhằm vào một giám đốc ngân hàng Nga khác, Alexander Antonov, tại Mátxcơva hồi năm 2009.</p>\r\n<p>Aleksander Nekrassov, một cựu cố vấn điện Kremlin, cho biết Gorbuntsov từng là một trong những giám đốc ngân hàng lớn tại Nga nhưng trong vài năm qua ông này đã rút khỏi lĩnh vực ngân hàng và bán các lợi tức tại Nga.</p>\r\n<p>Một tư vấn quản lý 54 tuổi, người sống tại cùng toà nhà với Gorbuntsov và tự xưng là John, cho hay cảnh sát đã nói với ông rằng nạn nhân biết kẻ tấn công.</p>\r\n<p>“Vào khoảng 9h tối hôm đó, cảnh sát đóng cửa khu nhà và cho biết rằng đã xảy một vụ việc, và rằng có một người bị thương nguy kịch và được đưa tới bệnh viện. Họ nói kẻ người bị tấn công biết thủ phạm”, ông John nói.</p>\r\n<p style="text-align: right;"><span style="font-weight: bold;">An Bình<br /></span>Theo <span style="font-style: italic;">BBC</span></p>', 1, 9, 0, 20, '2012-03-24 17:33:29', 62, '', '2012-03-24 17:34:29', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:33:29', '0000-00-00 00:00:00', '1_d5f1e.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 6, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (51, 'Thượng viện CPC bầu lại ông Chea Sim làm chủ tịch', 'thuong-vien-cpc-bau-lai-ong-chea-sim-lam-chu-tich', '', '<p>Theo phóng viên TTXVN tại Phnom Penh, sáng 24/3, tại phiên họp đầu tiên của Thượng viện Campuchia khóa Ba, ông Chea Sim, Chủ tịch đảng Nhân dân Campuchia (CPP) cầm quyền, đã được bầu lại với số phiếu tuyệt đối làm Chủ tịch Thượng viện Campuchia.', '</p>\r\n<p style="margin: 0in 0in 0pt;" align="center">&nbsp;<img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/1_b1397.jpg" /></p>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Chủ tịch Thượng viện Campuchia Chea Sim.</span></div>\r\n<p style="margin: 0in 0in 0pt;" align="center">&nbsp;</p>\r\n<p style="margin: 0in 0in 0pt;">Tại phiên họp này, Thượng viện Campuchia cũng đã bầu ông Say Chhum, Tổng thư ký đảng CPP, nguyên Phó Chủ tịch thứ hai Quốc hội làm Phó Chủ tịch thứ nhất, ông Tep Ngon, một thành viên khác của đảng CPP đã được bầu làm Phó Chủ tịch thứ hai.</p>\r\n<p style="margin: 0in 0in 0pt;">Quyền chủ tịch đảng đối lập Sam Rainsy (SRP), ông Kong Korm, được vào danh sách bầu cử Phó Chủ tịch thứ hai nhưng đã thất cử.</p>\r\n<p style="margin: 0in 0in 0pt;">Trước khi tiến hành bầu cử, Thượng viện Campuchia đã tiến hành phiên khai mạc với sự chủ trì của Quốc vương Norodom Sihamoni. Tham dự phiên khai mạc có Chủ tịch Quốc hội Heng Samrin, Thủ tướng Hun Sen cùng nhiều quan chức cao cấp của Campuchia. Đại sứ nước ta Ngô Anh Dũng và đoàn ngoại giao có mặt tại phiên khai mạc.</p>\r\n<p style="margin: 0in 0in 0pt;">Phát biểu nhân dịp này, Quốc vương Norodom Sihamoni bày tỏ tin tưởng các tân thượng nghị sĩ sẽ hoàn thành trọng trách của cơ quan lập pháp tối cao của Vương quốc, nhằm bảo đảm nguyên tắc cơ bản của một hệ thống dân chủ, giám sát và cân bằng quyền lực nhà nước, bảo đảm thực hiện các thể chế của Hiến pháp, phát triển kinh tế xã hội và bảo vệ quyền lợi cho mọi tầng lớp nhân dân.</p>\r\n<p style="margin: 0in 0in 0pt;">Quốc vương Sihamoni nhấn mạnh cùng với tốc độ cải tổ mạnh mẽ trên mọi lĩnh vực, yêu cầu phải có cơ quan lập pháp mạnh, cũng như sự tôn trọng và thực thi luật pháp một cách nghiêm ngặt. Thượng viện là một trong những nhân tố cơ bản bảo đảm sự ổn định xã hội, hòa bình và phát triển, duy trì độc lập, chủ quyền và toàn vẹn lãnh thổ, văn hóa, phong tục, tập quán, đạo đức xã hội, bảo vệ môi trường và nguồn lực tự nhiên của Campuchia. Với ý nghĩa đó, cùng với Quốc hội, Thượng viện sẽ là nơi ban hành luật ngày càng tốt hơn, đáp ứng những nhu cầu chung của mỗi công dân và toàn dân tộc.</p>\r\n<p style="margin: 0in 0in 0pt;">Thượng viện Campuchia được thành lập ngày 25/3/1999, được bầu 6 năm một lần. Thượng viện nhiệm kỳ hiện nay gồm 61 ghế. Tại cuộc bầu cử ngày 29/3, đảng CPP giành được 46 ghế, đảng SRP được 11 ghế. Trong 4 ghế còn lại, Quốc vương Norodom Sihamoni đã bổ nhiệm 2 thành viên thuộc Hoàng tộc và Quốc hội Campuchia đã bầu 2 thành viên./.</p>\r\n<p style="text-align: right;">Theo <span style="font-style: italic;">TTXVN/Vietnam+</span></p>', 1, 9, 0, 20, '2012-03-24 17:36:06', 62, '', '2012-03-24 17:37:15', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:36:06', '0000-00-00 00:00:00', '1_b1397.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 5, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (52, 'Tàu tuần dương Pháp thăm Thành phố Hồ Chí Minh', 'tau-tuan-duong-phap-tham-thanh-pho-ho-chi-minh', '', '<p>Được phép của Chính phủ Việt Nam, tàu tuần dương Vendémiere thuộc Bộ Tư lệnh các Lực lượng Pháp tại Ấn Độ Dương thăm Thành phố Hồ Chí Minh từ ngày 23 đến ngày 27/3.', '<img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/1_89020.jpg" /></p>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Tàu tuần dương Vendémiere cập cảng Sài Gòn. (Ảnh: Thế Anh/TTXVN)</span></div>\r\n<p style="margin: 0in 0in 0pt;" align="center">&nbsp;</p>\r\n<p style="margin: 0in 0in 0pt;">Trong thời gian ở thăm, chỉ huy, sỹ quan thủy thủ tàu tuần dương Vendemiere có các hoạt động chào xã giao lãnh đạo Uỷ ban nhân dân Thành phố Hồ Chí Minh, Bộ Tư lệnh Quân khu 7, Bộ Tư lệnh Vùng 2 Hải quân.</p>\r\n<p style="margin: 0in 0in 0pt;">Thủy thủ tàu tổ chức thi đấu bóng chuyền với cán bộ, học viên Trường Trung cấp Kỹ thuật Hải quân.</p>\r\n<p style="margin: 0in 0in 0pt;">Chuyến thăm lần này của tàu tuần dương Vendémiere góp phần củng cố và tăng cường mối quan hệ hữu nghị giữa Việt Nam - Cộng hòa Pháp nói chung và quân đội hai nước nói riêng./.</p>\r\n<p style="text-align: right; margin: 0in 0in 0pt;" align="right">Theo <span style="font-weight: bold;">Khổng Minh Khánh </span></p>\r\n<p style="text-align: right;"><span style="font-style: italic;">TTXVN</span></p>', 1, 9, 0, 20, '2012-03-24 17:37:29', 62, '', '2012-03-24 17:38:31', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:37:29', '0000-00-00 00:00:00', '1_89020.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 4, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (53, 'Cảnh sát Malaysia giải cứu 8 phụ nữ Việt Nam', 'canh-sat-malaysia-giai-cuu-8-phu-nu-viet-nam', '', '<p>Cảnh sát Malaysia đã giải cứu 8 phụ nữ Việt Nam, trong đó có 2 người 17 tuổi, được cho là nạn nhân của một vụ buôn bán người sau khi ập vào khu nhà hàng bốn tầng ở vùng Sunway, Kuala Lumpur, vào lúc 1 giờ sáng ngày 23/3.', '<img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/1_816b7.jpg" /></p>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Datuk Abd Jalil Hassan và quan chức cảnh sát Việt Nam kiểm tra hộ chiếu của những cô gái được giải cứu.<br /></span></div>\r\n<p style="margin: 0in 0in 0pt;">Các nạn nhân bị lừa là được đưa tới đây để làm phục vụ với mức lương cao, nhưng sau khi đợi 2 tháng, họ đã bị ép buộc làm gái mại dâm.</p>\r\n<p style="margin: 0in 0in 0pt;">Những phụ nữ này được đưa ra 3 lựa chọn: làm gái điếm, làm việc tiếp đón khách hoặc cưới những người đàn ông địa phương.</p>\r\n<p style="margin: 0in 0in 0pt;">Theo quan chức cảnh sát Datuk Abdul Jalil Hassan, thuộc cơ quan chống mại dâm, game và các xã hội ngầm cho biết khi cảnh sát đột kích vào khu nhà 4 tầng ở Taman Sunway, Batu Caves, những phụ nữ trên đã bị khóa trái bên trong.</p>\r\n<p style="margin: 0in 0in 0pt;">“Chúng tôi đã bắt một phụ nữ Việt Nam và 4 người đàn ông địa phương, được cho là thành viên của băng nhóm buôn người… Chúng tôi cũng thu giữ 12 hộ chiếu, tài liệu đăng ký kết hôn, 890 RM (365 USD) và tiền Việt.</p>\r\n<p style="margin: 0in 0in 0pt;">Các nạn nhân ở độ tuổi 17-35, hiện đã được đưa về một ngôi nhà an toàn.</p>\r\n<p style="margin: 0in 0in 0pt;">Ông Jalil cho biết điều tra ban đầu cho thấy những phụ nữ này đã bị ép buộc chấp nhận một trong 3 lựa chọn trên để trả cho băng nhóm số tiền mà chúng sử dụng để đưa họ tới Malaysia.</p>\r\n<p style="margin: 0in 0in 0pt;">“Nếu những phụ nữ này đồng ý kết hôn, băng nhóm sẽ làm giả giấy chứng tử của chồng họ ở Việt Nam để họ được phép kết hôn tại đây. Đám cưới được băng nhóm sắp xếp và chúng sẽ đánh phí họ từ khoảng 13.000-20.000 RM”.</p>\r\n<p style="margin: 0in 0in 0pt;">Ông Jalil cũng cho biết những thông tin trên tại lễ bế mạc Chương trình xây dựng khả năng của Interpol về Phòng chống buôn bán người và nhập cư trái phép ở Đông Nam Á. Hơn 20 cảnh sát ở 7 nước đã tham dự hội nghị kéo dài 2 tuần lần đầu tiên được tổ chức trong khu vực.</p>\r\n<p style="text-align: right; margin: 0in 0in 0pt;" align="right"><span style="font-weight: bold;">Phan Anh</span></p>\r\n<p style="text-align: right;">Theo <span style="font-style: italic;">Asia One</span></p>', 1, 9, 0, 20, '2012-03-24 17:38:55', 62, '', '2012-03-24 17:39:51', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:38:55', '0000-00-00 00:00:00', '1_816b7.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (54, 'Tổng thống Obama lên đường đi Hàn Quốc', 'tong-thong-obama-len-duong-di-han-quoc', '', '<p>Tổng thống Barack Obama hôm nay đã rời Mỹ đi châu Á trong chuyến công du vốn cũng sẽ bao gồm chuyến thăm đầu tiên với khu vực biên giới căng thẳng chia cắt hai miền Triều Tiên.', '</p>\r\n<div>&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/6DQQJ7yW5QPfG6EzuGal/Image/2012/03/1_37669.jpg" /></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Tổng thống Mỹ Barack Obama.</span></div>\r\n<div>&nbsp;</div>\r\n<div>Chiếc chuyên cơ Không lực Một chở nhà lãnh đạo Mỹ và các cố vấn đã cất cánh từ căn cứ không quân Andrews ở ngoại ô Washington vào lúc 00h07 đêm qua giờ địa phương.</div>\r\n<p>Tại Hàn Quốc, ông Obama muốn bày tỏ sự cảm ơn đối với một số trong tổng 28.500 binh sĩ Mỹ hiện đang đồn trú tại đây. Ông cũng muốn muốn vinh danh sức mạnh của Hàn Quốc, một đồng minh chủ chốt&nbsp;ở châu Á, tại một khu vực mà ông coi là ưu tiên trong chính sách ngoại giao của Mỹ.</p>\r\n<p>Theo lịch trình, Tổng thống Mỹ sẽ gặp người đồng cấp Hàn Quốc Lee Myung-Bak vào ngày mai, trước hội nghị an ninh với sự tham dự của đại diện 53 quốc gia ở Seoul và một ngày trước khi tiến hành các cuộc đàm phán quan trọng với Chủ tịch Trung Quốc Hồ Cẩm Đào.</p>\r\n<p>Vào thứ 2 tuần tới, ông Obama sẽ có cuộc gặp cuối cùng với Tổng thống Nga Dmitry Medvedev trước khi ông Medvedev mãn nhiệm.</p>\r\n<p>Dự kiến, ông Obama cũng có các cuộc hội đàm với các lãnh đạo Thổ Nhĩ Kỳ và Kazakhstan.</p>\r\n<p align="right"><span style="font-weight: bold;">An Bình<br /></span>Theo <span style="font-style: italic;">AFP</span></p>', 1, 9, 0, 20, '2012-03-24 17:40:16', 62, '', '2012-03-24 17:41:17', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:40:16', '0000-00-00 00:00:00', '1_37669.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (55, 'Trung Quốc: Xôn xao người đàn ông khỏa thân chống đẩy khắp nước', 'trung-quoc-xon-xao-nguoi-dan-ong-khoa-than-chong-day-khap-nuoc', '', '<p>Loạt ảnh về người đàn ông khỏa thân chống đẩy ở những địa điểm từng gắn liền với bê bối, tranh cãi trên khắp Trung Quốc đang tạo ra cơn sốt trong cộng đồng mạng nước này.', '</p>\r\n<p style="margin: 0in 0in 0pt;">Trong&nbsp;thời gian&nbsp;qua, ở Trung Quốc đã xảy rất nhiều vụ bê bối và những vấn đề gây tranh cãi. Và gần đây cư dân mạng nước này xôn xao về người đàn ông khỏa thân chống đẩy được thấy chụp ảnh ở khắp những nơi đã từng gắn với những vụ tai nạn, những vụ bê bối và những vấn đề gây tranh cãi, tiêu tốn nhiều giấy mực đó.</p>\r\n<p style="margin: 0in 0in 0pt;">Tuy nhiên một số người nghi ngờ các bức ảnh là được chỉnh sửa, cắt dán. Nhưng dù sao, họ cũng cho rằng người đàn ông đó đã nhắc lại được những sự kiện tai tiếng theo cách thức khá hài hước.</p>\r\n<div style="margin: 0in 0in 0pt;" align="center">&nbsp;<br /><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/1_62780.jpg" /></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Bệnh viện tâm thần Xu Wu, Hồ Bắc, Vũ Hán, trong vụ bê bối những người già hưu trí đã bị ép buộc ký kết để chuyển tới bệnh viện tâm thần.</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/2_90de6.jpg" /><br /></span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Tại nơi sinh viên nhạc viện Yao Jiaxin, 21 tuổi, đâm xe hơi vào người đi xe đạp Zhang Miao vào 11h đêm. Khi Zhang nhìn vào biển số của xe, Yao đã rút dao ra đâm cô 8 nhát.</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/3_5fe0d.jpg" /><br /></span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Một nông dân ở Hồ Nam đã trốn hàng triệu Tệ phí cầu đường khi làm giả biển số cho 2 chiếc xe tải của mình bằng biển số xe quân sự.</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/4_7bcbb.jpg" /><br /></span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">11 cửa hàng Carrefour ở Trung Quốc bị bắt quả tang bán đắt cho khách hàng các sản phẩm như đồ lót cốt tông, lá trà.</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/5_277e1.jpg" /><br /></span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Một cuộc điều tra trên cả nước cũng phát hiện 3 cửa hàng của Wal-Mart lừa dối khách về giá cả.</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/6_7e13e.jpg" /><br /></span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Một giáo sư 45 tuổi tại đại học danh giá Xi''an Jiaotong ở tỉnh Sơn Tây, tây bắc Trung Quốc đã bị sa thải sau khi 6 đồng nghiệp liên tục gửi thư tới trường và tung lên mạng phơi bày những vụ bê bối của ông. </span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/7_0808f.jpg" /><br /></span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Apple thừa nhận 137 công nhân ở nhà máy Wintek ở Tô Châu, một trong những nhà cung cấp màn hình cảm ứng của hãng này, đã bị ảnh hưởng nghiêm trọng về sức khỏe do tiếp xúc với n-hexane, hóa chất trong hợp chất tẩy rửa được dùng trong một số quá trình sản xuất.</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/8_ec2b8.jpg" /><br /></span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Một văn phòng chi nhánh của Chữ thập đỏ Thượng Hải thừa nhận đã chi 1.500 USD cho bữa tiệc trưa cho 17 người. Tính ra là mỗi người ăn 88 USD cho một bữa ăn ở thành phố mà chỉ cần chưa đầy 5USD có thể mua được một bữa trưa đầy ắp thức ăn và một bữa ăn thịnh soạn với cái giá 20USD hoặc ít hơn.</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/9_891b5.jpg" /><br /></span>\r\n<p><span style="font-family: Tahoma; font-size: 10pt;">Một tòa án địa phương ở Thâm Quyến đã kêu gọi nhà phát triển villa nổi xa xỉ trái phép trên biển ở tỉnh Quảng Đông rời đi, nếu không ngôi nhà sẽ bị phá hủy.&nbsp;</span></p>\r\n</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/10_8a3c9.jpg" /><br /></span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Đài truyền hình Trung Quốc CCTV đưa tin các sản phẩm dưới nhãn hiệu Shineway của tập đoàn Shuanghui Group đã được sản xuất từ thịt lợn nuôi bằng clenbuterol, phụ gia để ngăn lợn phát triển mỡ, có thể gây ung thư.</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center">\r\n<p><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/12_8d576.jpg" /><br /></span></p>\r\n</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Giới chức Thượng Hải đã thu giữ hơn 6.000 chiếc bánh bao và thị trưởng thành phố đã yêu cầu điều tra sau khi họ phát hiện bánh bao bị nhuộm và làm bằng hóa chất độc hại.</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/13_6d03e.jpg" /><br /></span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Thâm Khuyến đã trục xuất khoảng 80.000 “người có khả năng gây bất ổn” trong nỗ lực đảm bảo ổn định xã hộ cho Summer Universiade lần thứ 26. 8 nhóm người được xếp vào dạng “cảnh báo cao” gồm cựu tù nhân, du mục, thất nghiệp, người tham gia vào hoạt động đáng ngờ như buôn bán ma túy và hàng lậu. </span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/15_7a6ff.jpg" /></span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Hàng loạt vụ việc đã xảy ra ở Bảo tàng cung điện Bắc Kinh, gồm trộm, câu lạc bộ cho người giàu….</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/03/1_628db.jpg" /></p>\r\n<p><span style="font-family: Tahoma; font-size: 10pt;">Qiang Mingqi, 52 tuổi, người địa phương bị thất nghiệp, đã kích nổ bom ở tòa nhà chính quyền tại tỉnh Giang Tây, khiến 3 người thiệt mạng và 10 người bị thương. Qiang nằm trong số người thiệt mạng.</span></p>\r\n</div>\r\n<p style="text-align: right;"><span style="font-weight: bold;">Phan Anh<br /></span>Theo <span style="font-style: italic;">Asia</span><span style="font-style: italic;">One</span></p>', 1, 9, 0, 20, '2012-03-24 17:41:44', 62, '', '2012-03-24 17:42:48', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:41:44', '0000-00-00 00:00:00', '1_62780.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 1, '', '', 0, 4, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (56, 'Chuyện những người "mài ngọc" tại Miss Teen 2011', 'chuyen-nhung-nguoi-mai-ngoc-tai-miss-teen-2011', '', '<p>Miss Teen 2011 đã khép lại với hình ảnh đăng quang của cô nữ sinh trường Học viện Âm nhạc Quốc gia Cao Thanh Thảo My. Để làm nên một Tuần lễ Chung kết và Đêm Chung kết rực rỡ sắc màu, không thể quên sự đóng góp của những người “mài ngọc”.', '</p>\r\n<p style="margin: 0in 0in 0pt;"><span style="font-weight: bold;">Thúy Hằng- Cô giáo catwalk mê ăn rau</span></p>\r\n<p style="margin: 0in 0in 0pt;">Người đầu tiên phải kể đến là cô giáo dạy catwalk Thúy Hằng, người đồng hành với cuộc thi Miss Teen 2011 từ VCK miền với cương vị là giám khảo. Nhiều thí sinh trong vòng thi trước từng bị Thúy Hằng “chê” đã rất “run” khi gặp lại chị tại vòng này. Thế nhưng, với lối truyền đạt vui vẻ, tình cảm, “cô giáo” xinh đẹp đã nhanh chóng thu phục được 20 thí sinh.</p>\r\n<p style="margin: 0in 0in 0pt;">Ngoài các giờ lên lớp, Thúy Hằng đều dùng bữa chung cùng các Miss Teen. Quanh mâm cơm, cả cô lẫn trò “tám” đủ chuyện trên trời dưới biển. Các Miss Teen còn phát hiện ra “cô giáo” mình rất thích ăn rau và quà vặt khi nghe “cô giáo” kể vừa đến Quảng Ninh đã phải phi ngay đến hàng ốc quen nổi tiếng ở Hòn Gai để ăn cho thỏa cơn “nghiền”.</p>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/trangdiem2609113_c5ccc.jpg" /></div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<p style="margin: 0in 0in 0pt;">Thúy Hằng chia sẻ: “Là người đồng hành cùng nhiều cuộc thi sắc đẹp trong nước và quốc tế nhưng đây là lần đầu tiên mình làm việc với các thí sinh trong độ tuổi teen, thấy các em trẻ trung, hồn nhiên mình cũng thấy trẻ lại.</p>\r\n<p style="margin: 0in 0in 0pt;">Miss Teen có nhiều điều mà các cuộc thi khác không có được, đó chính là sức trẻ, sự hồn nhiên, vô tư. Nhìn thấy các em tranh nhau ăn ô mai giữa giờ tập luyện mình thấy thật bình yên”.</p>\r\n<p style="margin: 0in 0in 0pt;">Khi được hỏi về tài năng và sắc đẹp của các thí sinh Miss Teen 2011, chị Thúy Hằng nhận xét: “Mình đã đưa ra lời khuyên một số em có gương mặt và hình thể rất phù hợp để thi Hoa Hậu. Nhiều em khiến mình bất ngờ vì khi tập luyện thì trẻ con, nhõng nhẽo, nhưng lúc lên sân khấu lại chững chạc và rất chuyên nghiệp. Một số bạn cực kỳ “ăn” trang điểm và sân khấu.</p>\r\n<p style="margin: 0in 0in 0pt;">Mình cùng các stylist, chuyên gia trang phục, đội ngũ trang điểm KMA đã trao đổi và tìm ra được cách thể hiện riêng cho từng em để các em từ những viên ngọc thô trở nên lung linh, hoàn hảo hơn. Cũng nhờ êkíp hỗ trợ này mà Đêm chung kết vừa qua, 20 cô học trò của Thúy Hằng đã trở nên lộng lẫy, tự tin hơn qua từng bước sải chân trên sân khấu”.</p>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/trangdiem2609116_1377c.jpg" /></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">"Cô giáo" Hoài Thu tỏ ra khá nghiêm khắc trong giờ học với các thí sinh.</span></div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<p style="margin: 0in 0in 0pt;"><span style="font-weight: bold;">Hà Hoài Thu- Cô giáo dạy nhạc xì tin</span></p>\r\n<p style="margin: 0in 0in 0pt;">Huấn luyện thanh nhạc cho các thí sinh Miss Teen 2011 là ca sỹ Sao Mai Hà Hoài Thu. Sinh năm 1986, khi lên lớp, cô giáo Hoài Thu tỏ ra khá nghiêm khắc và cầu toàn, thế nhưng ngoài giờ học thì cũng “nhí nhố” xì tin như bạn đồng trang lứa với các Miss Teen.</p>\r\n<p style="margin: 0in 0in 0pt;">Từng tham dự cuộc thi Sao Mai điểm hẹn với yếu tố loại đầy khốc liệt, quán quân Hoài Thu dường như rất biết cách để động viên các thí sinh Miss Teen và đặc biệt không tiếc lời khen học trò mình lên hình xinh thế.</p>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/trangdiem2609114_1e8a1.jpg" /></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">...và&nbsp;góp&nbsp;ý&nbsp;từng động tác biểu diễn ca khúc&nbsp;trên sân khấu cho các thí sinh.</span></div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<p style="margin: 0in 0in 0pt;">Hoài Thu hào hứng: “Vốn là một thí sinh, rồi trở thành ca sỹ chuyên nghiệp, mình rất thích thí sinh phải thể hiện được “lửa” trên sân khấu. Ở sân khấu Miss Teen 2011 mình đã tìm thấy điều đó. Các thí sinh như được lột xác hoàn toàn và phải nói là học bài cực nhanh. Trong đêm Chung kết Tài năng Ca sỹ mình chỉ việc ngồi dưới bàn Giám khảo mà cười mãn nguyện”.</p>\r\n<p style="margin: 0in 0in 0pt;">Cô giáo Hoài Thu đánh giá, bản thân các thí sinh như Thảo My, Yến My, Thúy Vy, Kim Cương vốn đã có sẵn tài năng và tố chất âm nhạc. Những thí sinh này còn tiếp thu rất nhanh những góp ý của Hoài Thu để khắc phục những nhược điểm trong giọng hát và thể hiện thành công ca khúc của mình.</p>\r\n<p style="margin: 0in 0in 0pt;">“Chắc chắn các cô bạn này sẽ tiến xa trên con đường ca hát. Hy vọng một ngày cô trò sẽ gặp lại nhau trên sân khấu”, Hoài Thu chia sẻ.</p>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/trangdiem2609111_c6479.jpg" /></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Đạo diễn Việt Tú "xì tin" với các thí sinh.</span></div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<p style="margin: 0in 0in 0pt;"><span style="font-weight: bold;">Đạo diễn Việt Tú: “Bước lên sân khấu là không có chuyện thanh minh”</span></p>\r\n<p style="margin: 0in 0in 0pt;">Cùng với êkíp chuyên nghiệp chuyên tổ chức các sự kiện lớn như Hoa hậu Việt Nam, Đẹp Fashion Show, Con đường âm nhạc, Không gian âm nhạc, đạo diễn Việt Tú cùng nhạc sỹ Huy Tuấn, chuyên gia ánh sáng Tú Long, DJ Hùng Bnuts đã đem đến cho khán giả thưởng ngoạn một đêm Chung kết Miss Teen 2011 mang đầy hơi thở biển như đã hứa.</p>\r\n<p style="margin: 0in 0in 0pt;">Có mặt tại các buổi tổng duyệt mới thấy Việt Tú làm việc rất nghiêm khắc, nhiều khi anh phải la hét, lớn tiếng. Giải thích về điều này, vị đạo diễn trẻ nói: “Tôi rất hài lòng về tinh thần thái độ của các thí sinh vì mọi yêu cầu của tôi và các cộng sự có khó khăn đến mấy thì các thí sinh cũng hiểu và làm việc một cách vô điều kiện, không than vãn, không kêu ca.</p>\r\n<p style="margin: 0in 0in 0pt;">Tập luyện với cường độ lớn thì không bao giờ dễ dàng, vì nếu chương trình bình thường sẽ phải mất cả tháng tập luyện thì các em chỉ có mấy ngày, đó là lý do vì sao phải tập nhiều, phải khắt khe. Các thí sinh năm nay rất tiềm năng, cuộc thi chỉ là điểm khởi đầu, hy vọng họ sẽ còn thành công nhiều hơn nữa sau khi bước ra từ cuộc thi này”.</p>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/trangdiem2609112_f2685.jpg" /></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Các thí sinh Miss Teen luôn được trang điểm kỹ càng trước khi bước ra sân khấu.</span></div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<p style="margin: 0in 0in 0pt;"><span style="font-weight: bold;">Chuyên gia trang điểm KMA: Miss Teen có nhiều khuôn mặt sáng</span></p>\r\n<p style="margin: 0in 0in 0pt;">Đồng hành cùng Miss Teen 2011 từ ngày đầu tiên đến ngày cuối cùng, các chuyên gia trang điểm KMA cũng phải thức khuya dậy sớm cùng các thí sinh. Bích Khanh- SBD 1573 nói: “Mấy ngày đầu cứ mở mắt ra là em lại nghĩ đến việc đánh răng rửa mặt xong cho các anh chị trang điểm làm tóc mà ngại ghê.</p>\r\n<p style="margin: 0in 0in 0pt;">Thế mà sau mấy ngày nhờ các anh chị mà bọn em thấy mình đẹp hơn rất nhiều thì bạn nào cũng tự giác dậy rất sớm. Các anh chị không đợi em phải diễn tả tỉ mỉ muốn có khuôn mặt, kiểu tóc như thế nào mà em chỉ cần nói: Hôm nay em mặc trang phục này diễn catwalk, hôm nay em đóng vai người mẹ trong tiểu phẩm với con gái… là các anh chị đã trổ tài biến hóa em trong mấy chục phút. Cả em và các bạn thường rất ngạc nhiên và thích thú khi thấy mình thay đổi theo từng phong cách sau mỗi lần được trang điểm”.</p>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/trangdiem2609117_aa267.jpg" /><br /><span style="font-family: Tahoma; font-size: 10pt;">Chuyên gia trang điểm Ngọc Dung đánh giá cao các khuôn mặt Miss Teen 2011.</span></div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<p style="margin: 0in 0in 0pt;">Ngọc Dung- chuyên gia trang điểm của mỹ phẩm KMA hồ hởi: “Mặc dù đã quen với công việc trang điểm cho các người đẹp trong cuộc thi Hoa Hậu Việt Nam, iMiss Thăng Long, và đêm Cuxi night của Miss Teen 2010, chúng tôi vẫn thấy nhiều cảm hứng sáng tạo mới mẻ khi tiếp xúc với các thí sinh Miss Teen 2011.</p>\r\n<p style="margin: 0in 0in 0pt;">Năm nay có nhiều khuôn mặt đẹp, sáng và sang. Nhiều khuôn mặt phải nói là sinh ra đã dành cho việc trang điểm và lên hình. Nắm bắt được tính chất của cuộc thi, chúng tôi luôn phải làm sao để đem lại cho các em vẻ đẹp phù hợp với lứa tuổi teen, để các em nhìn không bị già trước tuổi hay giống một cô bé trẻ con “học đòi” làm người lớn”.</p>\r\n<p style="text-align: right;"><span style="font-weight: bold;">T.Nhung</span></p>', 1, 8, 0, 14, '2012-03-24 17:47:42', 62, '', '2012-03-24 17:48:57', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:47:42', '0000-00-00 00:00:00', 'trangdiem2609114_1e8a1.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 6, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (57, 'Ngắm cặp đôi nghịch ngợm nhất Miss Teen 2011', 'ngam-cap-doi-nghich-ngom-nhat-miss-teen-2011', '', '<p>Nửa đêm bật dậy mượn kính của nhau rồi vẽ mặt lem nhem đi hù các phòng khác - đó là những kỷ niệm khó quên của hai Miss Teen nghịch ngợm Mạc Yến My và Khúc Nguyễn Thúy Vy.', '</p>\r\n<p>Được sắp ở chung phòng với nhau ngay từ ngày đầu chung kết Miss Teen, cô bạn miền Bắc Dương Mạc Yến My và cô em út của gia đình Miss Teen 2011 Khúc Nguyễn Thúy Vy có rất nhiều tính cách tương đồng. Mọi thường thường ví hai thí sinh này như chị em ruột của nhau vậy. Đây cũng là hai thí sinh quậy nhất và luôn luôn đem lại tiếng cười cho cả đoàn vì những trò đùa ngộ nghĩnh.</p>\r\n<div>Thúy Vy và Yến My thường chu môi đóng… cá dọn bể và giả giọng các bộ phim kiếm hiệp khiến mọi người cười nghiêng ngả. Hai cô bạn còn có thể nhảy nhót bất cứ khi nào có nhạc. Vui vẻ và hòa đồng, Thúy Vy và Yến My luôn biết cách khuấy động không khí.</div>\r\n<div>&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom27091111_c058e.jpg" /></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Cặp đôi nghịch ngợm nhất Miss Teen 2011: Yến My - Thúy Vy.</span></div>\r\n<p>Và rất thú vị, hai cô bạn đã hai lần cùng được xướng tên trong Top của các đêm Chung kết Tài năng. Khúc Nguyễn Thúy Vy lọt vào Top 5 tài năng người mẫu, Top 4 Tài năng ca sỹ trong khi Dương Mạc Yến My lọt vào Top 4 Tài năng ca sỹ và Top 3 Tài năng Diễn xuất – MC, lập cú đúp đứng đầu hai đêm thi tài năng.</p>\r\n<p>Chung kết quốc gia diễn ra vào ngày 12/11 vừa qua, Yến My đã ẵm trọn giải Tài năng còn Thúy Vy đạt được giải Ngôi sao Tuần Châu – Hạ Long.</p>\r\n<div>Trong khoảnh khắc chia tay ở Sân bay Nội Bài, Thúy Vy - cô em út hay đùa nghịch, hay cười nhất Miss Teen 2011 lại là người bật khóc đầu tiên khiến các bạn cũng khóc theo. Đam mê ca hát, thời trang và diễn xuất, trở về sau cuộc thi hai cô bạn đều ấp ủ ước mơ trở thành những gương mặt trẻ tài năng của showbiz Việt. Chúc cho ước mơ của hai cô bạn đa tài Thúy Vy và Yến My sẽ trở thành hiện thực.</div>\r\n<div>&nbsp;</div>\r\n<div align="center"><span style="font-weight: bold;">Cùng ngắm chùm ảnh nghịch ngợm "khó đỡ" của bộ đôi này</span></div>\r\n<div align="center">&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom27091115_513b8.jpg" /></div>\r\n<div align="center">&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom27091113_d2dfa.jpg" /></div>\r\n<div align="center">&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom27091110_29940.jpg" />\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom27091112_017e5.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom27091114_95412.jpg" /></p>\r\n</div>\r\n<div align="center">&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom2709119_470a6.jpg" />\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom2709118_6f233.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom2709117_2dd77.jpg" /></p>\r\n</div>\r\n<div align="center">&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom2709115_5c13d.jpg" /></div>\r\n<div align="center">&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom2709112_8204d.jpg" />\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom2709113_fec61.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom2709114_5273c.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2011/11/nghichngom2709116_1cfea.jpg" /></p>\r\n</div>\r\n<div>&nbsp;</div>\r\n<div align="right"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;">Phương Nhung</span></div>\r\n<div align="right"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;"><span style="font-style: italic; font-size: 12pt; font-weight: normal;">Ảnh:</span> Lê Trường</span></div>', 1, 8, 0, 14, '2012-03-24 17:49:55', 62, '', '2012-03-24 17:50:57', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:49:55', '0000-00-00 00:00:00', 'nghichngom27091111_c058e.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 5, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (58, 'Cao Thanh Thảo My và cây đàn đánh thức đam mê', 'cao-thanh-thao-my-va-cay-dan-danh-thuc-dam-me', '', '<p>Khi rời Tuần Châu, điều khiến “họa my vàng” xúc động nhất là bất ngờ được tặng cây đàn guitar đã theo các anh chị PV trong suốt những ngày tác nghiệp ở đảo, cùng các anh chị đàn, hát suốt đêm.', '</p>\r\n<div>Trong suốt quãng thời gian 15 ngày em đã có rất nhiều kỷ niệm. Nhưng hầu hết các kỷ niệm đều gắn liền với cuộc sống gắn bó của 20 con người chúng em. Em đã có 19 người chị đồng thời cũng là những người bạn tuyệt vời trong những ngày tháng tập luyện vất vả.</div>\r\n<div>&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/12giadinh_1b99d.JPG" /></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Thảo My (thứ 3 từ phải vào) kề vai sát cánh cùng 19 thí sinh</span></div>\r\n<div align="left"><br />Và em cũng có cả những người anh, người chị luôn theo sát chúng em trong từng bước đi, chăm lo cho em từng viên thuốc. Đó đúng 15 ngày không hề buồn.</div>\r\n<p>Trong những ngày đó, có những buổi hoạt động ngoại khóa rất có ý nghĩa như tham quan Vịnh Hạ Long, đạp xe vận động mọi người bình cho Vịnh Hạ Long, đi thăm mỏ than Hà Lầm... Mỗi một hoạt động đều để lại cho em rất nhiều ấn tượng.</p>\r\n<div>Và một kỷ niệm rất đáng nhớ với em đó là khi chúng em tham quan Vịnh Hạ Long. Thuyền đã dừng ở giữa vịnh khoảng 2-3 tiếng để chúng em quay phóng sự và chụp hình. Sau khi xong phần chụp hình của mình, em đã trèo lên phía trên thuyền và ngồi ngắm cảnh... Thực sự em đã bị mê hoặc bởi vẻ đẹp của chốn thiên đường hạ giới này.</div>\r\n<div>&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/10_51f19.jpg" /></div>\r\n<p>Đến tận bây giờ, em vẫn không thể quên được cái khoảnh khắc đáng nhớ đó, khoảnh khắc mà lần đầu tiên em cảm thấy tâm hồn mình thoải mái một cách kỳ lạ đến vậy.</p>\r\n<div>Và không chỉ có những kỷ niệm ở những buổi ngoại khóa. Khi chúng em trở về ngôi nhà chung. Em cùng với chị Ý Nhi ở một phòng ngay cạnh phòng chị Kiều Anh và Mỹ Linh. Sau mỗi ngày tập luyện mệt mỏi, sáng hôm sau cả 4 người đều cố gắng ngủ nướng thêm một chút và hậu quả là thức dậy sát với giờ tập trung.</div>\r\n<div>&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/3_c725c.JPG" /></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Từ trái qua phải: Kiều Anh - Thảo My - Mỹ Linh</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/bo3_09c88.JPG" /></p>\r\n</div>\r\n<p>Khi đó cảnh tượng giống như một phiên chợ vậy, lật đồ, chạy nhảy, la hét loạn xạ để tìm đồ đạc. Nhưng lại rất may mắn vì khi lên tập trung thì chúng em vẫn là những người lên sớm nhất. Rồi ngồi ngẫm nghĩ một lúc kiểu gì cũng quên một số thứ như SBD, giày tập… Thế là lại chạy xuống tìm rồi chạy lên là đã muộn giờ (cười)</p>\r\n<p>Và trước khi em rời Tuần Châu, em đã nhận được một món quà vô cùng bất ngờ và ý nghĩa. Đó là một cây đàn guitar được đặt riêng để phù hợp với người chơi từ anh Hùng - trưởng đại diện VTC Quảng Ninh.</p>\r\n<div>Đó là cây đàn đã theo các anh chị phóng viên các báo suốt những ngày ở đảo, theo các anh chị đàn, hát suốt cả đêm. Em hứa sẽ học chơi guitar và năm sau sẽ quay lại đánh cho mọi người nghe. Và em tin, cây đàn sẽ luôn đánh thức những đam mê trong em.</div>\r\n<div>&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/2Dammenghethuat_4560c.JPG" />\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/3Dammenghethuat_c0738.JPG" /><br /><span style="font-family: Tahoma; font-size: 10pt;">Một cô gái đam mê nghệ thuật</span></p>\r\n</div>\r\n<div align="right"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;"></span>&nbsp;</div>\r\n<div align="right"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;">Phương Nhung </span><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">(ghi)</span></div>\r\n<div align="right"><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Ảnh: <span style="font-style: normal; font-weight: bold;">Huy Thành</span></span></div>', 1, 8, 0, 14, '2012-03-24 17:51:17', 62, '', '2012-03-24 17:52:34', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:51:17', '0000-00-00 00:00:00', '3_c725c.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 4, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (59, 'Thí sinh Miss Teen thổi nến chúc mừng Thảo My', 'thi-sinh-miss-teen-thoi-nen-chuc-mung-thao-my', '', '<p>Các Miss Teen Kiều Anh, Mỹ Linh, Quỳnh Anh cùng với Mạnh Quân, Mạnh Bin, Việt Michael,… đã cùng thổi nến chúc mừng Thảo My trở thành Miss Teen 2011.', '</p>\r\n<p>Một buổi gặp gỡ thân mật đã được tổ chức để các Miss Teen cùng sẻ chia niềm vui chiến thắng cùng Miss Teen 2011 Thảo My.</p>\r\n<div>Thảo My xuất hiện giản dị với áo T-shirt và quần jeans, cô bạn cũng trang điểm rất nhẹ nhàng. Đây cũng là dịp để “gia đình nhỏ” với bộ 3 thân thiết Kiều Anh – Thảo My – Mỹ Linh tụ hội trước ngày Kiều Anh trở lại TP.Hồ Chí Minh.</div>\r\n<p align="center"><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/1Tuhoi_65293.jpg" /></p>\r\n<div align="left">Cô bạn Quỳnh Anh đảm nhận vai trò MC và hoạt náo viên cho chương trình. Khi "Khúc hát Miss Teen" được cất lên, 4 cô gái đều cảm thấy nhớ những ngày tháng bên nhau tại Tuần Châu và nắm tay nhau hát.</div>\r\n<div align="left">&nbsp;</div>\r\n<div>Chung vui cùng Thảo My còn có bộ đôi Mạnh Quân, Mạnh Bin, anh chàng Việt Michael và ca sỹ Duy Khánh, cô bạn Top 100 Miss Teen Thái Thanh Hiền,…</div>\r\n<div>&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/21_b969d.jpg" /></div>\r\n<div align="center">&nbsp;</div>\r\n<div align="left">Sau khi đăng quang, Thảo My khá bận rộn với việc học và nhận được nhiều lời mời chụp hình, phỏng vấn. Cô bạn thấy hạnh phúc vì có mẹ luôn dõi theo từng bước đi của mình. My cũng thấy mình may mắn vì ngày đầu tiên trở lại Hà Nội thì Miss Teen 2010 Nguyễn Lâm Diễm Trang cũng ở lại thăm thú Hà Nội một ngày.</div>\r\n<p align="center"><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/3_e4793.jpg" /></p>\r\n<p align="left">My được Miss Teen 2010 Nguyễn Lâm Diễm Trang truyền đạt khá nhiều kinh nghiệm để hoàn thành tốt trọng trách của một Miss Teen, xứng đáng với sự kỳ vọng của khán giả.</p>\r\n<p align="center">&nbsp;</p>\r\n<div align="center"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;">Thêm những hình ảnh vui tươi của các Miss Teen</span></div>\r\n<div align="center"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;"></span>&nbsp;</div>\r\n<div align="center"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;"><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/10_1757e.jpg" /></span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/11_787c4.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/12_bdd4c.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/13_f7bf7.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/22_106ff.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/23_f7783.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/2_846d7.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/5_6e082.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/4_cd406.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/6_e02ec.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/7_8a797.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/8_46215.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Be-xinh-2/9_df370.jpg" /></p>\r\n</div>\r\n<div align="right"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;"></span>&nbsp;</div>\r\n<div align="right"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;">Phương Nhung</span></div>\r\n<div align="right"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;"></span><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Ảnh: </span><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;">Vũ Long</span></div>', 1, 8, 0, 14, '2012-03-24 17:52:59', 62, '', '2012-03-24 17:54:04', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:52:59', '0000-00-00 00:00:00', '1tuhoi_65293.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (60, 'Miss Teen Thảo My nhớ tiếng rao đêm HN khi Nam tiến', 'miss-teen-thao-my-nho-tieng-rao-dem-hn-khi-nam-tien', '', '<p>Tiếng rao đêm trong lòng thành phố, không khí ấm áp của phố cổ Hà Nội mùa Noel,… là những nỗi nhớ nao lòng với Cao Thanh Thảo My khi cô bạn Nam tiến.', '</p>\r\n<p><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Chào Thảo My, được biết bạn đang chuẩn bị Nam tiến để phát triển sự nghiệp ca hát. Vậy điều gì thôi thúc bạn đưa ra quyết định đó vào thời điểm này?</span></p>\r\n<div>Thảo My muốn bắt đầu càng sớm càng tốt, vì Thảo My nhận thấy bây giờ là thời điểm Thảo My có nhiều năng lượng để tiếp thu những cái mới, những kiến thức nghề nghiệp. Còn có một lý do nữa là người cô của Thảo My khuyên nên thực sự thử nghiệm thay vì ngồi nhìn thời gian trôi qua.</div>\r\n<div>&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Anh-1/4TM_b90eb.jpg" /></div>\r\n<p><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Điều đó có nghĩa là công việc học tập của bạn sẽ bị ảnh hưởng?</span></p>\r\n<p>Không. Việc học vẫn bình thường, chỉ là có một chút thay đổi về địa điểm thôi.</p>\r\n<p><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Thực tế là với thị trường âm nhạc miền Nam bạn là một cái tên còn quá mới mẻ, bạn đã lường trước được những khó khăn mình sẽ phải đối mặt?</span></p>\r\n<p>Không chỉ ở miền Nam mà cái tên Cao Thanh Thảo My hoàn toàn mới mẻ với thị trường âm nhạc trong nước. Ai cũng phải có một sự khởi đầu mới mẻ và chồng chất khó khăn. Vấn đề không phải là mới hay cũ mà là bạn đem cái gì đến cho khán giả của mình. Nếu sợ khó, sợ mới không dám đi thì mãi mãi bạn chỉ là cái tên mới mẻ thôi.</p>\r\n<p><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Hầu hết những gương mặt Miss Teen đăng quang các năm trước đều không tiến sâu vào showbiz hoặc hoạt động cầm chừng và thậm chí có bạn đã quyết định rẽ ngang, rời xa showbiz, liệu điều này có phải một áp lực đối với bạn?</span></p>\r\n<p>&nbsp;</p>\r\n<div>Các năm trước, tiêu chí lựa chọn Miss Teen khác với năm nay. Do vậy, các Miss Teen thường không được định hướng rõ ràng như năm 2011. Với tiêu chí “tìm kiếm tài năng showbiz” chẳng phải là BTC đã định hướng rất rõ rồi hay sao? Thảo My có được danh hiệu Miss Teen năm nay phù hợp với tiêu chí của cuộc thi.</div>\r\n<div>&nbsp;</div>\r\n<div>Vì vậy, Thảo My sẽ cố gắng để BTC và những người đã theo dõi, ủng hộ Thảo My không phải thất vọng. Đây cũng chính là mơ ước, là con đường mà Thảo My đã lựa chọn chứ không hề có áp lực.</div>\r\n<p>&nbsp;</p>\r\n<p><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Gần đây, showbiz Việt “bội thực” bởi những scandal và các chiêu trò của các ca sỹ để được nổi tiếng thay vì chứng tỏ khả năng ca hát. Nếu có một lời mời chào tương tự để đánh bóng tên tuổi, Thảo My sẽ phản ứng ra sao?</span></p>\r\n<div>Thảo My được đào tạo bài bản để trở thành một ca sĩ. Vì vậy Thảo My sẽ làm theo những gì thầy cô đã hướng dẫn và dậy dỗ. Thảo My mới 16 tuổi nên chưa cần phải đánh bóng tên tuổi. Những nghệ sĩ nổi tiếng không nhờ chiêu trò có rất nhiều và sự nghiệp của họ rất bền vững. Đó là cái đích để Thảo My vươn tới.</div>\r\n<div>&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Anh-1/1TM_591fe.jpg" /></div>\r\n<p><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Trở thành Miss Teen ở tuổi 16 với rất nhiều trách nhiệm và quyết định Nam tiến khi còn quá trẻ, đã bao giờ bạn muốn quay trở lại làm một cô gái tuổi teen bình thường không danh hiệu?</span></p>\r\n<p>&nbsp;</p>\r\n<p>Không đâu. Thảo My yêu quý danh hiệu Miss Teen mà mình mới đạt được lắm. Đó là sự nỗ lực của Thảo My, đó cũng là những ghi nhận đầu tiên của mọi người đối với những gì mà Thảo My rèn luyện, học tập. Thảo My chỉ mong được khán giả tiếp tục ủng hộ để Thảo My đi hết con đường mình đã chọn bằng thực lực của chính mình.</p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Được biết em trai Thảo My cũng có năng khiếu ca hát bẩm sinh, vậy gia đình có định hướng cho em trai bạn theo con đường ca hát chuyên nghiệp?</span></p>\r\n<p>Em trai của Thảo My mới học lớp 2. Ba, mẹ đang cho em học những gì mà em thích như nhảy dance, thanh nhạc, piano... Sau này em lớn lên, em sẽ được tự chọn nghề nghiệp của mình giống Thảo My. Ba Mẹ Thảo My chưa bao giờ định trước điều gì khi con cái chưa bộc lộ năng lực rõ ràng mà chỉ dồn tâm sức cho theo học nếu con yêu thích.</p>\r\n<p><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Còn nhớ, trong vòng thi sơ khảo Miss Teen, thể hiện ca khúc Xích lô, Thảo My cũng kể lại tình yêu với những ký ức thuở thơ bé của mình. Một người yêu những ký ức về những con phố thủ đô như bạn, xa Hà Nội có phải là một thử thách?</span></p>\r\n<p>“Khi ta ở đất chỉ là nơi ở. Khi ta đi đất bỗng hóa tâm hồn”, Thảo My thích câu thơ này của nhà thơ Chế Lan Viên. Sẽ rất nhớ cái rét của Hà Nội với ăm ắp kỷ niệm, nhưng mà Thảo My sẽ về luôn ấy mà. Thảo My không để đất chỉ là nơi ở đâu mà là “tất cả tuổi thơ tôi” (cười)</p>\r\n<p><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Điều gì thuộc về Hà Nội sẽ khiến Thảo My nhớ nhất khi đi xa? </span></p>\r\n<p>Tiếng rao đêm ạ. Thảo My nghe nói Sài Gòn không có tiếng rao, mà chỉ có âm thanh leng keng, lộc cộc của vật dụng. Nhớ nhất là chữ A… i… thật dài bắt đầu cho mỗi đoạn rao.</p>\r\n<p><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Đã bao giờ bạn đón Noel và Tết xa nhà chưa?</span></p>\r\n<p>Chưa ạ! Noel và Tết cũng là những khoảng thời gian mà Thảo My thèm ở Hà Nội nhất. Xa rồi chắc sẽ nhớ lắm!</p>\r\n<p><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Thảo My có dự định gì trong dịp lễ Noel năm nay? </span></p>\r\n<p>&nbsp;</p>\r\n<p>Sẽ làm cho ba, mẹ bất ngờ về một chuyện, nhưng không “bật mí” được đâu ạ! (Cười)</p>\r\n<div><span style="font-style: italic;">Cảm ơn Thảo My và chúc bạn sẽ thành công!</span></div>\r\n<div>&nbsp;</div>\r\n<div align="center"><span style="font-weight: bold;">Ngắm Thảo My biến hóa với những shot hình cá tính và đầy ma quái</span></div>\r\n<div align="center"><span style="font-weight: bold;"></span>&nbsp;</div>\r\n<div align="center">&nbsp;<img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Anh-1/2TM_770f2.jpg" /></div>\r\n<div align="center">&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Anh-1/3TM_80914.jpg" /></div>\r\n<div align="center">&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Anh-1/5TM_db2a7.jpg" />\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Anh-1/6TM_19c5f.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Anh-1/7TM_2c868.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Anh-1/8TM_5f9f8.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/is9eep68pOz3YVjKaY9/Image/2011/Anh-1/9TM_a5171.jpg" /></p>\r\n</div>\r\n<div align="right"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;">Phương Nhung</span></div>\r\n<div align="right"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;"></span><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Ảnh: </span><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;">Tuấn Thành</span></div>\r\n<div align="right"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;"></span><span style="font-style: italic; font-family: Times New Roman; font-size: 12pt;">Retouch: </span><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;">Vũ Long</span></div>', 1, 8, 0, 14, '2012-03-24 17:55:12', 62, '', '2012-03-24 17:56:14', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:55:12', '0000-00-00 00:00:00', '4tm_b90eb.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (61, 'Obama kể chuyện gặp người ngoài hành tinh', 'obama-ke-chuyen-gap-nguoi-ngoai-hanh-tinh', '', '<p>Tổng thống Barack Obama hài hước kể rằng ông đã từng gặp và trò chuyện với người ngoài hành tinh khi ông đến thăm một thành phố nổi tiếng từng được cho là là nơi ghé chân của những người ngoài trái đất. ', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3b/bd/47/15/obama.jpg" border="1" height="323" width="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tổng thống Barack Obama. Ảnh: <em>peacelink.it</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">"Khi tôi còn là một cậu bé khoảng 9-10 tuổi, tôi đã gặp họ và họ hỏi tôi rằng cậu đã tới thành phố Roswell chưa. Liệu đây có phải là những gì họ yêu cầu?", ông kể. "Và tôi nói với họ rằng tôi mà kể bí mật của mình thì tôi sẽ phải giết họ. Do vậy, cả hai phải giữ bí mật của riêng mình".</p>\r\n<p>Ông Obama có những phát biểu hài hước về người ngoài hành tinh nhân chuyến thăm tới thành phố Roswell, bang New Mexico. Tại đây, ông cũng sẽ phát biểu về chính sách năng lượng.</p>\r\n<p class="Normal">Roswell là một thành phố có khoảng 50.000 người sinh sống. Thành phố này là một trong những nơi nổi tiếng trên thế giới vì có tin cho rằng tàu vũ trụ của người ngoài hành tinh đã tới đây khoảng 65 năm trước, sau đó bị chính phủ Mỹ phát hiện và bắt giữ.</p>\r\n<p class="Normal" align="right"><strong>(Theo <em>TTXVN</em>)</strong></p>', 1, 5, 0, 35, '2012-03-24 17:57:37', 62, '', '2012-03-24 17:58:41', 62, 0, '0000-00-00 00:00:00', '2012-03-24 17:57:37', '0000-00-00 00:00:00', 'obama.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 1, '', '', 0, 1, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (62, 'Dân Mỹ xôn xao vì những âm thanh lạ', 'dan-my-xon-xao-vi-nhung-am-thanh-la', '', '<p>Vài tiếng nổ bí ẩn, những tiếng động khó hiểu là biểu hiện của việc gì đó tại một thành phố nhỏ thuộc bang Wisconsin tại Mỹ, song không ai biết hiện tượng gì đang xảy ra.', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3b/bd/47/25/cong-nhan.jpg" alt="Một công nhân kiểm tra đường ống để" height="324" width="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một công nhân kiểm tra đường ống để xem âm thanh lạ phát ra từ đó hay không. Ảnh: <em>CNN</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nhiều người tại thành phố Clintonville, nơi có khoảng 4.600 dân, kể họ nghe thấy nhiều âm thanh lạ giống như tiếng sấm, pháo hoa, kim loại va chạm vào nhau từ tối 18/3 tới nay. Những âm thanh đó chỉ phát ra từ một khu vực ở phía đông bắc của thành phố, <em>CNN</em> đưa tin.</p>\r\n<p class="Normal">Lisa Kuss, một quan chức thành phố, nói rằng mặt đất rung lắc sau khi những âm thanh lạ vang lên khiến các ngôi nhà và đồ gia dụng lắc lư. Những âm thanh lớn đến nỗi người ở nơi khác có thể nghe thấy chúng khi họ nói chuyện qua điện thoại với người dân trong thành phố. Tuy nhiên, chúng chưa gây nên bất kỳ thiệt hại lớn nào về người và tài sản.</p>\r\n<p class="Normal">“Các công nhân đã xem xét từng nắp cống, dò tìm khí gas rò rỉ và những vấn đề khác, song chưa ai phát hiện điều gì bất thường. Quân đội và các nhà địa chất cũng không biết hiện tượng gì đang diễn ra. Cơ quan Địa chất Mỹ không phát hiện địa chấn tại bang Wisconsin trong hai ngày 18 và 19/3”, Kuss nói.</p>\r\n<p class="Normal">“Chúng tôi liên tục thảo luận về các âm thanh để tìm ra nguồn gốc của chúng”, Verda Shultz, một người dân trong thành phố Clintonville, kể.</p>\r\n<p class="Normal">Nhiều người dân khác đã tự tìm ra câu trả lời.</p>\r\n<p class="Normal">“Tôi đoán những con quỷ đã gây ra âm thanh bí ẩn”, “Chúng phát ra từ một cỗ máy của sinh vật ngoài hành tinh được chôn dưới lòng đất từ hàng nghìn năm trước” là những câu mà người ta đăng lên Facebook.</p>\r\n<p class="Normal">Clintonville không phải là nơi duy nhất chứng kiến những âm thanh lạ. Giới truyền thông từng đưa tin về những trường hợp tương tự tại một số bang như North Carolina, Idaho, Tennessee.</p>\r\n<p class="Normal">David Hill, một nhà khoa học của Cơ quan Địa chất Mỹ, cho rằng thủ phạm gây nên những âm thanh có thể là những hiện tượng thiên nhiên như thiên thạch rơi, sấm, rò rỉ khí từ lòng đất . Chẳng hạn, nếu khí tự nhiên từ bên dưới các lớp đá thoát lên mặt đất, nó sẽ gây nên âm thanh giống tiếng nổ.</p>\r\n<p class="Normal">Hoạt động khai thác đá hoặc khoáng sản cũng gây nên tiếng nổ.</p>\r\n<p class="Normal">“Khi các công nhân đào hoặc khoan trúng lớp đá bị chôn vùi trong thời gian dài, lực nén bên dưới lớp đá được giải phóng và gây nên tiếng nổ lớn”, Hill giải thích.</p>\r\n<p class="Normal" align="right"><strong>Minh Long</strong></p>', 1, 5, 0, 33, '2012-03-24 18:00:13', 62, '', '2012-03-24 18:01:17', 62, 0, '0000-00-00 00:00:00', '2012-03-24 18:00:13', '0000-00-00 00:00:00', 'cong-nhan.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (63, 'Anh lo ngại sự nguy hiểm của bão mặt trời', 'anh-lo-ngai-su-nguy-hiem-cua-bao-mat-troi', '', '<p>Nước Anh vừa bổ sung bão mặt trời và núi lửa vào danh sách các mối đe dọa an ninh quốc gia cùng với lũ lụt, dịch bệnh và nạn khủng bố. ', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3b/bd/44/d1/baotu.jpg" height="350" width="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nước Anh liệt kê bão từ trong danh sách mối nguy hiểm tới người dân. Ảnh: A<em>mateurastronomy.org.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Danh sách các mối đe dọa tự nhiên năm nay do chính phủ Anh đưa ra, mức đe dọa an ninh cao nhất với nước này là dịch cúm, lũ lụt ven biển, các cuộc tấn công khủng bố, thêm hai mục mới là bão mặt trời và núi lửa phun trào ở Iceland.</p>\r\n<p class="Normal">Theo đánh giá của chính phủ Anh, bão mặt trời sẽ đe dọa nghiêm trọng đến hệ thống liên lạc, mạng lưới điện trên mặt đất, các chuyến bay. Với người nông dân, bão từ có thể gây hạn hán kéo dài, thất thu mùa màng.</p>\r\n<p class="Normal">Các cơn bão mặt trời xuất hiện khi dòng hạt mang điện tích phóng ra từ gió mặt trời tác động lên toàn bộ trái đất. Theo chu kỳ 11 năm, dự kiến, cơn bão từ đạt mức cực đại vào năm tới.</p>\r\n<p class="Normal">Những con bão từ không làm hại con người, nhưng có thể đánh sập mạng lưới điện, làm hỏng hệ thống GPS và vệ tinh.</p>\r\n<p class="Normal">Năm 1989, một cơn bão mặt trời cực mạnh đã làm mất điện ở Quebec, Canada, khiến 6 triệu người không có điện sinh hoạt. Tuần trước, cơn bão mặt trời đã mạnh nhất trong vòng 5 năm qua, rất may nó không ra tổn thất nào cho trái đất.</p>\r\n<p class="Normal">Tháng trước, Ủy ban quân sự, thuộc Quốc hội Anh đã kêu gọi chính phủ chuẩn bị trước tình huống xấu nhất trong trường hợp các nguồn cung cấp điện bị ngưng trệ, và vệ tinh ngưng hoạt động trước tác động của các cú sốc điện từ.</p>\r\n<p class="Normal">"Chúng tôi đang ngày càng trở nên phụ thuộc vào công nghệ, nên hậu quả của cơn bão từ gây ra khiến chúng tôi lo sợ", <em>AP</em> dẫn lời James Arbuthnot, nghị sĩ đảng Bảo thủ nói với tờ <em>The Observer. </em>Theo đánh giá của nhiều chuyên gia, mức độ ảnh hưởng từ các cơn bão mặt trời phụ thuộc vào tốc độ và hướng từ trường phát tán.</p>\r\n<p class="Normal" align="right"><strong>Trang Nguyên</strong></p>', 1, 5, 0, 33, '2012-03-24 18:03:19', 62, '', '2012-03-24 18:06:35', 62, 0, '0000-00-00 00:00:00', '2012-03-24 18:03:19', '0000-00-00 00:00:00', 'baotu.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 2, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (64, 'Dệt áo xanh cho rừng Tây Bắc', 'det-ao-xanh-cho-rung-tay-bac', '', '<p>Tôi là một kỹ sư nông nghiệp ở miền xuôi lên công tác tại một tỉnh vùng cao Tây Bắc từ năm 2003. Tây Bắc là nơi sinh sống của phần lớn các dân tộc thiểu số như Thái, Mường, H’Mông...', '</p>\r\n<p class="Normal">Đời sống kinh tế ở các tỉnh Tây Bắc còn gặp rất nhiều khó khăn. Cuộc sống của người dân chủ yếu dựa vào sản xuất nông lâm nghiệp và phụ thuộc rất nhiều vào rừng như: nguồn lâm sản ngoài gỗ, khai thác nguồn tài nguyên từ rừng, phá rừng lấy đất làm nương canh tác nông nghiệp, chặt rừng lấy củi, gỗ để bán kiếm tiền mua gạo, lương thực.</p>\r\n<p class="Normal">Khu vực Tây Bắc gồm 4 tỉnh miền núi, trung du phía Bắc: Điện Biên, Lai Châu, Sơn La, Hòa Bình. Tổng diện tích tự nhiên khoảng 3.737.670 ha, chiếm 11,5 % diện tích cả nước. Trong đó diện tích rừng và đất đồi núi quy hoạch phát triển lâm nghiệp là 2.804.444 ha, chiếm 75% diện tích tự nhiên. Tây Bắc là vùng núi cao, dốc nhất Việt Nam. Đây cũng là vùng đầu nguồn của các hệ sông lớn như sông Đà, sông Mã… có tiềm năng thủy điện lớn nhất cả nước. Hệ thống rừng nơi đây là mái nhà xanh có vai trò quan trọng trong việc điều tiết nguồn nước cho các nhà máy thủy điện; phòng chống lũ lụt, đảm bảo an toàn cho đồng bằng Bắc Bộ và Hà Nội.</p>\r\n<p class="Normal">Theo báo cáo của ngành lâm nghiệp từ năm 2000 đến năm 2008, tỷ lệ che phủ của rừng vùng Tây Bắc liên tục tăng từ 26,97 % (năm 2000) lên 39,49% (năm 2005) và 41,32% (năm 2008). Tuy nhiên, diện tích rừng và đất lâm nghiệp lớn, địa hình cao dốc, chia cắt phức tạp; cơ sở hạ tầng (hệ thống giao thông, các cơ sở hạ tầng phục cuộc sống người dân…) còn nhiều khó khăn; mặt bằng dân trí thấp… đã ảnh hưởng đến phát triển lâm nghiệp của vùng. Thực trạng cho thấy, diện tích các đồi trọc, đất trống và đất do người dân phá rừng làm nương canh tác ngô, sắn… trên các tỉnh Tây Bắc rất lớn. Diện tích này cứ lan rộng theo tỷ lệ tăng trưởng dân và nhu cầu mưu sinh của họ. Đây là một vấn đề khiến các cơ quan quản lý Nhà nước gặp khó khăn trong việc bảo vệ tài nguyên rừng ở vùng Tây Bắc.</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3b/bd/46/43/5b.jpg" border="1" height="330" width="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Gìn giữ màu xanh của núi rừng Tây Bắc. <em>Ảnh minh họa</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Việc canh tác nông nghiệp của người dân (đốt nương, dọn dẹp nương rẫy trong những tháng mùa khô) là một trong những nguyên nhân lớn dẫn đến cháy rừng. Tuy nhiên, các cơ quan chức năng lại nhưng chưa có biện pháp xử lý nghiêm khắc vấn đề này. Mặt khác do sự phát triển kinh tế nên một số ngành công nghiệp như điện (nhà máy thủy điện Hòa Bình, Sơn La, Nậm Chiến, Suối Sập (Sơn La), Huổi Quảng, Bản Chát (Lai Châu), khoáng sản Niken (Sơn La), đất hiếm (Lai Châu), xi măng… cũng đã phá đi một số diện tích rừng làm nơi khai khác.</p>\r\n<p class="Normal">Diện tích rừng bị tàn phá trầm trọng khiến lũ lụt, thiên tai, lũ quét và sạt nở đất đá luôn xảy ra ở vùng Tây Bắc. Một câu hỏi được đạt ra là ý thức của con người? các giải pháp phát triển kinh tế bền vững như thế nào cho phù hợp với điều kiện và người dân ở vùng Tây Bắc?</p>\r\n<p class="Normal">Theo quan điểm của tôi, chúng ta cần giúp người dân biết tìm ra sinh kế bền vững: biết canh tác bền vững trên nương rẫy. Ngoài ra, người dân nên đổi mới cách chăn nuôi, quy mô hộ hoặc trang trại nhỏ hiệu quả; phát triển tốt tiềm năng các cây lâm sản ngoài gỗ như các nguồn cây thuốc, cây đặc sản của Tây Bắc và khai thác tốt tiềm năng du lịch sinh thái tại Tây Bắc… Vì một trái đất xanh, chúng ta nên dệt cho đất tấm áo màu xanh từ các chất liệu của tình yêu cuộc sống.</p>', 1, 5, 0, 33, '2012-03-24 18:04:30', 62, '', '2012-03-24 18:05:42', 62, 0, '0000-00-00 00:00:00', '2012-03-24 18:04:30', '0000-00-00 00:00:00', '5b.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 1, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (65, 'Thay đổi tốc độ xe đạp bằng ý nghĩ', 'thay-doi-toc-do-xe-dap-bang-y-nghi', '', '<p>Một công ty tại Mỹ chế tạo loại xe đạp cho phép người điều khiển thay đổi tốc độ bằng ý nghĩ nhờ loại mũ bảo hiểm đặc biệt.', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3b/bd/43/30/xe-dap-0.jpg" alt="Chiếc xe đạp" height="395" width="460" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Xe đạp Prius cho phép người sử dụng thay đổi tốc độ bằng suy nghĩ. Ảnh: <em>MSNBC.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><em>Innovation News Daily</em> đưa tin Deeplocal, một công ty tiếp thị sản phẩm công nghệ cao tại Mỹ, đã chế tạo loại xe đạp cho phép người lái thay đổi tốc độ bằng ý nghĩ. Patrick Miller, kỹ sư sáng tạo của Deeploacal, chịu trách nhiệm chế tạo hệ thống phát tín hiệu không dây trong mũ bảo hiểm để thay đổi tốc độ xe. Parlee Cycles, công ty chuyên sản xuất những xe đạp đua bằng sợi carbon, chế tạo khung xe của Deeplocal.</p>\r\n<p class="Normal">Hệ thống điện cực bên trong mũ bảo hiểm liên tục dò tìm các tín hiệu điện từ não. Sau đó hệ thống gửi các tín hiệu tới một máy tính bên trong áo vest của người lái.</p>\r\n<p class="Normal">"Máy tính sẽ chuyển các tín hiệu thành lệnh tương ứng, ví dụ như tăng số hay giảm số. Lệnh sẽ được chuyển tới một thiết bị điều khiển nhỏ xíu trong khung xe đạp. Thiết bị điều khiển sẽ tác động tới bộ chuyển đổi tốc độ của xe", Miller giải thích.</p>\r\n<p class="Normal">Miller khẳng định công ty Deeplocal chỉ áp dụng những công nghệ cũ để chế tạo Prius, tên của loại xe đạp mới.</p>\r\n<p class="Normal">"Nó là thành quả của sự tổng hợp các công nghệ đang tồn tại", Miller nói.</p>\r\n<p class="Normal">Bộ chuyển đổi tốc độ điện tử, bao gồm cả những dây phanh, được giấu bên trong khung. Việc đưa các loại dây vào khung làm khiến lực cản của không khí giảm, nhờ đó tốc độ tăng. Hệ thống dò tín hiệu trong mũ bảo hiểm không thể hiểu được mệnh lệnh tăng hay giảm tốc độ trong não người. Thay vào đó, người lái phải được huấn luyện để tạo ra mệnh lệnh trong não sao cho hệ thống có thể xác định được.</p>\r\n<p class="Normal">Nhược điểm của hệ thống biến tín hiệu điện trong não thành mệnh lệnh điều khiển tốc độ xe đạp là nó có thể ngừng hoạt động do tác động của sóng từ điện thoại di động hoặc máy tính xách tay.</p>\r\n<p class="Normal" align="right"><strong>Minh Long</strong></p>', 1, 5, 0, 32, '2012-03-24 18:07:27', 62, '', '2012-03-24 18:08:24', 62, 0, '0000-00-00 00:00:00', '2012-03-24 18:07:27', '0000-00-00 00:00:00', 'xe-dap-0.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (66, 'Sứa kim loại ra đời', 'sua-kim-loai-ra-doi', '', '<p>Các nhà khoa học chế tạo robot hình sứa có khả năng tận dụng năng lượng vô tận của nước biển để chuyển động.', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3b/bd/46/ac/Robojelly-1.jpg" alt="8 mảnh thân của Robojelly" height="306" width="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">8 mảnh thân của Robojelly co giãn để đẩy nước ra ngoài và tiến lên. Ảnh: <em>AFP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Yonas Tadesse, một nhà nghiên cứu của Viện Công nghệ Virginia tại Mỹ, cùng các đồng nghiệp nảy ra ý tưởng dùng hợp kim có khả năng phục hồi hình dạng sau khi chịu tác động của lực bên ngoài để chế tạo robot hình sứa, <em>Telegraph</em> đưa tin.</p>\r\n<p class="Normal">Robojelly - tên của robot hình sứa - sử dụng bó cơ hình tròn để mở và đóng cơ thể cái chuông của nó. Thao tác đóng-mở cơ thể của Robojelly khiến nước bị phun ra ngoài và tạo lực đẩy giúp nó tiến lên. Thân của robot được tạo nên từ 8 mảnh hợp kim. Nhóm nghiên cứu phủ một lớp bột bạch kim màu đen, thứ bột có khả năng tương tác với oxy và hydro trong nước biển để tạo ra nhiệt. Lượng nhiệt đó lan tỏa tới các cơ nhân tạo của Robojelly khiến chúng co vào và đẩy nước ra ngoài. Sau khi co, thân của robot phục hồi hình dạng ban đầu.</p>\r\n<p class="Normal">"Theo hiểu biết của chúng tôi, đây là lần đầu tiên giới khoa học chế tạo thành công một robot có khả năng sử dụng nguồn hydro từ môi trường bên ngoài để làm nhiên liệu", Tadesse phát biểu.</p>\r\n<p class="Normal">Hiện tại Robojelly mới chỉ có thể di chuyển theo một hướng vì 8 mảnh cơ thể nó co giãn cùng lúc. Nhóm nghiên cứu sẽ tiếp tục cải tiến để các mảnh co giãn vào các thời điểm khác nhau, nhờ đó robot sẽ di chuyển theo nhiều hướng.</p>\r\n<p class="Normal">Phát minh của Tadesse và các đồng nghiệp được công bố trên tạp chí Smart Materials and Structures của Viện Vật lý Anh.</p>\r\n<p class="Normal" align="right"><strong>Minh Long</strong></p>', 1, 5, 0, 32, '2012-03-24 18:08:50', 62, '', '2012-03-24 18:09:53', 62, 0, '0000-00-00 00:00:00', '2012-03-24 18:08:50', '0000-00-00 00:00:00', 'robojelly-1.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 1, '', '', 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (67, 'Angela Phương Trinh diện đồ "mát mẻ" sau khi chia tay bạn trai ', 'angela-phuong-trinh-dien-do-mat-me-sau-khi-chia-tay-ban-trai', '', '<p>Cô nàng trông vẫn rất tươi tắn và vui vẻ.', '</p>\r\n<p class="sapo">Cô nàng trông vẫn rất tươi tắn và vui vẻ.</p>\r\n<div style="text-align: justify;">Hôm qua (24/3), cô nàng<span style="font-weight: bold;"> Angela Phương Trinh</span> đã xuất hiện tại một sự kiện khai trương cửa hàng mới ở Sài Gòn. Được cánh săn ảnh khá chú ý vì đây là lần "tái xuất" đầu tiên của <span style="font-weight: bold;">Angela </span>sau khi <a target="_blank" href="http://kenh14.vn/c102/20120314041944917/angela-phuong-trinh-da-chia-tay-ban-trai-lon-tuoi.chn"><span style="font-weight: bold;">chia tay bạn trai</span></a> cách đây không lâu. Thời điểm đó, <span style="font-weight: bold;">Angela </span>tiết lộ rằng chính cô nàng là người <a target="_blank" href="http://kenh14.vn/c102/20120315124126187/angela-phuong-trinh-chu-dong-chia-tay-nguoi-yeu-lon-tuoi.chn"><span style="font-weight: bold;">chủ động nói lời chia tay</span></a>, vậy nên bản thân không quá "sốc". Có lẽ, chính vì vậy mà trong lần trở lại công khai tại sự kiện này, <span style="font-weight: bold;">Angela </span>trông khá tươi tắn và vui vẻ. Cô nàng lại rạng rỡ và nhí nhảnh như trước đây.\r\n<p>Tuy nhiên, một điểm khác cũng thu hút ống kính của các nhiếp ảnh chính là style ăn mặc của&nbsp; <span style="font-weight: bold;">Angela Phương Trinh</span>. Cô nàng diện một chiếc áo cổ trễ, hở phần bụng và đường cắt "táo bạo" sau lưng cũng khiến "đường cong" của <span style="font-weight: bold;">Angela</span> lộ hết ra ngoài. Cùng ngắm những hình ảnh mới nhất của <span style="font-weight: bold;">Angela Phương Trinh</span>.</p>\r\n</div>\r\n<p><br /><img src="http://k14.vcmedia.vn/CGYTHfZgPOUqbvI0g75Q9XTWccccc/Image/2012/03/120325staangela-10_d42f6.jpg" /></p>\r\n<p><img src="http://k14.vcmedia.vn/CGYTHfZgPOUqbvI0g75Q9XTWccccc/Image/2012/03/120325staangela-9_20a01.jpg" /></p>\r\n<p><img src="http://k14.vcmedia.vn/CGYTHfZgPOUqbvI0g75Q9XTWccccc/Image/2012/03/120325staangela-7_c8419.jpg" /><br style="font-style: italic;" /><span style="font-style: italic;">Angela Phương Trinh không chỉ diện áo hở rốn...</span></p>\r\n<p><img src="http://k14.vcmedia.vn/CGYTHfZgPOUqbvI0g75Q9XTWccccc/Image/2012/03/120325staangela-2_296a3.jpg" /><br style="font-style: italic;" /><span style="font-style: italic;">... mà còn hở cả lưng</span></p>\r\n<p><img src="http://k14.vcmedia.vn/CGYTHfZgPOUqbvI0g75Q9XTWccccc/Image/2012/03/120325staangela-4_7c782.jpg" /></p>\r\n<p><img src="http://k14.vcmedia.vn/CGYTHfZgPOUqbvI0g75Q9XTWccccc/Image/2012/03/120325staangela-6_037c8.jpg" /></p>\r\n<p><img src="http://k14.vcmedia.vn/CGYTHfZgPOUqbvI0g75Q9XTWccccc/Image/2012/03/120325staangela-1_d59ba.jpg" /></p>', 1, 8, 0, 14, '2012-03-24 18:11:09', 62, '', '2012-03-24 18:12:58', 62, 0, '0000-00-00 00:00:00', '2012-03-24 18:11:09', '0000-00-00 00:00:00', '120325staangela-1_d59ba.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 1, '', '', 0, 1, 0, 'robots=\nauthor=');

