INSERT INTO `jos_content` VALUES (5, '“Gái hai con” Lưu Đào trẻ trung tới bất ngờ!', 'gai-hai-con-luu-dao-tre-trung-toi-bat-ngo', '', '<p>Nhận lời làm MC cho buổi tiệc gây quỹ từ thiện giúp đỡ những người nghèo gặp hoàn cảnh khó khăn tại Bắc Kinh, nữ diễn viên Lưu Đào khiến tất cả mọi người phải ngỡ ngàng bởi sự trẻ trung và đáng yêu của mình.', '</p>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24031_70909.jpg" /></span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Lưu Đào là một trong những nhân vật trung tâm của buổi tiệc gây quỹ từ thiện tại Bắc Kinh, hôm 23/3.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24032_4f43c.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24033_1368b.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Cô ăn mặc và làm tóc rất trẻ trung, khiến nhiều đồng nghiệp và giới truyền thông bất ngờ.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24034_b3b49.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Lưu Đào kêu gọi mọi người quan tâm tới những đứa trẻ mồ côi, sinh trưởng trong điều kiện khó khăn bởi "trẻ em vốn là những thiên thần nhỏ và xứng đáng được hưởng những điều tốt đẹp nhất trong cuộc sống này".</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24036_1a708.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Bà mẹ hai con cho biết, với cô, hai đứa con là những món quà quý giá nhất mà cuộc sống ban tặng cho vợ chồng cô.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24037_b32a2.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Chính bởi vậy, vì các con, cô sẵn sàng hi sinh sự nghiệp riêng để không bỏ lỡ bất kỳ bước trưởng thành nào của các con.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24038_3682b.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24039_98bbb.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Trước đó, Lưu Đào từng xuất hiện rất gợi cảm với váy khoe lưng trần tại một sự kiện khác ở Thượng Hải.</span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240310_01e6e.jpg" /></span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240311_99053.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">"Nàng A Châu" của <span style="font-style: italic;">Thiên long bát bộ</span>ngày một đẹp và đằm thắm hơn!</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240312_029f8.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Lưu Đào bất ngờ kết hôn vào năm 2008 với một trong những đại gia của Trung Quốc đại lục.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240313_80d82.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240314_8018b.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Kể từ khi lập gia đình, cô tạm gác sự nghiệp diễn xuất riêng để tập trung chăm sóc người thân và sinh liền cho ông xã hai đứa con, một gái và một trai.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240315_ffe02.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240316_35052.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240317_caa1b.jpg" /></span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Bắt đầu từ năm 2011, Lưu Đào bắt đầu trở lại với một số hoạt động của làng giải trí và góp mặt trong một số bộ phim truyền hình.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240318_7ac11.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240319_10ec7.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240320_0d6c2.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240321_f191a.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240322_aa106.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Tuy nhiên, Lưu Đào luôn khẳng định, sự nghiệp điện ảnh giờ không còn là ưu tiên số một của cô, bởi gia đình nhỏ nơi có những đứa con xinh xắn, đáng yêu và một ông xã tốt bụng hết lòng chiều chuộng vợ mới là điều cô trân trọng nhất. Cuộc sống gia đình viên mãn khiến Lưu Đào lúc nào cũng tươi tắn và trẻ trung.</span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<div align="right"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;">Theo Dantri.com<br /></span></div>', 1, 4, 0, 29, '2012-03-24 14:06:06', 62, '', '2012-03-24 14:10:58', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:06:06', '0000-00-00 00:00:00', 'dao24039_98bbb.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 8, '', '', 0, 3, 1, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (7, 'Cận cảnh nhan sắc Việt', 'can-canh-nhan-sac-viet', '', '<p>Ánh mắt, bờ môi, sóng mũi trên gương mặt của Hoa hậu Diễm Hương, diễn viên múa Linh Nga, ca sĩ Mai Khôi... được nhiếp ảnh Hà Sơn Minh ''zoom'' thật sát. Màu đen trắng được sử dụng càng làm các bức ảnh thêm bắt mắt.', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/diem-huong.jpg" border="1" height="317" width="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nhiếp ảnh gia Hà Sơn Minh (bút danh minHa) kết hợp stylist Nguyễn Bình Yên thực hiện nhiều bộ ảnh cho các người đẹp Việt suốt một năm qua. Dịp cuối năm, cả hai quyết định tổng hợp một số gương mặt mà họ chộp được những góc "xuất thần". Trong ảnh là một góc lạ của Hoa hậu Thế giới Người Việt Diễm Hương.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/phung-ngoc-yen.jpg" border="1" height="350" width="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Màu ảnh trắng đen giúp nổi bật những nét thanh tú trên gương mặt của các nhan sắc Việt. Một vài bức ảnh được êkíp chụp tự nhiên, không hề sắp đặt tạo nên độ biểu cảm lớn. Bức ảnh ghi lại một góc nhìn "sắc lẹm" của người mẫu Phùng Ngọc Yến.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/ngoc-quyen.jpg" border="1" height="345" width="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Người mẫu Ngọc Quyên.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/truong-thi-may.jpg" border="1" height="589" width="420" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Vẻ ưu tư của Á hậu dân tộc Trương Thị May.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/mai-khoi.jpg" border="1" height="607" width="420" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ca sĩ Mai Khôi luôn toát lên vẻ gợi cảm trong mọi tư thế.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/kim-thu.jpg" border="1" height="610" width="420" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một bức ảnh "chộp" bà chủ nhà hàng Biển Nhớ bất ngờ. Tóc bay tự nhiên, miệng cười mỉm cũng tự nhiên làm Kim Thư trông thật đằm thắm.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/hien-thuc.jpg" border="1" height="327" width="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ca sĩ Hiền Thục.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/linh-nga.jpg" border="1" height="340" width="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Diễn viên múa Linh Nga.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style="text-align: right;"><strong>Theo Vnexpress.net</strong></p>', 1, 4, 0, 29, '2012-03-24 14:19:48', 62, '', '2012-03-24 14:21:05', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:19:48', '0000-00-00 00:00:00', 'linh-nga.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 6, '', '', 0, 2, 1, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (8, 'Thế giới bóng đá tiếp tục chia tay một cầu thủ vì…đột quỵ', 'the-gioi-bong-da-tiep-tuc-chia-tay-mot-cau-thu-vidot-quy', '', '<p>Khi mà những thông tin về vụ đột quỵ của tiền vệ CLB Bolton, Muamba vẫn còn rất nóng hồi thì thế giới bóng đá tiếp tục phải đón nhận tin buồn khi cầu thủ Venkatesh (Bangalore Mars) đã qua đời sau cơn đau tim ngay trên sân cỏ.', '</p>\r\n<p>Tiền vệ Muamba (Bolton) đang phải chiến đấu với sự sống nhưng anh còn may mắn hơn rất nhiều người đồng nghiệp Venkatesh (Bangalore Mars) ở đất nước Ấn Độ. Theo báo giới ở đất nước này, Venkatesh đã qua đời ngay trên sân bóng sau một cơn đau tim.</p>\r\n<p>&nbsp;</p>\r\n<div style="text-align: center;"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/uajiKupQ6reCuKKDlVlG/Image/2012/03/an-do_b23df.jpg" _fl="" width="450" align="middle" /><br /><span style="font-family: Tahoma; font-size: 10pt;">Venkatesh đã không may mắn như Muamba</span></div>\r\n<p>Nói Muamba may mắn là bởi lẽ anh được sự chăm sóc nhiệt tình, chuyên nghiệp của những nhân viên y tế ở trên sân, còn Venkatesh thì không. Với tình trạng thiết bị nghèo nàn lạc hậu cũng như trình độ bác sĩ ở Ấn Độ đã không thể giúp Venkatesh vượt qua cơn nguy kịch.</p>\r\n<p>Venkatesh đã qua đời ngay trên chiếc xe cứu thương thô sơ, quá tồi tàn. Cái chết của cầu thủ người Ấn Độ này tiếp tục là thông tin buồn với bóng đá thế giới, chỉ trong vòng chưa đầy 1 tuần đã xảy ra tới 2 vụ đột quỵ vô cùng nghiêm trọng.</p>\r\n<p>Venkatesh không phải cầu thủ đầu tiên qua đời ngay trên sân cỏ Ấn Độ, còn nhớ, 7 năm trước họ đã đau buồn tiễn đưa cầu thủ người Brazil, Cristioano Junior của CLB Dempo Sports Club. Năm ngoái thủ thành Arun Kumar cũng qua đời sau chấn thương trong 1 buổi tập.</p>\r\n<p>Nói về sự kiện này, một CĐV Ấn Độ đau xót đề cập tới trang thiết bị của những sân bóng quê nhà: “Sự việc này đã phơi bày tình trạng thảm hại của những sân bóng Ấn Độ, không phải tới sau khi Venkatesh qua đời, người ta mới biết đến điều này. Không có một bác sĩ ở trên sân, cũng không xe cứu thương, mọi chuyện đâu chỉ đơn giản là túi đá trườm và thuốc giảm đau”.</p>\r\n<p style="text-align: right; font-weight: bold;">Theo Dantri.com</p>\r\n<input id="gwProxy" type="hidden" /><!--Session data--><input jscode="leoInternalChangeDone()" onclick="if(typeof(jsCall)==''function''){jsCall();}else{setTimeout(''jsCall()'',500);}" id="jsProxy" type="hidden" />\r\n<div id="refHTML">&nbsp;</div>', 1, 7, 0, 38, '2012-03-24 14:23:08', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-03-24 14:23:08', '0000-00-00 00:00:00', 'an-do_b23df.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 5, '', '', 0, 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (6, 'Phan Huỳnh Điểu xúc động vì thí sinh Tiếng hát mãi xanh', 'phan-huynh-dieu-xuc-dong-vi-thi-sinh-tieng-hat-mai-xanh', '', '<p>Làm giám khảo cuộc thi hát cho người trung, cao niên, Phan Huỳnh Điểu suýt khóc khi nghe thí sinh Nguyễn Thị Ngọc Mỹ thể hiện ''Tình ca cho em'', bài hát khiến ông thấy nhạc sĩ quá cố Nguyễn Nam kề bên.', '</p>\r\n<p class="Normal">Đêm chung kết đầu tiên, chủ đề "Dòng nhạc trữ tình, lãng mạn" của cuộc thi Tiếng hát mãi xanh diễn ra tối 23/3 tại Nhà hát truyền hình, TP HCM với sự tham gia của hơn 500 khán giả.</p>\r\n<p class="Normal">Khi thí sinh Nguyễn Thị Ngọc Mỹ, 37 tuổi, đến từ Bến Tre hoàn thành nhạc phẩm <em>Tình ca cho em</em> của Nguyễn Nam, nhạc sĩ Phan Huỳnh Điểu rưng rưng nói lời cảm ơn vì chị hát rất cảm xúc, rất đạt bài hát trữ tình. Phan Huỳnh Điểu nói, khi nghe Ngọc Mỹ hát, ông thấy như Nguyễn Nam vẫn còn đang ngồi đây, kề bên mọi người. Tháng 10/2011, tác giả <em>Tình ca cho em</em> <a href="http://vnexpress.net/gl/van-hoa/guong-mat-nghe-sy/2011/10/nhac-si-nguyen-nam-qua-doi-vi-ung-thu/" class="Normal">qua đời ở tuổi 60</a> vì bệnh ung thư, để lại niềm thương nhớ trong bạn bè và khán giả.</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3b/bd/47/65/ngoc-myto.jpg" alt="Thí sinh Ngọc Mỹ có hát &quot;Tình ca cho em&quot; khiến mọi người đều xúc động. Ảnh: Lý Võ Phú Hưng." border="1" height="682" width="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Thí sinh Ngọc Mỹ hát "Tình ca cho em" (nhạc Nguyễn Nam, lời Nguyễn Nam - Phan Vũ)khiến mọi người đều xúc động. Ảnh: <em>Lý Võ Phú Hưng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">NSND Trần Hiếu cũng dành nhiều lời khen ngợi Ngọc Mỹ. Ông nhận xét, thí sinh này hát rất có hồn, trau chuốt. Nhờ chiếm cảm tình của khán giả và giám khảo, chị đã đoạt giải "Thí sinh thể hiện dòng nhạc trữ tình - lãng mạn xuất sắc" ở đêm này. Khi kết quả được công bố, không chỉ người nhà của thí sinh bày tỏ sự vui mừng mà mọi người trong khán phòng đều vỗ tay tán thưởng.</p>\r\n<p class="Normal"><span style="color: #3f3f3f;" color="#3f3f3f"><strong>Nhiều thí sinh khác của đêm chung kết một cũng để lại cho khán giả và ban giám khảo cảm xúc, ấn tượng khó quên.</strong></span></p>\r\n<p class="Normal">Cùng bảng C (độ tuổi từ 35 đến 50) với Ngọc Mỹ là Thái Thanh Hiệp (37 tuổi) và Trang Thị Mai Hương (49 tuổi). Khi lên sân khấu, họ hát hết sức mình, đúng với tiêu chí của chương trình là "hát cho thỏa đam mê".</p>\r\n<p class="Normal">Khi Thái Thanh Hiệp xuất hiện, anh thu hút khán giả vừa bởi vóc dáng điển trai, cao ráo, vừa với chất giọng khỏe khi trình bày ca khúc <em>Anh còn nợ em </em>(thơ Phan Thanh Tài, nhạc Anh Bằng). Giám khảo Văn Thị Minh Hương, Giám đốc Nhạc viện TP HCM nói đùa, nghe thí sinh này hát là biết anh còn nợ nần quá nhiều. Tiến sĩ Văn Thị Minh Hương sẵn sàng nhận Thái Thanh Hiệp vào khoa Thanh nhạc của Nhạc viện dù anh quá độ tuổi để được nhận vào trường. Theo chị, khi anh vừa cất tiếng hát, cả ban giám khảo đều bất ngờ với chất giọng khỏe khoắn và ngọt ngào...</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3b/bd/47/65/thai-thanh-hiepto.jpg" alt="Thí sinh Thái Thanh Hiệp là người có điểm số cao nhất đêm chung kết một cuộc thi &quot;Tiếng hát mãi xanh&quot;: 19,73 điểm." border="1" height="655" width="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Thí sinh Thái Thanh Hiệp là người nhận điểm số trung bình từ ban giám khảo cao nhất đêm chung kết một cuộc thi Tiếng hát mãi xanh: 19,73 điểm. Ảnh: <em>Lý Võ Phú Hưng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Bên cạnh lời khen, các giám khảo cũng có những góp ý chân tình để người thi hoàn thiện mình hơn. Với bài hát <em>Mơ về nơi xa lắm</em> (Phú Quang) do thí sinh Trang Thị Mai Hương, 49 tuổi, đến từ An Giang thể hiện, NSƯT Ánh Tuyết cho rằng, chị đã hát nhạc phẩm này với sự trìu mến nhưng chất giọng hơi bị căng thẳng. "Đây là một bài hát trữ tình, tình cảm không nên quá nặng nề mà nên bay bổng, nhẹ nhàng hơn", nữ giám khảo nhận xét.</p>\r\n<p class="Normal">Bảng A (từ 66 tuổi trở lên) cụ Nguyễn Thị Ngọc Thanh, 75 tuổi, khuấy động đêm thi khi nhảy tango, hát <em>Bóng chiều tà</em> của Anh Bằng trên sân khấu, còn khán giả ở dưới thì vỗ tay làm nhịp át cả ban nhạc. Giám khảo, nhạc sĩ Phan Huỳnh Điểu (năm nay 88 tuổi) vui vẻ khen: "Giá mà bà xã tôi hát hay như cô thì tôi còn sáng tác được nhiều bài hay lắm". Thành viên khác của ban giám khảo là NSƯT Ánh Tuyết cũng không tiếc lời: “Khi cô bước ra trông đã thật đẹp rồi, lúc cất giọng thì hay quá, đến khi nhảy thì lại càng duyên dáng và tuyệt vời”.</p>\r\n<p class="Normal">Khi thể hiện ca khúc <em>Lệ đá</em> (thơ Hà Huyền Chi, nhạc Trần Trịnh), thí sinh Dương Văn Vá (68 tuổi), cũng được giám khảo, NSND Trần Hiếu chúc mừng: "Giọng anh rất sướng, không phải giọng người già mà là giọng trẻ!". Còn giám khảo Văn Thị Minh Hương thì xuýt xoa: “Chất giọng của chú những người chuyên nghiệp còn ước ao mà có... Xin chú cứ tự tin ở những đêm thi tiếp theo”.</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3b/bd/47/65/9-thi-sinhto.jpg" alt="9 thí sinh vòng chung kết cuộc thi &quot;Tiếng hát mãi xanh&quot; mang lại cho khán giả đêm thi nhiều cảm xúc." border="1" height="296" width="495" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">9 thí sinh vòng chung kết cuộc thi Tiếng hát mãi xanh mang lại cho khán giả đêm thi nhiều cảm xúc. <em>Ảnh: Lý Võ Phú Hưng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ở độ tuổi "trung lập" của cuộc thi, các thí sinh bảng B (từ 51 đến 65 tuổi) cũng không kém cạnh. Chị Trần Thị Kim Thoa, 53 tuổi, xuất thân từ gia đình có cụ thân sinh là nghệ sĩ kèn. Không là ca sĩ chuyên nghiệp, nhưng nhờ ảnh hưởng từ cha, chị có dáng dấp một nghệ sĩ và giọng hát mượt mà, đằm thắm. Chị thể hiện cảm xúc ca khúc <em>Cô đơn</em> (Nguyễn Ánh 9). Dù vậy, có lẽ do căng thẳng, chị hát chưa thật đúng phong độ của mình như ở các vòng bán kết. Nhiều khán giả hy vọng, ở các đêm thi tới, chị Kim Thoa sẽ bứt phá hơn. Hai thí sinh Trần Thị Thu Thủy (54 tuổi) và Dương Xuân Chánh (57 tuổi) cũng để lại nhiều rung cảm tươi trẻ nơi người xem khi lần lượt thể hiện ca khúc <em>Lâu đài tình ái</em> (Trần Thiện Thanh) và <em>Phượng hồng</em> (nhạc Vũ Hoàng, thơ Đỗ Trung Quân).</p>\r\n<p class="Normal">Sau đêm chung kết một, các thí sinh sẽ đến với đêm thi thứ hai - dòng nhạc truyền thống cách mạng và các ca khúc mang âm hưởng dân ca, diễn ra vào 20h30 ngày 30/3 và đêm ba - đêm các thí sinh tự thể hiện mình, diễn ra vào lúc 20h30 ngày 6/4, diễn ra tại Nhà hát truyền hình HTV, truyền hình trực tiếp trên HTV7.</p>\r\n<p style="text-align: right;" class="Normal"><strong>Theo Vnexpress.net</strong></p>', 1, 4, 0, 29, '2012-03-24 14:12:33', 62, '', '2012-03-24 14:17:14', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:12:33', '0000-00-00 00:00:00', 'ngoc-myto.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 7, '', '', 0, 0, 1, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (9, 'Gọi tên Messi', 'goi-ten-messi', '', '<p>Trong các môn thể thao, những người chơi giỏi vào hàng kiệt xuất sẽ được gọi là vĩ đại. Thông thường thì để được gọi là vĩ đại, người chơi sẽ phải tích lũy trong các một quá trình dài, nhưng điều đáng ngạc nhiên Messi mới 24 tuổi đã đạt được kết quả ấy...', '</p>\r\n<div style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Lionel Messi - Bóng đá</span></div>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Messi_7a936.jpg" _fl="" width="450" align="middle" /></div>\r\n<div style="margin: 0cm 0cm 0pt;" align="left">&nbsp;24 tuổi, Messi đã trở thành người ghi bàn nhiều nhất trong lịch sử Barcelona với 234 bàn thắng. Nhìn những gì mà Messi giành được, từ các giải thưởng cùng CLB đến cá nhân thì người hâm mộ chẳng thể nói anh là một cầu thủ xuất sắc nữa, mà chỉ có thể nói rằng giờ đây anh đã là một huyền thoại sống. Và rất may hiện nay những người hâm mộ bóng đá vẫn được chứng kiến cầu thủ vĩ đại này thi đấu.</div>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Muhammad Ali - Quyền Anh</span></p>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Ali_4fe11.jpg" _fl="" width="450" align="middle" /></div>\r\n<p style="margin: 0cm 0cm 0pt;">Muhammad Ali chẳng giống một ngôi sao quyền anh nào trong thời đại của ông. Ông trở thành tay đấm vĩ đại của môn thể thao này nhờ cú hat-trick vô địch quyền anh hạng nặng thế giới, thời còn thi đấu Ali đã tham gia rất nhiều trận đấu nổi tiếng và thu hút được đông đảo sự quan tâm của dư luận. Tầm ảnh hưởng của Ali đã vượt qua môn quyền anh và giúp ông có tiếng nói cho phong trào đòi dân quyền (cho người da đen) tại Mỹ ở thập niên 60 thế kỷ trước.</p>\r\n<div style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Usain Bolt - Điền kinh </span></div>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Bolt_5ecba.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<p style="margin: 0cm 0cm 0pt;">Những gì mà Bolt thể hiện trên đường chạy 100 mét khiến cả thế giới phải “kinh hoàng”, bởi không ai nghĩ rằng con người có thể đạt tới điều đó. Hiện nay, Bolt đang nắm giữ cả 2 kỷ lục tại đường chạy 100m (9 giây 58) và 200m (19 giây 19), những kỷ lục mà có lẽ chẳng biết đến bao giờ mới có người phá vỡ. Quả thật với tuổi 25, cũng khá trẻ như Messi, Bolt đã khiến cả thế giới phải ngưỡng mộ anh.</p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Roger Federrer - Tennis</span></p>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Federer_25e0f.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<div style="margin: 0cm 0cm 0pt;">Trong làng banh nỉ, có rất nhiều&nbsp;người chơi giỏi, giành nhiều danh hiệu. Nhưng chắc chắn nếu nói đến các tay vợt nam, người hâm mộ sẽ nghĩ đến ngay Roger Federrer, người đang có được 16 danh hiệu Grand Slam và 41 danh hiệu Master. Bởi thế người hâm mộ mới đặt cho anh biệt danh “Tàu tốc hành”</div>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Wayne Gretzky - Hockey trên băng</span></p>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/GRETZKY_3f40e.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<div style="margin: 0cm 0cm 0pt;">Chẳng phải ngẫu nhiên mà biệt danh của Wayne Gretzky lại là “Người vĩ đại”. Sự nghiệp của cầu thủ Canada này kéo dài tới 2 thập kỷ và trong quãng thời gian thi đấu, ông đã xô đổ mọi kỷ lục ở môn thể thao này. Dù đã giải nghệ vào cuối thập niên 90 thế kỷ trước, nhưng hiện tại kỷ lục ghi nhiều bàn thắng nhất tại giải NHL của ông vẫn chưa có người xô đổ được.</div>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Michael Jordan - Bóng rổ</span></p>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Jordan_dabfa.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<div style="margin: 0cm 0cm 0pt;">Tên tuổi của Jordan chắc chắn đã vượt qua ngoài môn bóng rổ. Với sự xuất sắc trong lối chơi, giành 6 cúp vô địch NBA (giải bóng rổ nhà nghề Mỹ) và 5 lần được NBA MVP vinh danh, Jordan khiến cả thế giới phải ngưỡng mộ anh.</div>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Jonad Lomu – Bóng bầu dục</span></p>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Lomu_a1e3c.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<div style="margin: 0cm 0cm 0pt;">Người đàn ông cao 1m96 nặng 125 kilogam trở thành ngôi sao toàn cầu đầu tiên của môn bóng bầu dục, sự trình diễn của anh tại giải Vô địch thế giới năm 1995 được tổ chức tại New Zealand được mô tả khiến tất cả người xem phải bật dậy. Trong Lomu là sự pha trộn giữa cả sức mạnh và tốc độ, nên người hâm mộ đặt cho anh biệt danh là “quái vật”.</div>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Michael Phelp - Bơi</span></p>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Phelp_559c3.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<div style="margin: 0cm 0cm 0pt;">Phelp là tay bơi vĩ đại nhất trong lịch sử Olympic, khi anh giành tổng cộng đến 14 huy chương vàng tại các giải đấu của sân chơi này. Ở Athens Phelp giành tới 6 chiếc huy chương vàng, 4 năm sau tại Bắc Kinh anh giành tới 8 chiếc huy chương vàng, phá vỡ kỷ lục của Mark Spitz trước kia giành 7 HCV tại kỳ ĐH Olympic. Với Olympic 2012 sắp diễn ra, người hâm mộ đang chờ đợi Phelp phá kỷ lục người giành nhiều HCV Olympic nhất là 18 tấm của VĐV thể dục người Liên Xô trước đây, Larisa Latynina.</div>\r\n<div style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Michael Schumicher - F1</span></div>\r\n<div style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;"></span>&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Schumi_94677.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<p style="margin: 0cm 0cm 0pt;">Với 7 chiếc cúp vô địch trên đường đua F1, trong đó có một loạt 5 chức vô địch liên tiếp từ năm 2000 - 2004 đã đủ nói lên sự thống trị của tay đua người Đức khi còn gắn bó với Ferrari, con số trên đã đủ đưa Schumi lên hàng tay đua vĩ đại nhất trong lịch sử môn thể thao này.</p>\r\n<div style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Sachin Tendulkar - Cricket</span></div>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Tendukar_c9383.jpg" _fl="" width="450" align="middle" /></div>\r\n<div style="margin: 0cm 0cm 0pt;" align="left">Biệt danh của Tendulkar là “tiểu sư phụ”. Anh có trận ra mắt đầu tiên ở đội tuyển Ấn độ khi mới chỉ 16 tuổi và giờ đây Tendulkar chuẩn bị bước sang tuổi 39. Phá vỡ mọi kỷ lục trong quãng thời gian dài thi đấu, và chỉ mới tuần trước Tendulkar trở thành VĐV trên thế giới đạt mốc 100 trận thi đấu quốc tế.</div>\r\n<div style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Tiger Woods - Golf</span></div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Woods_271bc.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="left">Sự thống trị của Tiger Woods ở môn Golf suốt một thập kỷ qua khiến anh trở thành tay golf vĩ đại nhất thế giới của môn thể thao này. Và có lẽ nếu không có những scandal tình ái đã lấy đi mất phong độ của Woods thì không hiểu anh sẽ còn thống trị vị trí số 1 của môn thể thao này đến bao giờ.</div>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<p style="text-align: right;"><span style="font-weight: bold;">Theo Dantri.com<br /></span></p>', 1, 7, 0, 38, '2012-03-24 14:25:25', 62, '', '2012-03-24 14:26:26', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:25:25', '0000-00-00 00:00:00', 'messi_7a936.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 4, '', '', 0, 0, 1, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (10, 'Chùm ảnh biếm họa Thierry Henry: “Tượng đài sống”', 'chum-anh-biem-hoa-thierry-henry-tuong-dai-song', '', '<p>Vừa mới đây Henry bất ngờ trở Anh từ Mỹ để thăm người đồng đội cũ Muamba bị đột quị, điều đó cho thấy nhân cách lớn trong con người của Titti, một tượng đài bóng đá thực sự trong lòng những người hâm mộ Arsenal và nước Pháp.', '</p>\r\n<div align="left"><span style="font-weight: bold;">Tượng đài sống của "Pháo thủ"</span></div>\r\n<div align="left">&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA7_a66c7.jpg" _fl="" width="450" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA2_f43dd.jpg" _fl="" width="375" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA3_496ba.jpg" _fl="" width="300" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA4_bcd7a.jpg" _fl="" width="300" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA5_f58a7.jpg" _fl="" width="299" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA6_50ea3.jpg" _fl="" align="middle" /></div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA1_3941a.jpg" _fl="" width="300" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA9_9105e.jpg" _fl="" width="350" align="middle" /></div>\r\n<div align="left">&nbsp;</div>\r\n<div align="left"><span style="font-weight: bold;">Cùng Barcelona chinh phục các đỉnh cao mới nh