INSERT INTO `jos_content` VALUES (45, 'Galaxy Y Duos hai sim giá 4,5 triệu', 'galaxy-y-duos-hai-sim-gia-45-trieu', '', '<p>Samsung sẽ bán chiếc Galaxy Y hai sim trong tháng tới, máy đắt hơn phiên bản đi trước hơn một triệu đồng.', '</p>\r\n<p class="Normal">Galaxy Y Duos là phiên bản mới của chiếc smartphone chạy Android giá rẻ hãng giới thiệu năm ngoái. Điểm nổi bật của máy là tính năng hai sim online đồng thời, với hai khe cắm bên dưới. Đây là một trong nhiều di động hai sim của Samsung ở tầm giá thấp ra mắt năm nay.</p>\r\n<p class="Normal">Máy chạy Android 2.3, sở hữu cấu hình trung bình với màn hình 3,12 inch, độ phân giải thấp QVGA 320 x 240 pixel. Sản phẩm có máy ảnh 3 Megapixel, vi xử lý 832MHz, RAM 385MB, bộ nhớ trong 512MB. Galaxy Y hai nhân tích hợp các kết nối tốc độ cao như HSDPA, Wi-Fi, Bluetooth, GPS cùng với cổng microUSB và đài FM. Máy có pin dung lượng 1.000 mAh, thiết kế gọn giống phiên bản đi trước.</p>\r\n<p class="Normal">Samsung sẽ bán model này với thẻ nhớ 2GB đi kèm. Galaxy Y Duos là đối thủ của chiếc Optimus Net mà LG bán ra trước đây, cả hai cùng tầm giá, chạy Android và sở hữu hai sim.</p>\r\n<p class="Normal"><strong><span style="color: #2f2f2f;" color="#2f2f2f">Dưới đây là một số hình ảnh thực tế "đập hộp" chiếc Galaxy Y hai sim trên iGyaan.</span></strong></p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Hộp đựng máy. Galaxy Y Duos là chiếc smartphone hai sim chạy Android rẻ nhất của hãng." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5E/samsung_galaxy_y_dous_1.jpg" border="1" height="305" width="480" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hộp đựng máy. Galaxy Y Duos là chiếc smartphone hai sim chạy Android rẻ nhất của hãng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Các thông tin về cấu hình của máy, đây là bản nâng cấp của model bán tốt năm ngoái." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5E/samsung_galaxy_y_dous_2.jpg" border="1" height="345" width="480" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Các thông tin về cấu hình của máy, đây là bản nâng cấp của model bán tốt năm ngoái.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Ngoài thẻ nhớ 2GB, máy còn có tai nghe, sạc, pin." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5E/samsung_galaxy_y_dous_3.jpg" border="1" height="392" width="480" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ngoài thẻ nhớ 2GB, máy còn có tai nghe, sạc, pin.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Thiết kế của model này không khác nhiều thế hệ Galaxy Y đầu tiên." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5E/samsung_galaxy_y_dous_4.jpg" border="1" height="424" width="480" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Thiết kế của model này không khác nhiều thế hệ Galaxy Y đầu tiên.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Giắc cắm tai nghe trên đỉnh." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5E/samsung_galaxy_y_dous_5.jpg" border="1" height="320" width="480" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Giắc cắm tai nghe trên đỉnh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Với kích thước 109.8 x 60 x 12 mm, nặng 109 gram, máy rất gọn khi cầm trên tay." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/5E/samsung_galaxy_y_dous_6.jpg" border="1" height="418"