INSERT INTO `jos_content` VALUES (84, 'Liễu Nham khoe ngực đẹp, eo thon', 'lieu-nham-khoe-nguc-dep-eo-thon', '', '<p>Thay hai chiếc váy trong buổi tiệc dành cho giới nghệ sĩ Trung Quốc tại Bắc Kinh tối 31/3, nữ diễn viên Liễu Nham đã biết cách tôn lên những nét đẹp trên cơ thể, đó là vòng một đầy đặn và vòng hai thon gọn.', '</p>\r\n<div style="margin: 0in 0in 0pt;" align="center">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham010410_3c856.jpg" />\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham010411_ee629.jpg" /></p>\r\n</div>\r\n<div style="margin: 0in 0in 0pt;" align="center">"Siêu vòng một" Liễu Nham khoe vòng một đầy với váy cổ trễ.\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham01041_e90bc.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham01042_d67fe.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham01043_31667.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham01044_7bd5b.jpg" /></p>\r\n</div>\r\n<div style="margin: 0in 0in 0pt;" align="center">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham01049_d90a9.jpg" /></div>\r\n<div style="margin: 0in 0in 0pt;" align="center">Liễu Nham rất tự tin\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham01045_eea1b.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham01046_229ad.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham01047_c2367.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham01048_758d2.jpg" /></p>\r\n</div>\r\n<div style="margin: 0in 0in 0pt;" align="center">Gương mặt ngọt ngào của người đẹp</div>\r\n<div style="margin: 0in 0in 0pt;" align="center">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham010412_276b6.jpg" /></div>\r\n<div style="margin: 0in 0in 0pt;" align="center">Sau đó, cô thay một chiếc váy khác giúp cô khoe vòng hai thon gọn.\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham010413_89aec.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham010414_0898a.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham010415_57a41.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/04/nham010416_d7797.jpg" /></p>\r\n</div>\r\n<div style="margin: 0in 0in 0pt;" align="center">Liễu Nham là tâm điểm của sự kiện.</div>\r\n<div style="margin: 0in 0in 0pt;" align="center">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="right"><span style="font-weight: bold;">Mi Vân</span></div>', 1, 4, 0, 29, '2012-04-01 06:43:50', 62, '', '2012-04-01 06:45:10', 62, 0, '0000-00-00 00:00:00', '2012-04-01 06:43:50', '0000-00-00 00:00:00', 'nham010411_ee629.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 1, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (85, 'Diện váy hở lưng, Mỹ Tâm e ấp “chiều” Tuấn Hưng', 'dien-vay-ho-lung-my-tam-e-ap-chieu-tuan-hung', '', '<p>Trong chiếc váy hở lưng khá bắt mắt, không chỉ cùng Hiếu Hiền quậy tưng trên sân khấu, “em gái” Mỹ Tâm còn sẵn sàng chiều Tuấn Hưng bằng một ca khúc của cô mà anh ưa thích.', '</p>\r\n<p style="margin: 0in 0in 0pt;">Cùng với các thành viên nhóm Quả dưa hấu, ca sỹ Siu Black hay Khắc Việt, có lẽ Mỹ Tâm chính là vị khách mời đặc biệt trong liveshow Ngày về của Tuấn Hưng.</p>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam31031216_f009a.jpg" /></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Hiếu Hiền và Mỹ Tâm là hai vị khách mời đặc biệt trong chương trình Tuấn Hưng.</span></div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<p style="margin: 0in 0in 0pt;">Không chỉ có thời gian kết hợp trên sân khấu hay làm album ca nhạc mà bộ đôi Tuấn Hưng - Mỹ Tâm cùng tham gia diễn xuất và thể hiện ca khúc trong bộ phim <span style="font-style: italic;">Cho một tình yêu</span> nên chàng ca sỹ đất Hà thành không ngần ngại gọi Mỹ Tâm là “cô em gái”.</p>\r\n<div style="margin: 0in 0in 0pt;">Tuy nhiên cũng theo Tuấn Hưng, dù cô ca sỹ “họa my tóc nâu” này từng hứa như đinh đóng cột “khi nào anh làm liveshow ở Hà Nội, chắc chắn em sẽ tham gia” nhưng phải “mất 3 lần mời mọc”, Tuấn Hưng mới kéo được Mỹ Tâm hát trong liveshow của mình.</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam3103126_6a9ba.jpg" /></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Và Mỹ Tâm sẵn sàng hát tặng Tuấn Hưng bài mà anh yêu thích.</span></div>\r\n<p style="margin: 0in 0in 0pt;">Khác hẳn với vẻ “bặm trợn” và quậy tới bến của Hiếu Hiền, chàng diễn viên cùng tham gia bộ phim Cho một tình yêu, khiến cả hội trường Cung văn hóa hữu nghị Việt – Xô không ngớt tiếng cười, Mỹ Tuân xuất hiện bất ngờ trên sân khấu trong bộ váy hở lưng khá bắt mắt.</p>\r\n<div style="margin: 0in 0in 0pt;">Không những thế, bên cạnh hai ca khúc hát chung với Tuấn Hưng và Hiếu Hiền, để chiều lòng “chủ liveshow”, Mỹ Tâm còn thể hiện thêm bài hát mới nhất của mình có tên <span style="font-style: italic;">Sai</span>, bởi “đây là một yêu cầu của anh Hưng. Nhưng không biết có ý gì không mà anh Hưng thích và muốn nghe hát ca khúc này”.</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;">Clip bộ ba "quậy tưng" trong thể hiện ca khúc Trống vắng</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-weight: bold;">Một số hình ảnh cuộc gặp gỡ "tay ba": Tuấn Hưng - Hiếu Hiền - Mỹ Tâm</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam31031215_be39b.jpg" />\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam31031218_150fb.jpg" /></p>\r\n</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Sự xuất hiện của diễn viên Hiếu Hiền khiến cả hội trường bùng nổ tiếng cười.</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam31031213_5dc0c.jpg" /></span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam31031212_df24f.jpg" /></p>\r\n</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Và quậy tưng trên sân khấu</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam31031211_dcc06.jpg" /></span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam31031214_58bf2.jpg" /></p>\r\n</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam3103125_bf4b9.jpg" /></span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Và Hiếu Hiền rút lui để Tuấn Hưng - Mỹ Tâm trình diễn ngọt ngào trên sân khấu.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam31031210_49e39.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam3103127_02a95.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam3103123_15928.jpg" /></p>\r\n</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Mỹ Tâm dành tặng bài Sai thay cho lời chúc liveshow và sự nghiệp&nbsp;tới "anh trai" Tuấn Hưng.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/y0V2Vnsx49nhQPv7HdH/Image/2012/03/mytam3103124_eb216.jpg" /></p>\r\n</div>\r\n<div style="margin: 0in 0in 0pt;" align="right"><span style="font-weight: bold;">Vũ Phong</span></div>', 1, 4, 0, 29, '2012-04-01 06:45:56', 62, '', '2012-04-01 06:47:32', 62, 0, '0000-00-00 00:00:00', '2012-04-01 06:45:56', '0000-00-00 00:00:00', 'mytam31031216_f009a.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 5, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (86, 'Học trường quốc tế, đuối… tiếng Việt', 'hoc-truong-quoc-te-duoi-tieng-viet', '', '<p>Các chuyên gia về ngôn ngữ đều khuyên rằng nên cho trẻ tiếp cận ngoại ngữ sớm để giúp trẻ dễ dàng phát huy khả năng ngôn ngữ thứ hai của mình. Tuy nhiên, chính điều đó lại khiến không ít học sinh xem nhẹ môn tiếng Việt.', '</p>\r\n<p>Một học sinh lớp 10 có thể nghe, nói, đọc, viết tiếng Anh sành sỏi là niềm tự hào của bố mẹ và nhà trường và chắc chắn tương lai của em cũng mở ra nhiều cơ hội như: tự tin trong giao tiếp với người nước ngoài, khả năng ngoại ngữ cho môi trường du học hoàn hảo, dễ dàng tiếp cận những tiến bộ của thời đại về mọi mặt vì có thể đọc hiểu và nghiên cứu tài liệu tốt… Nhưng nếu em không thể viết lưu loát một bài văn hoặc thậm chí một câu văn tiếng Việt hoàn chỉnh thì sẽ là điều đáng báo động.</p>\r\n<p><span style="font-weight: bold;">Muốn viết lời nhắn cho bố mẹ phải nhờ… Google dịch!</span></p>\r\n<p>Chị Nghiêm Thị Lan (phường 4, quận Tân Bình, TPHCM) rất tự hào về hai cậu con trai. Cả hai đều học trường quốc tế, tiếng Anh nói và viết làu làu. Ngoài ra, các cháu còn tham dự nhiều chương trình ngoại khóa như hướng đạo, âm nhạc… Nói chung là hai cậu con trai phát triển tuyệt vời cả về trí lực, thể lực và hòa mình vào các hoạt động xã hội. Tuy nhiên, trong câu chuyện mới đây với chúng tôi, cả hai vợ chồng chị thoáng lo âu khi kể về… khả năng tiếng Việt của cậu trai cả năm nay học lớp 10. Chị cho biết toàn bộ chuyện ghi chép cháu đều sử dụng tiếng Anh. Do vậy, khi có việc gì phải ghi bằng tiếng Việt như viết vài dòng nhắn lại cho bố mẹ thì cậu tắc tị. Có lần cậu nảy ra sáng kiến lấy máy tính gõ bằng tiếng Anh rồi copy vào Google dịch!</p>\r\n<p>Về khả năng nói thì cậu vẫn giao tiếp bình thường nhưng sử dụng ngôn ngữ không được tinh tế cho lắm. Chị Lan chia sẻ: “Cháu nói tiếng Việt tạm ổn nhưng những điều cơ bản như <span style="font-style: italic;">gà trống</span> hay <span style="font-style: italic;">gà đực</span> cháu không phân biệt được để sử dụng chính xác…”.</p>\r\n<p>Theo chị Lan, ở trường mà con chị đang theo học, tiếng Việt là môn tự chọn. Ý thức được chuyện cần thiết phải luyện tiếng Việt cho con, chị đã đăng ký cho cháu học môn tiếng Việt. Thế nhưng vì môn tiếng Việt của con chị quá yếu nên nó đã kéo điểm trung bình của các môn xuống theo. Con chị đang năn nỉ mẹ xin trường cho thôi học môn tiếng Việt để… bảo toàn thành tích.</p>\r\n<p>Rút kinh nghiệm từ đứa con trai đầu, đến đứa thứ hai chị không cho học trường quốc tế ngay từ lớp 1 mà chị chờ cho đến lớp 4 mới cho vào trường quốc tế. Đúng như tính toán của gia đình, khả năng tiếng Việt của cậu em không đáng lo ngại như đứa anh.</p>\r\n<p>Cũng vì thực trạng này mà một số trường quốc tế hiện nay như Trường Quốc tế Á Châu đã chú trọng giảng dạy bằng tiếng Việt cho các môn học chính và dạy theo đúng chương trình của Bộ GD&amp;ĐT vào một buổi trong ngày, buổi còn lại sẽ học tiếng Anh, dạy bằng tiếng Anh và những ngoại ngữ phụ khác. Tuy nhiên, vẫn không thể phủ nhận rằng môn tiếng Việt tại các trường quốc tế chỉ được xem là ngoại ngữ phụ. Thậm chí học sinh có quyền chọn học tiếng Nhật hoặc tiếng Hoa thay cho tiếng Việt vì các môn này là môn ngoại ngữ tự chọn. Vì thế sẽ không tránh khỏi việc học sinh bỏ môn tiếng Việt nếu cảm thấy lười học hoặc cho rằng không cần thiết học vì các em vốn là người Việt.</p>\r\n<p><span style="font-weight: bold;">Tại sao trẻ không giỏi tiếng mẹ đẻ?</span></p>\r\n<p>Nhà ngôn ngữ học người Mỹ lừng danh Noam Chomsky cho rằng ngôn ngữ con người là một hệ thống phổ quát. Có mấy ngàn ngôn ngữ tự nhiên khác nhau. Con người thừa hưởng một năng lực bẩm sinh về ngôn ngữ được di truyền từ đời này qua đời khác. Trẻ em dùng cái năng lực bẩm sinh ngôn ngữ này để học những ngôn ngữ cụ thể. Ban đầu sẽ học nói, sau đó học viết, học các quy tắc từ vựng, ngữ pháp. Đứa trẻ hình thành một ngôn ngữ qua quá trình tiếp nhận, học tập, sáng tạo nhờ năng lực bẩm sinh ngôn ngữ. Các em bắt chước và lặp lại theo lời người lớn, tự ghép nối những từ rời theo những “quy tắc” các em cảm nhận được khi nghe người chung quanh nói và được điều chỉnh, rút kinh nghiệm theo sự chỉ bảo của người lớn cho đúng với tình huống dùng… Vì thế, một đứa bé dù là người Việt, gia đình người Việt nhưng nếu được tiếp xúc hằng ngày trong môi trường sử dụng ngoại ngữ, bé chắc chắn sẽ giỏi nói ngoại ngữ hơn là tiếng mẹ đẻ.</p>\r\n<p>Điều này đúng với bé trai Anh Nguyên, năm tuổi, học mẫu giáo (quận Bình Tân, TP.HCM). Bé có chị học tại một trường quốc tế. Bé có khả năng đặc biệt về bắt chước ngôn ngữ và trí nhớ nên bé dường như nhớ tất cả lời thoại trong phim hoạt hình. Từ cách suy luận theo diễn biến phim và thuyết minh, bé tự hiểu và nói được tiếng Anh như người lớn. Bất ngờ hơn, khi chị của bé học tiếng Anh tại nhà, bé đều học theo và hiểu theo cách truyền đạt của chị. Sự thú vị khi tiếp nhận một ngôn ngữ mới từ chị và phim ảnh dù vô tình nhưng cũng khiến bé có thói quen nói linh hoạt tiếng Anh nhiều hơn tiếng Việt. Tuy nhiên, điều này phụ huynh của bé lại được cô giáo cảnh báo: “Bé có khả năng sẽ học yếu môn tiếng Việt hơn các bạn cùng tuổi khi vào lớp 1!”.</p>\r\n<p><span style="font-weight: bold;">“Học ngoại ngữ sớm trên nền tảng tiếng Việt vững chắc”</span></p>\r\n<p>GS-TS Ngôn ngữ học Nguyễn Đức Dân, Trường ĐH Khoa học Xã hội và Nhân văn TP.HCM, chia sẻ: “Có những đứa trẻ nói được hai thứ tiếng thành thạo như nhau. Thứ tiếng mà bố mẹ dùng trong gia đình chúng nghe từ bé và thứ tiếng của xã hội mà đứa trẻ học khi tới lớp mẫu giáo và lớn lên trong nhà trường. Vậy thì sau năm năm đầu đời các em đã vô thức tiếp nhận được cơ cấu tiếng Việt, năng lực bẩm sinh ngôn ngữ vẫn được các em dùng để tiếp nhận một ngoại ngữ khác. Nên tận dụng điều này cho trẻ em học ngoại ngữ từ rất sớm, sẽ hiệu quả hơn nhiều so với tới trung học mới học ngoại ngữ. Tuy nhiên, ở độ tuổi tiểu học, học sinh chỉ nên học ngoại ngữ theo cách vừa học vừa chơi, còn nền tảng tiếng Việt nhất định phải được đào tạo và học tập vững chắc…”.</p>\r\n<p>Như thế, nếu phụ huynh muốn con em có một môi trường giáo dục tốt và chất lượng thì việc chọn các trường quốc tế là không sai. Nhưng điều quan trọng là phải biết nên cho trẻ theo học từ khi nào là hợp lý nhằm chuẩn bị cho con em nền tảng tiếng Việt thật tốt. Vì bất cứ nền văn hóa hay môn ngoại ngữ nào khi các em tiếp nhận cũng đều phải cần dựa trên cái nôi ngôn ngữ và văn hóa Việt Nam.</p>\r\n<div align="right">Theo <span style="font-weight: bold;">Thụy Lê</span></div>\r\n<div align="right"><span style="font-style: italic;">Pháp luật TPHCM</span></div>', 1, 2, 0, 7, '2012-04-01 06:48:21', 62, '', '2012-04-01 06:49:30', 62, 0, '0000-00-00 00:00:00', '2012-04-01 06:48:21', '0000-00-00 00:00:00', 'tiengviet12_d38dd.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (87, 'Bạn trẻ rủ nhau học... chữ Nho', 'ban-tre-ru-nhau-hoc-chu-nho', '', '<p>Nằm ở ngoại ô Hà Nội, cách trung tâm thành phố chưa đầy 20km, lớp Hán Nôm Sao Khuê miễn phí của ông Nguyễn Nghiêm Đạt (xóm Đình, xã Sơn Đồng, huyện Hoài Đức) được nhiều bạn trẻ tìm đến để nhập môn.', '</p>\r\n<p>Bạn Nguyễn Thị Thúy (sinh viên năm 2 Trường CĐ Sư phạm Hà Nội) cho biết: “Học chữ Hán Nôm khó nhưng vì yêu thích học chữ và cảm kích trước tấm lòng tận tâm của thầy Vết (tên thường gọi của ông Đạt) nên tôi có thêm động lực để học”.</p>\r\n<div align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/vtfPRccccccccccccodZ/Image/2012/03/Nho_f4841.jpg" _fl="" width="455" align="middle" /></div>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Thầy đồ Vết (<span style="font-style: italic;">thứ hai từ trái qua</span>) trong một lần đưa học trò đi xem triển lãm thư pháp. (Ảnh: Văn Tuấn)</span></div>\r\n<p>Chẳng ngại nắng mưa hay mùa đông lạnh rét, cứ đều đặn sáng chủ nhật hằng tuần Thúy lại tranh thủ đạp xe đến lớp Sao Khuê để học. Thúy còn rủ thêm bạn bè cùng trường đi học chữ Hán Nôm.</p>\r\n<p>Bạn Nguyễn Trung Hiếu (sinh viên năm 1 Học viện Kỹ thuật quân sự) là một trong những môn sinh lâu năm của lớp học Sao Khuê. Khi Hiếu mới 12, 13 tuổi đã được bố mẹ đưa đến lớp Hán Nôm của thầy giáo Vết, đến nay Hiếu đã có thể viết thư pháp thành thạo chẳng kém gì một ông đồ thực thụ.</p>\r\n<p>Hiếu chia sẻ: “Ban đầu đến với môn học chỉ nghĩ rằng học cho biết thôi, nhưng càng về sau càng thấy đam mê với môn học nghệ thuật này. Trên lớp thầy không chỉ luyện chữ mà còn giảng đạo làm người nên mình cũng vỡ ra được nhiều điều. Nhờ vậy mà bản thân mình sống và suy nghĩ chín chắn hơn”.</p>\r\n<p>Với quan điểm học để thành nhân và ai cũng là thầy để cho ta học hỏi, thế nên ông đồ Vết không chỉ rèn chữ mà còn đề cao việc rèn nhân cách cho học trò. Đến nay, lớp học Hán Nôm Sao Khuê đã có hơn 400 môn sinh tốt nghiệp. Học trò muốn theo học phải làm lễ nhập môn, có cha mẹ đưa đến và mang theo lá trầu quả cau với thẻ hương.</p>\r\n<p>Ông Vết giải thích làm như vậy để học trò hiểu được truyền thống tôn sư trọng đạo. Học trò đi học phải chuẩn bị đầy đủ nghiên, bút, mực tàu. Thầy dạy trò cách mài mực và học viết bút lông như học trò ngày xưa, có như vậy trò mới rèn được tính kiên trì.</p>\r\n<p>Bạn Nguyễn Nhật Anh (sinh viên năm 2 ĐH Hà Nội) là một trong những học trò xuất sắc của lớp học Sao Khuê tâm sự: “Thầy dạy cho chúng mình biết được nhiều chữ đồng thời hiểu hết nghĩa của chữ. Thầy rất chú trọng đến nhân - nghĩa - lễ - trí - tín, những nề nếp gia phong hay đạo lý làm người, cách ứng xử giao tiếp với người trên kẻ dưới. Chính vì vậy mà từ khi học thầy, những người trẻ như mình sống tốt hơn với ông bà, cha mẹ và mọi người xung quanh”.</p>\r\n<p>Ngoài những giờ học trên lớp, vào những dịp hội hè hay dịp tết, thầy giáo Vết còn tổ chức cho học trò đi viết chữ tặng mọi người. Bạn Nguyễn Thị Phương Anh (sinh viên năm 2 Trường ĐH Quốc gia Hà Nội) cười hóm hỉnh khoe: “Thầy khen mình viết tiến bộ hơn rất nhiều. Mình sẽ cố gắng luyện tập chăm chỉ để cùng thầy đi viết chữ tặng mọi người. Tết năm sau mình có thể tự tin viết chữ tặng ông bà, bố mẹ mình”.</p>\r\n<div align="right">Theo <span style="font-weight: bold;">Xuân Dịu</span></div>\r\n<div align="right"><span style="font-style: italic;">Tuổi Trẻ</span></div>', 1, 2, 0, 7, '2012-04-01 06:49:59', 62, '', '2012-04-01 06:50:47', 62, 0, '0000-00-00 00:00:00', '2012-04-01 06:49:59', '0000-00-00 00:00:00', 'nho_f4841.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (88, 'Cô học trò nghèo và phần mềm “English for kid”', 'co-hoc-tro-ngheo-va-phan-mem-english-for-kid', '', '<p>Trước thực trạng nhiều bạn bè cùng trang lứa rất ngại học tiếng Anh, cô bạn Lê Tiến Minh Châu (SN 1999) - học sinh lớp 7/4 Trường THCS Trưng Vương, TP Đà Nẵng đã tự mày mò nghiên cứu cho ra đời phần mềm “English for kid” (Tiếng Anh cho trẻ em).', '</p>\r\n<p>Đây là phần mềm giúp cho học sinh đang bắt đầu học tiếng Anh phát triển kỹ năng nghe, nói, đọc viêt và rèn luyện ngữ pháp thông qua các trò chơi, tập hát, đọc truyện… Phần mềm này đã đoạt giải nhì trong cuộc thi sáng tạo thanh, thiếu niên, nhi đồng toàn quốc lần thứ 7 năm 2011.&nbsp;</p>\r\n<p><span style="font-weight: bold;">Sáng tạo để phục vụ học tập</span></p>\r\n<p>Minh Châu sinh ra trong một gia đình có 4 anh em, gia đình có hoàn cảnh kinh tế khó khăn. Mẹ em&nbsp;bán nước ở công viên, bố làm nghề dạy lái xe. Cả gia đình sinh sống trong căn nhà nhỏ chưa đầy 25m2 nhưng Minh Châu chưa bao giờ lấy những khó khăn, thiếu thốn để lơ là việc học. Suốt 7 năm học qua, Minh Châu luôn giữ vững thành tích học sinh giỏi xuất sắc, không những thế em còn rất đam mê tin học và sáng tạo ra nhiều phần mềm hữu ích.</p>\r\n<p>Nhận thấy từ nhu cầu cuộc sống là tiêu chí đầu tiên trong sáng tạo phần mềm của Minh Châu. Trong một lần đang trong giờ học tiếng Anh, cô giáo kiểm tra từ mới và gọi người bạn thân của Châu lên bảng. Do không thích học tiếng Anh nên cô bạn của Châu không viết được từ nào vì vậy bị cô giáo cho nợ điểm. Khi Châu hỏi tại sao không học bài cũ đi, cô bạn của Châu thẳng thừng: “Khó thế này ai học cho nổi, nhìn là hoa cả mắt sao nhớ được chữ gì”. Từ nhiều lần nhận thấy bạn bè rất ngại học tiếng Anh, Minh Châu đã nghĩ đến việc phải tìm cách để giúp các bạn thích học tiếng Anh và học thật giỏi môn học này.</p>\r\n<p>Nghĩ là làm, Châu bắt đầu mày mò lao vào nghiên cứu. Dù chưa được học qua lớp Tin học nào nhưng tận dụng chiếc máy tính để bàn cũ rích của 2 người anh trai, Châu đã nhiều đêm thức trắng để viết phần mềm. Ban đầu gặp rất nhiều khó khăn vì em chỉ có được chút ít kiến thức đọc lén được trong những lần đi đọc sách ở hiệu sách cũ. May mà Châu có 2 anh trai là Lê Hoàng Anh và Lê Anh Tiến - tác giả của nhiều phần mềm sáng tạo đã nhận được rất nhiều giải thưởng sáng tạo cao ở trong nước và quốc tế. Trong thời gian sáng tạo phần mềm này, Châu được hai anh trai hướng dẫn việc cắt, gọt hình ảnh, phần&nbsp;còn lại cô bé tự mày mò viết và hoàn thiện phần mềm.</p>\r\n<p>Sau một thời gian tự mày mò và nghiên cứu, Châu đã cho ra đời phần mềm phần mềm “English for kid”. Với phần mềm này, người dùng có thể luyện kỹ năng nghe thông qua bài hát, làm bài tập thông qua các video flash vui nhộn, rèn luyện khả năng từ vựng… có thể giúp người học tiếng Anh dễ dàng học mà không bị nhàm chán. Đây là phần mềm dành cho các bạn bắt đầu học tiếng Anh nhằm phát triển kỹ năng nghe, nói, đọc, viết, rèn luyện ngữ pháp… Đồng thời phần mềm “English for kid” còn được các thầy cô sử dụng như một công cụ phục vụ giảng dạy.</p>\r\n<div align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/vtfPRccccccccccccodZ/Image/2012/03/hinh-2_0015c.jpg" _fl="" width="455" align="middle" /></div>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Minh Châu tranh thủ lúc rảnh rỗi để sáng tạo các phần mềm hữu ích.</span></div>\r\n<p><span style="font-weight: bold;">“Quan trọng nhất là đam mê và biết cố gắng”</span></p>\r\n<p>Với những ứng dụng thiết thực của phần mềm này, Minh Châu đã nhận được sự ủng hộ nhiệt tình từ bạn bè và thầy cô giáo, từ khi sáng tạo ra phần mềm rất nhiều công ty phần mềm đã tìm đến mua phần mềm. Tuy nhiên, Châu cho biết em sẵn sàng chia sẻ phần mềm cho những ai có như cầu sử dụng phần mềm. Với Châu, đơn giản sáng tạo ra là vì đam mê, vì thấy nhu cầu thiết thực của cuộc sống và chia sẻ miễn phí cho bạn bè miễn sao các bạn thích học tiếng Anh và học thật giỏi môn học này. Sắp tới, em sẽ đưa, thêm chương trình tiếng Anh lớp 8, 9 vào phần mềm.</p>\r\n<p>Được biết, Dù chưa được học và đào tạo bài bản về Tin học nhưng ngày từ năm học lớp 5, Châu đã đoạt giải nhất Tin học trẻ cấp thành phố, giải khuyến khích tin học trẻ cấp quốc gia. Năm lớp 6, em giành giải ba cuộc thi sáng tạo thanh thiếu niên, nhi đồng toàn quốc lần thứ 6 với phần mềm “Vui học cùng chữ cái”. Mới đây, năm 2011, Châu đoạt giải nhì cuộc thi sáng tạo thanh, thiếu niên, nhi đồng toàn quốc lần thứ 7 với phần mềm “English for kid”.</p>\r\n<p>Khi hỏi về kinh nghiệm học tập và sáng tạo phần mềm, Châu chia sẻ: “Quan trọng nhất là đam mê và biết cố gắng. Để làm được phần mềm này, điều quan trọng nhất với em đó là sự tự tin vì em đã nắm bắt được như cầu của cuộc sống vì vậy khi bỏ công sức vào sáng tạo thì chắc chắn khi thành công sản phẩm của mình sẽ được đón nhận. Còn trong học tập, quan trọng nhất là biết cố gắng và nắm bắt ngay bài học thầy cô giảng dạy trên lớp và đọc sách tham khảo để nắm vững thêm kiếm thức”.</p>\r\n<p>Được biết, Minh Châu là một trong 4 gương mặt của Đà Nẵng sẽ tham gia cuộc thi “Tài năng khoa học trẻ thế giới” ở Thái Lan vào cuối tháng 6/2012. Trao đổi với chúng tôi, cô Nguyễn Thị Tình - giáo viên chủ nhiệm lớp 7/4 cho biết: “Minh Châu là một học sinh nghèo vượt khó học giỏi, ngoan, hiền, 7 năm liền em là học sinh giỏi xuất sắc".</p>\r\n<p align="right"><span style="font-weight: bold;">Đỗ Luyến</span></p>', 1, 2, 0, 7, '2012-04-01 06:51:10', 62, '', '2012-04-01 06:52:11', 62, 0, '0000-00-00 00:00:00', '2012-04-01 06:51:10', '0000-00-00 00:00:00', 'hinh-2_0015c.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 1, '', '', 0, 1, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (89, 'Apple trả 15 USD cho người dùng iPhone 4 lỗi sóng', 'apple-tra-15-usd-cho-nguoi-dung-iphone-4-loi-song', '', '<p>Thỏa thuận được thực hiện nếu người dùng không thể trả lại sản phẩm, hoặc không đồng ý sử dụng lớp vỏ do Apple tặng.', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Apple đã cố gắng dàn xếp vụ việc từ thời Steve Jobs còn tại vị. Ảnh: David Paul Morris." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/FE/iphone4.jpg" border="1" height="334" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Apple đã cố gắng dàn xếp vụ việc từ thời Steve Jobs còn tại vị. Ảnh: <em>David Paul Morris.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Vụ việc liên quan đến lỗi bắt sóng trên mẫu iPhone 4 của Apple cuối cùng cũng đi đến hồi kết khi hãng đồng ý trả 15 USD cho mỗi khách hàng gặp phải rắc rối do ăng-ten của thiết bị gây ra.</p>\r\n<p class="Normal">Các điều khoản thỏa thuận được công bố hôm qua, cùng với đó là một website phục vụ cho quá trình đền bù. Mỗi khách hàng sở hữu iPhone 4 gặp trục trặc về sóng sẽ được hoàn lại 15 USD bằng tiền mặt, trong trường hợp họ không thể trả lại sản phẩm (do phải chịu phí hao tổn) hoặc không chấp nhận sử dụng lớp vỏ bảo vệ để tránh mất sóng.</p>\r\n<p class="Normal">Tháng 7/2010, Apple quyết định tặng miễn phí loại case bọc iPhone 4 cho người dùng, sau khi có nhiều người phản ánh lỗi mất sóng điện thoại khi cầm vào một số vị trí nhất định. Trước đó vài ngày, hồ sơ khởi kiện sự vụ này đã được chuẩn bị sẵn sàng.</p>\r\n<p class="Normal">Để nhận được khoản tiền trên, người dùng cần điền đầy đủ thông tin vào một phiếu trực tuyến, trước ngày 28/8 năm nay. Những ai đang dùng iPhone 4 cũng sẽ nhận được một email thông báo về vụ dàn xếp này từ Apple trước ngày 30/4 này.</p>\r\n<p class="Normal" align="right"><strong>Anh Quân</strong></p>', 1, 6, 0, 22, '2012-04-01 06:54:42', 62, '', '2012-04-01 06:55:33', 62, 0, '0000-00-00 00:00:00', '2012-04-01 06:54:42', '0000-00-00 00:00:00', 'iphone4.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 7, '', '', 0, 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (90, 'Samsung M i9300 trang bị nhiều cảm biến mới', 'samsung-m-i9300-trang-bi-nhieu-cam-bien-moi', '', '<p>Máy sẽ trang bị các tính năng thể thao như đo nhịp tim, cùng với hàng loạt cảm biến nhận biết mới, tối ưu tiện lợi cho người dùng.', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Samsung M có khả năng thanh toán bằng thẻ Visa thông qua NFC. Ảnh: Pocket-lint." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/07/samsung-olympic-phone.jpg" border="1" height="333" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Samsung M có khả năng thanh toán bằng thẻ Visa thông qua NFC. Ảnh: Pocket-lint.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo <em>Phone Arena</em>, mẫu smartphone Samsung GT-i9300 mới lộ diện gần đây có thể mang tên chính thức Samsung M, và không thuộc trong dòng Galaxy của hãng.</p>\r\n<p class="Normal">Trang tin cũng cho hay, máy có thể ra mắt vào tháng 5 năm nay, trước khi khai mạc sự kiện Thế vận hội Mùa Hè tại London không lâu. Samsung là một trong những đơn vị tài trợ chính ở đây, và trong kế hoạch của hãng có phần liên quan đến phương pháp thanh toán di động. Thông tin này xuất hiện trong bài thuyết trình liên quan đến phương thức thanh toán tại Thế vận hội năm nay, thông qua thẻ Visa. Nhiều khả năng smartphone mới của Samsung sẽ có khả năng thanh toán trên.</p>\r\n<p class="Normal">Samsung M có thể mang nhiều tính năng cảm biến mới, dành riêng cho các hoạt động thể thao, bao gồm cả đo nhịp tim và nhịp sinh học. Ngoài ra, thiết bị còn được trang bị công nghệ "mắt theo dấu", cho phép giữ màn hình sáng liên tục khi máy "biết" người dùng đang...nhìn vào, cảm biến gia tốc mới có khả năng nhận diện khi người dùng nằm và giữ chế độ tự động xoay màn hình ở tình trạng phù hợp với tư thế của người dùng.</p>\r\n<p class="Normal">Các tính năng môi trường cũng được tính hợp, ví dụ cảm biến trạng thái sẽ "nhận thức" được khi người dùng muốn gọi điện cho số vừa gửi tin nhắn đến, và tự động quay số ngay khi điện thoại được đặt lên gần tai. Hay như khi chụp ảnh, máy sẽ tự động gắn thông tin những người có mặt, sau đó đề nghị gửi một email đến số người trên.</p>\r\n<p class="Normal" align="right"><strong>Nguyễn Phương</strong></p>', 1, 6, 0, 22, '2012-04-01 06:58:17', 62, '', '2012-04-01 06:59:08', 62, 0, '0000-00-00 00:00:00', '2012-04-01 06:58:17', '0000-00-00 00:00:00', 'samsung-olympic-phone.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 6, '', '', 0, 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (91, 'Tháng 4, Sony bắt đầu nâng cấp Android 4.0', 'thang-4-sony-bat-dau-nang-cap-android-40', '', '<p>Arc S, Neo V và Ray sẽ được cập nhật ngay từ giữa tháng 4 trong khi các model Xperia còn lại phải chờ đến cuối tháng 5 hoặc đầu tháng 6.', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Các dòng máy Xperia của Sony sẽ lên Android 4.0 từ tháng sau. Ảnh: GSM Arena." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/12/Sony.jpg" border="1" height="215" width="500" align="middle" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Các dòng máy Xperia của Sony sẽ lên Android 4.0 từ tháng sau. Ảnh: <em>GSM Arena.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Trước đó hãng điện thoại của Nhật từng đưa ra lời hứa hẹn sẽ bắt đầu nâng cấp smartphone Xperia từ tháng 3. Nhưng việc các bản ROM nâng cấp chưa thực sự hoàn thiện khiến hãng trì hoãn phát hành bản cập nhật cho các model của mình. Tuy nhiên theo một thông tin mới được đưa ra từ <em>Android Community</em>, các dòng máy gồm Xperia Arc S, Neo V và Ray sẽ chính thức lên Android 4.0 ngay trong tháng sau.</p>\r\n<p class="Normal">Trong khi đó các model như Xperia Arc, Xperia Play, Neo, Xperia Mini, Mini Pro, Xperia Pro, Active và Sony Ericsson Live with Walkman sẽ phải chờ đến cuối tháng 5 hoặc đầu tháng 6 mới chính thức sở hữu Ice Cream Sandwich.</p>\r\n<p class="Normal">Một điểm lưu ý, trong lần cập nhật này các smartphone của Sony sẽ không thể sử dụng phương thức cập nhật không dây OTA ngay trên điện thoại. Thay vào đó người dùng phải sử dụng các ứng dụng PC Companion hay Bridge trên MAC để tiến hành nâng cấp điện thoại lên phiên bản mới.</p>\r\n<p class="Normal" align="right"><strong>Thiên Minh</strong></p>', 1, 6, 0, 22, '2012-04-01 06:59:27', 62, '', '2012-04-01 07:00:22', 62, 0, '0000-00-00 00:00:00', '2012-04-01 06:59:27', '0000-00-00 00:00:00', 'sony.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 5, '', '', 0, 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (92, '10 điện thoại đi vào lịch sử Nokia', '10-dien-thoai-di-vao-lich-su-nokia', '', '<p>Mỗi sản phẩm đều đánh dấu một bước phát triển riêng của Nokia, góp phần trong thành công thương hiệu của hãng.', '</p>\r\n<p class="Normal">Nokia gần đây khá "ồn ào" với loạt sản phẩm dòng Lumia sử dụng Windows Phone, và cái tên mới nhất là model Lumia 900, smartphone thuộc Top của hãng hiện nay. Những kỳ vọng về "sự trở về của nhà vua" ngày càng lớn hơn, dù cuộc cạnh tranh phía trước ngày càng khốc liệt.<br /> <br />Thương hiệu điện thoại Nokia được gây dựng bởi một chuỗi các sản phẩm không ngừng gây ấn tượng với người dùng. Tuy không phải thiết bị nào cũng thành công, nhưng không thể phủ nhận sức ảnh hưởng của các model này.</p>\r\n<p class="Normal">Dưới đây là danh sách 10 mẫu điện thoại đánh dấu các bước phát triển của Nokia do trang <em>Phone Arena</em> bình chọn.</p>\r\n<p class="SubTitle">Nokia 5110/5165</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/14/nokia5110.jpg" border="1" height="300" width="480" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Thiết bị ra mắt vào cuối những năm 1990, đầu những năm 2000 của thế kỷ 21. Với tiêu chuẩn mẫu mã như ngày nay, Nokia 5110/5165 chắc chắn sẽ giống loài...khủng long hơn là một thiết bị di động, nhưng đây lại được xem như mẫu điện thoại nổi tiếng nhất của hãng. Khả năng thay đổi vỏ khiến người dùng bớt nỗi lo "đụng hàng".</p>\r\n<p class="SubTitle">Nokia 3600</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/14/nokia3600.jpg" border="1" height="338" width="450" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Mẫu điện thoại sở hữu nhiều danh hiệu đầu tiên của Nokia: smartphone S60 đầu tiên có mặt tại thị trường Bắc Mỹ, điện thoại dầu tiên tích hợp camera, điện thoại đầu tiên hướng đến người ưa nhắn tin nhờ vào thiết kế bàn phím dạng vòng tròn... Máy trở thành một trong những hiện tượng khi ra mắt và được ghi dấu trong dịch sử của các dòng điện thoại.</p>\r\n<p class="SubTitle">Nokia 6800</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/14/nokia6820.jpg" border="1" height="342" width="480" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">"Transformer" của điện thoại, hay "tắc kè hoa" là từ mà nhiều người đã dùng để gọi tên Nokia 6800. Trước 6800, Nokia sử dụng hoàn toàn thiết kế dạng thanh đơn thuần cho các sản phẩm của mình, nhưng với mẫu điện thoại này, máy trở nên khác biệt hơn với khả năng tách và gập thân, tạo thành bộ phím mới. Màn hình và bàn phím QWERTY của máy khi biến đổi hướng đến người thích nhắn tin và xem đây như phương thức liên lạc chính.</p>\r\n<p class="SubTitle">Nokia 6620/6600</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/14/nokia6600.jpg" border="1" height="353" width="480" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Trước khi N95 ra đời, Nokia 6600 hay còn biết đến với cái tên Nokia 6620 tại Bắc Mỹ, mới là sản phẩm được chú ý nhiều. Tại thời điểm ra mắt, mẫu smartphone hướng đến giới doanh nhân được xem như sản phẩm tiện dụng nhất từng ra đời của Nokia. Máy có thiết kế dạng thanh nhưng "béo" phần giữa, được trang bị những tính năng đáng giá thời bấy giờ như hệ điều hành S60, camera VGA, bộ nhớ có thể mở rộng, tính năng chơi nhạc/video và không thể nhắc đến Bluetooth.</p>\r\n<p><a href="http://sohoa.vnexpress.net/SH/Dien-thoai/2005/05/3B9AD26C/Nokia-N90---si234u-di-2737897ng-ghi-h236nh" class="SubTitle"><span style="color: #0000ff;" color="#0000ff">Nokia N90</span></a></p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/14/nokian90.jpg" border="1" height="541" width="480" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Không cần phải bàn cãi nhiều vẫn có thể khẳng định rằng Nokia N90 là sản phẩm đáng giá và không thể quên của gã khổng lồ Phần Lan. Thiết bị có thiết kế camera đặc biệt, khả năng xoay lật màn hình 270 độ khiến model trông giống với mẫu máy quay phim hơn là một chiếc điện thoại. Không chỉ giống, mà N90 còn thực sự là smartphone có khả năng quay phim của Nokia. Đây cũng là một trong những smartphone đầu tiên dòng N, chuyên giải trí đa phương tiện của hãng.</p>\r\n<p><a href="http://sohoa.vnexpress.net/SH/Dien-thoai/2007/11/3B9AEE90/Nokia-5310---nghe-nh7841c-ki7875u-th7875-thao" class="SubTitle"><span style="color: #0000ff;" color="#0000ff">Nokia 5310 XpressMusic</span></a></p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/14/Nokia-5310-1.jpg" border="1" height="321" width="500" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nokia tạo ấn tượng tốt khi kết hợp thêm tính năng giải trí vào thiết bị nhỏ nhắn thuộc phân khúc phổ thông, trong khi không hề tăng giá. Máy có thiết kế mỏng manh, thân dạng thanh truyền thống chắc chắn và khả năng chơi nhạc MP3 nhận rất nhiều lời khen. Bộ phím chơi nhạc tiện dụng đặt ngay cạnh thân máy, cho phép người dùng thưởng thức âm nhạc bất kỳ nơi đâu mà không phải thao tác quá nhiều.</p>\r\n<p><a href="http://sohoa.vnexpress.net/SH/Dien-thoai/2007/04/3B9AEAB0/C7843m-nh7853n-v7873-Nokia-N95" class="SubTitle"><span style="color: #0000ff;" color="#0000ff">Nokia N95</span></a></p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/14/Nokian95.jpg" border="1" height="333" width="500" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">N95 là một trong smartphone được biết đến và lựa chọn nhiều nhất lịch sử thiết bị của Nokia. Tại thời điểm được giới thiệu năm 2004, cho đến khi bán ra chính thức vào năm 2007, Nokia N95 trở thành chủ đề "hot" tại rất nhiều diễn đàn công nghệ. Những tính năng khủng ngày ấy đều được tích hợp trên máy như định vị GPS, khả năng điều hướng, quay video VGA, màn hình lớn, cảm biến gia tốc,...</p>\r\n<p><a href="http://sohoa.vnexpress.net/SH/Dien-thoai/2008/07/3B9AF48F/Nokia-E71---b7843n-n226ng-c7845p-c7911a-E61i" class="SubTitle"><span style="color: #0000ff;" color="#0000ff">Nokia E71</span></a></p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Ảnh: Cnmo." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/14/nokia-e71-white04.jpg" border="1" height="375" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ảnh: Cnmo.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">RIM từng thống trị dòng điện thoại với bàn phím QWERTY, nhưng Nokia đã chứng tỏ mình không phải là "tay mơ" khi ra mắt mẫu điện thoại doanh nhân E71. Cho đến tận bây giờ, E71 vẫn là lựa chọn của nhiều người ưa chuộng điện thoại Nokia, với vinh dự trở thành điện thoại phím QWERTY đáng nhớ nhất mà hãng từng sản xuất. Không chỉ có thiết kế bàn phím hướng đến người dùng doanh nhân, cách Nokia tạo hình cho máy trông rất chắc chắn.</p>\r\n<p><a href="http://sohoa.vnexpress.net/SH/Dien-thoai/2008/10/3B9AF805/Hay---d7903-v7899i-Nokia-5800-XpressMusic" class="SubTitle"><span style="color: #0000ff;" color="#0000ff">Nokia 5800 XpressMusic</span></a></p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/14/nokia-5800.jpg" border="1" height="320" width="480" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Từ khi bắt đầu sản xuất điện thoại cho mãi đến đầu năm 2008, Nokia chưa từng có model nào tích hợp màn hình cảm ứng. Và Nokia 5800 XpressMusic được ghi vào lịch sử khi trở thành smartphone đầu tiên của hãng sở hữu khả năng chạm. Tuy nhiên, thay vì sử dụng màn hình cảm ứng điện dung đang ngày càng phổ biến lúc bấy giờ, Nokia lại quyết định chọn màn hình cảm ứng điện trở, đồng nghĩa với chiếc bút stylus kèm theo.</p>\r\n<p><a href="http://sohoa.vnexpress.net/sh/dien-thoai/smartphone/2012/03/3b9b5cd9/Nokia-N9-sap-co-dan-em-chay-MeeGo" class="SubTitle"><span style="color: #0000ff;" color="#0000ff">Nokia N9</span></a></p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Ảnh: Nokia." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/14/nokian9.jpg" border="1" height="316" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ảnh: Nokia.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Smartphone không có tuổi đời như các mẫu điện thoại trên, trẻ trung nhưng N9 hoàn toàn khác biệt. N9 là smartphone đầu tiên trên thế giới không có phím bấm trên màn hình, mọi thao tác thực hiện dựa hết vào màn cảm ứng của máy. Thân máy được chế tạo từ vật liệu polycarbonate và làm nguyên khối. Thật không khó để nhận ra rằng, kể cả Lumia 800 hay Lumia 900 đều mang phong cách thiết kế thân của N9.</p>\r\n<p class="Normal" align="right"><strong>Anh Quân</strong></p>', 1, 6, 0, 22, '2012-04-01 07:00:45', 62, '', '2012-04-01 07:01:52', 62, 0, '0000-00-00 00:00:00', '2012-04-01 07:00:45', '0000-00-00 00:00:00', 'nokia3600.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 4, '', '', 0, 2, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (93, 'Microsoft mở cửa Marketplace thêm 13 quốc gia', 'microsoft-mo-cua-marketplace-them-13-quoc-gia', '', '<p>Windows Phone của Microsoft đang dần mở rộng thị trường của mình, hiện có mặt chính thức tại 54 quốc gia và vùng lãnh thổ khác nhau.', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Đợt mở rộng thị trường diễn ra vào cuối năm nay của Windows Phone sẽ thêm Việt Nam vào danh sách. Ảnh: Wpsuperfanboy." src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/FB/wp-apps.jpg" border="1" height="301" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đợt mở rộng thị trường diễn ra vào cuối năm nay của Windows Phone sẽ thêm Việt Nam vào danh sách. Ảnh: <em>Wpsuperfanboy.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Microsoft cho hay hãng vừa mở quyền truy cập chợ ứng dụng Windows Phone Marketplace cho thiết bị tại 13 quốc gia và vùng lãnh thổ nữa, bên cạnh những khu vực hiện nay. Như vậy, Microsoft đã thực hiện được lời hứa đưa ra hồi cuối tháng 2 tại sự kiện MWC, thêm Bulgaria, Costa Rica, Croatia, Estonia, Iceland, Latvia, Lithuania, Romania, Slovakia, Slovenia, Thổ Nhĩ Kỳ, Ukraine và Venezuela vào danh sách hỗ trợ chính thức của Windows Phone Marketplace.</p>\r\n<p class="Normal">Các quốc gia gồm Argentina, Trung Quốc, Indonesia, Malaysia, Peru và Philippines đã được hỗ trợ từ đầu năm nay, nâng tổng số thị trường mới của Marketplace trong năm 2012 lên 19. Đến lúc này, Microsoft đã hỗ trợ chợ ứng dụng dành cho nền tảng di động Windows Phone tại 54 quốc gia và vùng lãnh thổ trên thế giới.</p>\r\n<p class="Normal">Đợt mở rộng thị phần lần tới sẽ diễn ra vào cuối năm nay, bao gồm Các tiểu vương quốc Ả-rập, Bahrain, Iraq, Saudi Arabia, Qatar, Kazakhstan, Israel, Thái Lan và Việt Nam. Hiện chưa có khung thời gian xác định cho kế hoạch này.</p>\r\n<p class="Normal">Microsoft đang nhanh chóng mở rộng thị trường cho nền tảng của mình, mặc dù hiện tại hệ điều hành Windows Phone vẫn chưa gây được nhiều dấu ấn. Nhiều người đặt kỳ vọng vào bản cập nhật Apollo, xem đây là nhân tố giúp nền tảng của Microsoft gia nhập "cuộc đua tam mã", cùng với iOS và Android.</p>\r\n<p class="Normal" align="right"><strong>Anh Quân</strong></p>', 1, 6, 0, 22, '2012-04-01 07:02:49', 62, '', '2012-04-01 07:03:42', 62, 0, '0000-00-00 00:00:00', '2012-04-01 07:02:49', '0000-00-00 00:00:00', 'wp-apps.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (94, 'Huawei đặt mục tiêu bán 60 triệu smartphone năm 2012', 'huawei-dat-muc-tieu-ban-60-trieu-smartphone-nam-2012', '', '<p>Hai "chủ lực" của hãng sẽ là model Ascend D quad và D quad XL, với quyết tâm trở thành một trong những nhà sản xuất điện thoại hàng đầu thế giới.', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="Chủ tịch Huawei, ông Richard Yu đặt nhiều hy vọng vào các sản phẩm " src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5D/09/yu.jpg" border="1" height="286" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chủ tịch Huawei, ông Richard Yu đặt nhiều hy vọng vào các sản phẩm "đỉnh" của hãng hiện nay. Ảnh: <em>Technobuffalo.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Huawei vừa công bố kế hoạch đầy tham vọng của mình với dự kiến 60 triệu smartphone xuất xưởng năm 2012, nhờ bước tiến sâu hơn vào sản xuất điện thoại cao cấp.</p>\r\n<p class="Normal">Chủ tịch Richard Yu của Huawei nhấn mạnh ngành công nghiệp smartphone đang đứng trước cơ hội chuyển mình mạnh mẽ, và chỉ khoảng 3 hay 4 công ty có thể tồn tại được. Ông cho hay giờ Apple đang năm 70% lợi nhuận toàn ngành, trong khi 20% thì trong tay Samsung, số còn lại không đủ để tất cả các nhà sản xuất chia nhau.</p>\r\n<p class="Normal">Trong khi doanh số là ưu tiên hàng đầu để làm nên thương hiệu Huawei trong ngành di động, thì quy mô cũng không thể xem nhẹ. Ông Yu nhận định, để công ty tồn tại nhất định phải có quy mô, đó là lý do vì sao Huawei đặt mục tiêu 60 triệu máy bán ra trong năm nay. Trả lời câu hỏi về "mảnh đất" để cho hai nhà sản xuất điện thoại lớn nhất của Trung Quốc hiện nay (Huawei và ZTE) tồn tại, lãnh đạo của Huawei cho rằng chất lượng sản phẩm sẽ quyết định ai là người chiến thắng trong cuộc chiến smartphone. Huawei cũng đặt mục tiêu doanh thu 100 tỷ mỗi năm.</p>\r\n<p class="Normal">Năm 2012, mũi nhọn trong bước tiến của Huawei sẽ là hai sản phẩm Ascend D quad lõi tứ và <a href="http://sohoa.vnexpress.net/sh/dien-thoai/ung-dung/2012/03/3b9b5c0d/Ascend-D-quad-XL-nhanh-nhat-the-gioi" class="Normal"><span style="color: #0000ff;" color="#0000ff">D quad XL</span></a> (cùng cấu hình, pin lớn hơn). Cả hai thiết bị đều sử dụng bộ xử lý 4 nhân K3V2 do chính Huawei sản xuất, hướng đến danh hiệu <a href="http://sohoa.vnexpress.net/sh/dien-thoai/smartphone/2012/02/3b9b59df/Huawei-ra-di-dong-nhanh-nhat-the-gioi" class="Normal"><span style="color: #0000ff;" color="#0000ff">smartphone Android nhanh nhất</span></a>. Máy dự kiến bắt đầu bán ra từ tháng 7 năm nay, thời điểm khá muộn cho mục tiêu to lớn của Huawei.</p>\r\n<p class="Normal" align="right"><strong>Anh Quân</strong></p>', 1, 6, 0, 22, '2012-04-01 07:04:07', 62, '', '2012-04-01 07:04:49', 62, 0, '0000-00-00 00:00:00', '2012-04-01 07:04:07', '0000-00-00 00:00:00', 'yu.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (95, 'Đánh giá iPad thế hệ 3', 'danh-gia-ipad-the-he-3', '', '<p>iPad 2012 gây ấn tượng mạnh với màn hình mịn, sắc nét và màu sắc hiển thị tốt nhưng sạc mất nhiều thời gian cũng như hơi nóng ở mặt sau. ', '</p>\r\n<p class="Normal">Apple không nổi tiếng với quá nhiều nâng cấp trong mỗi sản phẩm mới nhưng các tính năng lần đầu tiên xuất hiện đều được hãng khá trau chuốt trước khi đến tay người dùng. iPad phiên bản 2012 mà "Quả táo" giới thiệu hôm 8/3 vừa qua cũng không phải ngoại lệ. Sản phẩm này chỉ thay đổi một chút ở kiểu dáng nhưng gây ấn tượng mạnh với màn hình độ phân giải siêu cao Retina từng gây "sốt" giống như khi iPhone 4 mới xuất hiện. Ngoài ra, khả năng nhập liệu giọng nói bằng Siri, kết nối mạng 4G cũng như camera độ phân giải cao cũng là những nâng cấp đáng kể khác.</p>\r\n<table style="width: 77%;" border="0" cellpadding="3" cellspacing="2" align="center">\r\n<tbody>\r\n<tr>\r\n<td class="Image"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/5C/E1/IMG_9295.jpg" border="1" height="366" width="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">iPad thế hệ 3. Ảnh: <em>Tuấn Hưng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">iPad mới có độ dày 9,4 mm, dày hơn so với iPad 2 là 8,8 mm để dành không gian cho bộ pin có dung lượng gần gấp đôi so với iPad 2, 42,5 watt một giờ so với 25 watt một giờ . Máy nặng 652 gram cho bản Wi-Fi và 662 gram cho bản 4G.</p>\r\n<p class="Normal">iPad thế hệ 3 vẫn giữ màn hình kích thước 9,7 inch nhưng độ phân tăng gấp 4 lần lên 2.045 x 1.536 pixel, mật độ điểm ảnh 264 ppi, vi xử lý lõi kép A5X (bên trong có chứa chip xử lý đồ họa lõi tứ), bộ nhớ RAM 1 GB, camera phía sau tăng lên 5 Megapixel, quay video chuẩn Full HD, trong khi camera phía trước vẫn giữ nguyên độ phân giải VGA.</p>\r\n<p class="Normal">iPad mới có giá tương tự như phiên bản 2 bao gồm khởi điểm 499 USD cho bản 16 GB, bản 32 GB và 64 GB là 599 và 699 USD. Phiên bản mạng 4G là 629, 729 và 829 USD.</p>\r\n<p class="Normal">Giá bán ở thị trường Việt Nam tính đến ngày 29/3 tham khảo khoảng 12,5 triệu đồng cho bản 16 GB kết nối Wi-Fi, 15,8 triệu cho bản 16 GB có kết nối 4G, 18,6 triệu cho bản 32 GB và 20,7 triệu cho bản 64 GB.</p>', 1, 6, 0, 23, '2012-04-01 07:06:26', 62, '', '2012-04-01 07:07:29', 62, 0, '0000-00-00 00:00:00', '2012-04-01 07:06:26', '0000-00-00 00:00:00', 'img_9295.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 1, '', '', 0, 3, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (96, 'Nhà hàng ẩm thực chú Cuội', 'nha-hang-am-thuc-chu-cuoi', '', '<p>Tuổi thơ trong mỗi người chúng ta luôn gắn liền với những điều thật bình dị, hồn nhiên và trong sáng. Chắc hẳn đã có ít nhất một lần trong đời, ai ai trong chúng ta cũng từng mơ ước được trở l ', '</p>\r\n<p>Tuổi thơ trong mỗi người chúng taluôn gắn liền với những thứ thật bình dị, hồn nhiên và trong sáng. Chắc hẳn đã có ít nhất một lần trong đời, ai ai trong chúng ta cũng từng mơ ước được trở lại với những ký ức trong trẻo ấy.&nbsp;</p>\r\n<p style="text-align: justify;">Lấy ý tưởng xuất phát từ những ký ức đẹp đẽ của tuổi thơ, người sáng lập Nhà hàng Chú Cuội với mong muốn khơi gợi lại những kỷ niệm tươi đẹp trong lòng mỗi thực khách khi đến đây. Làm sống dậy những thứ nguyên sơ tưởng như đã ngủ yên trong quá khứ bởi những bộn bề lo toan của cuộc sống hiện tại.</p>\r\n<p>Không gian nhà hàng cũng được trang trí bình dị và gần gũi như chính cái tên của nó. Từ hồ sen, khóm trúc, hàng cau thẳng tắp, giàn dây trầu đung đưa trong gió cho đến những mái ngói đỏ … một không gian mà người chủ nhân ấy đã đặt cả tâm huyết của mình trong đó, chăm chút tỉ mẫn từng chi tiết khiến nhà hàng trở nên sinh động và thật có hồn. Tất cả đều mang lại cho thực khách cảm giác bình yên đến không ngờ khi bước chân đến đây.</p>\r\n<p><img style="display: block; margin-left: auto; margin-right: auto;" src="http://www.vnnavi.com/restaurants/chucuoi/res_3_20100913_1284341139_216821904.jpg" alt="" height="268" width="401" /></p>\r\n<p><img style="display: block; margin-left: auto; margin-right: auto;" src="http://www.vnnavi.com/restaurants/chucuoi/res_3_20100913_1284341139_2146601159.jpg" alt="" height="259" width="388" /></p>\r\n<p>Sở hữu một không gian vô cùng rộng rãi và thoáng mát cùng phòng Vip riêng biệt từ 10 khách cho đến 80 khách, nhà hàng thích hợp với những buổi liên hoan công ty, họp mặt gia đình hoặc các buổi sinh nhật ấm cúng <br /><img style="display: block; margin-left: auto; margin-right: auto;" src="http://www.vnnavi.com/restaurants/chucuoi/res_3_20100913_1284341306_1854828170.jpg" alt="" height="291" width="436" /><br />Hãy đến với Chú Cuội và cho phép mình một lần được trở về với tuổi thơ để cùng ôn lại những kỷ niệm ngọt ngào khó quên của một thời…</p>', 1, 13, 0, 56, '2012-04-01 07:08:59', 62, '', '2012-04-01 07:11:41', 62, 0, '0000-00-00 00:00:00', '2012-04-01 07:08:59', '0000-00-00 00:00:00', '2011-05-23.09.18.36-mqg1303095330.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 5, '', '', 0, 5, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (102, '5 mẹo chăm cho tóc xơ rối', '5-meo-cham-cho-toc-xo-roi', '', '<p><img src="images/stories/tc dp.jpg" alt="" /><strong>Tóc xơ rối là chuyện… rất bình thường sau một thời gian dài tóc bị uốn nhuộm, và tạo đủ các kiểu thời trang.</strong></p>\r\n<p><strong>', '</strong></p>\r\n<p style="text-align: justify;">Nhưng cái chuyện rất bình thường này sẽ không xảy ra, chỉ cần bạn tuân thủ theo 5 mẹo chăm sóc <strong>tóc xơ rối</strong> dưới đây:<br /> <br /> 1.&nbsp;&nbsp;&nbsp; <strong>Giữ cho tóc đủ nước</strong>: Dầu gội và dầu xả dưỡng ẩm dạng kem có thể ngăn ngừa tình trạng xơ rối của tóc, chỉ cần bạn lưu ý xả thật sạch để tóc trông không bị bóng nhờn và xỉn màu.<br /> <br /> Mỗi tuần cần gội như vậy vài lần. Dầu tự nhiên của tóc là vũ khí tốt nhất để chống lại tóc rối.</p>\r\n<p style="text-align: center;"><img class="news-image" alt="5 mẹo chăm  cho tóc xơ rối - 1" src="http://www.eva.vn/upload/1-2012/images/2012-03-17/1331957573-tocxoroi-lamdep-eva12.jpg" /></p>\r\n<p style="text-align: justify;">2.&nbsp;&nbsp;&nbsp; <strong>Sấy khô đúng cách</strong>: Để giữ cho sóng tóc mềm mại, thoa dưỡng tạo kiểu để làm ẩm tóc, sau đó để khô tự nhiên, và đừng chốc chốc lại đưa tay làm rối tóc. <br /> <br /> Bạn càng đưa tay vuốt tóc thì càng chỉ làm tóc rối thêm mà thôi. Để có mái <em>tóc thẳng mượt</em>, hướng máy sấy từ chân tóc đến ngọn. <br /> <br /> Nếu làm theo chiều ngược lại, chỉ một chút thôi cũng đủ khiến tóc xơ rối. Và giữ máy sấy cho đến khi tóc khô hẳn mới dừng (tất nhiên là đừng sấy ở nhiệt độ cao).<br /> <br /> 3.&nbsp;&nbsp;&nbsp; <strong>Cẩn thận khi dưỡng bóng</strong>: Dùng vài giọt silicone hoặc xịt dưỡng để chống xơ rối, nhưng đừng dùng quá liều. <br /> <br /> Thoa một giọt hoặc đổ ra lòng bàn tay rồi thoa đều trong tay trước khi thoa lên tóc, bắt đầu thoa từ ngọn tóc đến chân tóc.<br /> <br /> 4.&nbsp;&nbsp;&nbsp; <strong>Ủ thêm dầu cho tóc</strong>: Để tóc thực sự không còn xơ rối, hai lần mỗi tuần hãy cho tóc được trị liệu với dầu (những loại dầu như jojoba là tốt). <br /> <br /> Dùng tay thoa khoảng một thìa cà phê dầu lên tóc, sau đó dùng lược sợi lông cứng để chải. Để càng lâu càng tốt (ít nhất từ 5 – 10 phút) trước khi gội đầu.</p>\r\n<p style="text-align: center;"><img class="news-image" alt="5 mẹo chăm  cho tóc xơ rối - 2" src="http://www.eva.vn/upload/1-2012/images/2012-03-17/1331957573-tocxoroi-lamdep-eva14.jpg" /></p>\r\n<p style="text-align: justify;">5.&nbsp;&nbsp;&nbsp;<strong> Cắt và nhuộm tóc đúng cách:</strong> Tránh cắt tóc quá nhiều tầng lớp, bởi nhiều lớp tóc khiến các sợi ngắn lòi ra ngoài và tóc nhanh xơ rối. <br /> <br /> Nếu bạn có tóc quăn hoặc tóc thô ráp, hãy tránh xa kiểu cắt tóc bằng dao cạo, bởi nó có thể khiến các biểu bì dựng ngược và làm tình trạng xơ rối trầm trọng hơn.<br /> <br /> Thay vào đó, cắt bằng kéo có thể giúp tóc bạn bớt xơ rối. Khi <span style="text-decoration: underline;">nhuộm tóc</span>, tránh xa kiểu tóc highlights, hoặc các kiểu tóc nhuộm màu vàng hoe. <br /> <br /> Việc tẩy màu tóc sẽ làm hư tổn lớp ngoài cùng của tóc, khiến nó khô ráp và xơ rối hơn.</p>\r\n<p><strong><br /></strong></p>', 1, 14, 0, 64, '2012-04-02 04:05:00', 63, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-04-02 04:05:00', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 10, '', '', 0, 2, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (103, 'Dấu hiệu bạn cần ''tân trang'' tóc', 'dau-hieu-ban-can-tan-trang-toc', '', '<p><strong>Những dấu hiệu cảnh báo bạn cần phải tới tiệm chăm sóc tóc.</strong></p>\r\n<p><strong>', '</strong></p>\r\n<p style="text-align: justify;"><strong>Tóc không vào nếp</strong></p>\r\n<p style="text-align: justify;">Mái tóc không chịu vào nếp luôn khiến bạn cảm thấy phiền toái. Nếu tóc vẫn bị rối xù ngay cả khi đã gội và sấy, tốt nhất bạn nên ép hoặc uốn lại tóc để tạo nếp gọn gàng hơn.</p>\r\n<p style="text-align: justify;"><strong>Bạn hầu như không đổi kiểu tóc mới</strong></p>\r\n<p style="text-align: justify;">Nếu bạn chỉ “trung thành” với một kiểu tóc nhất định thì hãy thay đổi để tự làm mới mình. Bắt đầu bằng việc sưu tầm những kiểu tóc mà bạn thích. Sau đó lựa chọn kiểu hợp với mình nhất và tiện lợi trong cuộc sống hằng ngày. Thay đổi một chút bắt đầu từ mái tóc sẽ khiến bạn cảm thấy mới mẻ và thú vị hơn rất nhiều.</p>\r\n<p style="text-align: justify;"><strong>Tóc của bạn không được khen nhiều nữa</strong></p>\r\n<p style="text-align: justify;">Bạn có thể lấy lại “thời hoàng kim” cho mái tóc của mình bằng máy uốn tóc. Trong tất cả các dụng cụ làm tóc bằng nhiệt, máy uốn tóc có thể cho những sóng tóc mềm mại và gợi cảm nhất.</p>\r\n<p style="text-align: center; font-style: italic; color: #0000ff;"><img class="news-image" alt="Dấu hiệu bạn cần ''tân trang'' tóc - 1" src="http://www8.eva.vn:8008/upload/1-2012/images/2012-03-31/1333179469-chamsoctoc.jpg" /><br /> Tóc bạn xơ rối và không còn được khen nhiều nữa. (Ảnh minh họa).</p>\r\n<p style="text-align: justify;"><strong>Màu highlight quá nổi bật so màu tóc nề</strong></p>\r\n<p>Đây là một dấu hiệu cho thấy màu highlight của bạn quá đậm. Bạn có thể khắc phục bằng cách che đi những lọn tóc highlight quá đậm và nhuộm highlight mới cho tóc trong vòng ít nhất ba tháng. Màu highlight nhẹ nhàng hơn sẽ tạo cho bạn vẻ ngoài hài hòa và tự nhiên hơn.</p>\r\n<p style="text-align: justify;"><strong>Màu tóc quá sáng</strong></p>\r\n<p style="text-align: justify;">Nếu màu tóc của bạn hơi sáng và gần giống như màu da, bạn có thể nhở đến sự trợ giúp của thuốc nhuộm lowlight để làm dịu màu tóc. Bạn nên chọn thuốc nhuộm tóc màu đậm và ấm hơn đồng thời không chứa chất peroxide và amoniac để bảo vệ mái tóc không bị hư tổn.</p>\r\n<p style="text-align: justify;"><strong>Bạn thay đổi cách ăn mặc nhưng mái tóc thì vẫn như cũ</strong><br /> <br /> Thay đổi cách ăn mặc là lí do hoàn hảo nhất để “nâng cấp” kiểu tóc của bạn. Ví dụ như bạn vừa giảm cân thành công, và cất hết những bộ đồ quá khổ xuống đáy tủ quần áo, bạn sẽ rất cần kiểu tóc mới mượt mà và làm tôn lên những đường nét khuôn mặt.<br /> <strong><br /> Tóc khô và xơ</strong></p>\r\n<p style="text-align: justify;">Tỉa bớt tóc là cách duy nhất để khắc phục được tình trang tóc khô và xơ. Bên cạnh đó, bạn nên dùng kem ủ tóc để bổ sung độ ẩm cho tóc, làm cho tóc dẻo dai hơn và ít gãy rụng.<br /> <br /> <strong>Thường xuyên buộc tóc đuôi ngựa<br /> </strong><br /> Nếu bạn rất thích buộc kiểu tóc đuôi ngựa, bạn nên tỉa tóc bằng hoặc tỉa tóc tầng kiểu layers để tạo hình cho đuôi tóc khi buộc. Điều đó sẽ giúp bạn có thể dễ dàng “diện” kiểu tóc đuôi ngựa thời trang và sành điệu này.<br /> <br /> <strong>Bạn mất tới hơn 20 phút để tạo kiểu tóc<br /> </strong><br /> Nếu bạn mất quá nhiều thời gian cho mái tóc thì kiểu tóc của bạn rõ ràng không được cắt tỉa phù hợp. Bạn có thể tỉa tóc layer dài nếu bạn sở hữu mái tóc xoăn. Còn nếu bạn muốn mái tóc mỏng trông dày dặn hơn, bạn có thể thử kiểu đầu blunt bob (tóc cắt ngang hàm, hở gáy, mái cắt ngang để lộ chân mày).&nbsp; <br /> <strong><br /> Tiệm cắt tóc bạn vẫn tới không còn phù hợp</strong><br /> <br /> Để tránh những kiểu tóc đi vào lối mòn, bạn nên tìm kiếm tiệm cắt tóc mới sáng tạo và chuyên nghiệp hơn. Bạn cũng có thể thay đổi kiểu tóc và màu sắc thường xuyên nếu bạn thích. Bạn cũng có thể dùng thêm keo xịt tóc tạo sóng ở phần chân tóc đối với kiểu tóc tỉa layer dài.</p>\r\n<p><strong><br /></strong></p>', 1, 14, 0, 64, '2012-04-02 04:09:50', 63, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-04-02 04:09:50', '0000-00-00 00:00:00', '1333179575_chamsoctocbaophunueva.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 9, '', '', 0, 5, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (108, 'Gà rô ti mềm ngon tuyệt đỉnh', 'ga-ro-ti-mem-ngon-tuyet-dinh-20120402', '', '<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Món này vô cùng "dễ tính", dùng chung với cơm, bánh mỳ hay thậm chí là bánh bao hấp vẫn ngon tuyệt vời!</span></strong></p>\r\n', '\r\n<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Nguyên liệu:</span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">1kg thịt gà (nhà mình thích thịt đùi nên mình dùng thịt đùi gà)</span><br /><span style="font-family: verdana,geneva; font-size: 10pt;">4 củ hành tím</span><br /><span style="font-family: verdana,geneva; font-size: 10pt;">Gia vị: nước tương, nước đường (hay đường cát), bột ớt không cay, muối, tiêu xay.</span></p>\r\n<p style="text-align: center;" class="caption"><span style="font-size: 10pt; font-family: verdana,geneva;"><img alt="Gà roti" src="images/M_images/Gà%20roti.jpg" height="332" width="500" /></span></p>\r\n<p style="text-align: justify;"><strong><em><span style="font-family: verdana,geneva; font-size: 10pt;">Bước 1:</span></em></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Thịt gà rửa sạch, để ráo sau đó khía vào khía lên lưng miếng thịt gà rồi ướp với ít muối và tiêu xay khoảng 20-30 phút cho thấm.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Hành tím xắt mỏng.</span></p>\r\n<p style="text-align: justify;"><strong><em><span style="font-family: verdana,geneva; font-size: 10pt;">Bước 2:</span></em></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Làm nóng dầu ăn trong chảo, cho hành tím vào xào thơm.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Thêm thịt gà vào xào săn.</span></p>\r\n<p style="text-align: justify;"><strong><em><span style="font-family: verdana,geneva; font-size: 10pt;">Bước 3:</span></em></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Cho 1 muỗng cà phê ớt bột (mình dùng ớt bột không cay để lấy màu), nước đường và nước tương vào chảo gà, gia giảm gia vị tùy khẩu vị. Nếu nêm nước tương mà vẫn thấy chưa được mặn thì có thể nêm thêm tí muối hoặc hạt nêm, đừng nêm chỉ một mình nước tương thôi vì nhiều nước tương quá sẽ làm món gà rất sậm màu.</span><br /><span style="font-family: verdana,geneva; font-size: 10pt;"></span></p>\r\n<p style="text-align: justify;"><em><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Bước 4:</span></strong></em></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Châm thêm ít nước vào xâm xấp mặt gà, đập nắp chảo nấu cho tới khi gà chín là được. Trong khi nấu thỉnh thoảng bạn nhớ mở nắp ra trở miếng gà cho thấm đều gia vị.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Bày ít rau sống ra đĩa, xếp thịt gà lên trên; ăn kèm với bánh mỳ hay cơm, chấm chút tương ớt sẽ rất hợp.</span></p>\r\n<p style="text-align: center;"><img style="vertical-align: middle;" alt="120320afamily-AN-ga-roti-7_ea014" src="http://khoemoingay.com/images/stories/20120319/120320afamily-AN-ga-roti-7_ea014.jpg" height="325" width="450" /></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Thịt gà là món dễ chế biến, dễ làm ra nhiều món ngon vừa miệng cho cả gia đình. Thịt gà ướp nước tương, hành tím sau đó đem “kho” cho thấm tạo nên món gà rô ti ngon miệng thích hợp cho cả nhà. Món này vô cùng "dễ tính", dùng chung với cơm, bánh mỳ hay thậm chí là bánh bao hấp vẫn ngon tuyệt vời; chính vì vậy mỗi lần làm món này mình thường làm nhiều để bữa tối ăn với cơm, sáng hôm sau còn lại ăn sáng với bánh mỳ hoặc bánh bao, rất tiện và nhanh.</span></p>\r\n<p style="text-align: right;"><span style="font-family: verdana,geneva; font-size: 10pt;"><strong>(Theo khoemoingay.com)</strong><br /></span></p>', 1, 13, 0, 61, '2012-04-02 08:30:14', 63, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-04-02 08:30:14', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 10, 'món ngon, bánh bao, bánh mỳ', 'Món này vô cùng dễ tính, dùng chung với cơm, bánh mỳ hay thậm chí là bánh bao hấp vẫn ngon tuyệt vời!', 0, 3, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (109, 'Đơn giản món cá bống kho tiêu', 'don-gian-mon-ca-bong-kho-tieu-20120402', '', '<p><strong><span style="font-family: verdana,geneva; font-size: 10pt;">rổ tài nấu ăn bằng món cá bống kho tiêu cho cả nhà nào!</span></strong></p>\r\n', '\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Cá bống kho tiêu tuy không phải là món cao lương mỹ vị nhưng hấp dẫn bao người. Các bạn cùng thử món này xem nhé!</span></p>\r\n<p style="text-align: center;" class="caption"><span style="font-size: 10pt; font-family: verdana,geneva;"><br /></span></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Nguyên liệu:</span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Cá bống sông: 500gr</span><br /><span style="font-family: verdana,geneva; font-size: 10pt;">- Giềng: 1 nhánh nhỏ</span><br /><span style="font-family: verdana,geneva; font-size: 10pt;">- Tiêu sọ nguyên hạt: 1 thìa cà phê</span><br /><span style="font-family: verdana,geneva; font-size: 10pt;">- Tiêu xay: 1/2 thìa cà phê</span><br /><span style="font-family: verdana,geneva; font-size: 10pt;">- Nước mắm: 2/3 bát nhỏ</span><br /><span style="font-family: verdana,geneva; font-size: 10pt;">- Đường: 3-4 thìa cà phê</span><br /><span style="font-family: verdana,geneva; font-size: 10pt;">- Vài miếng mỡ</span></p>\r\n<p style="text-align: center;" class="caption"><span style="font-size: 10pt; font-family: verdana,geneva;"><img src="images/stories/n gin mn c bng kho tiu.jpg" alt="" title="Đơn giản món cá bống kho tiêu" /></span></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Cách làm:</span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Bước 1: cá bống sơ chế sạch, mỡ và riềng thái thành những lát mỏng xếp xuống dưới đáy nồi. Các bạn nên dùng nồi đất và nồi gốm cá kho sẽ ngon hơn.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Bước 2: lần lượt xếp cá kín nồi rồi thả thêm vài lát riềng lên trên.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Bước 3: rót nước mắm vào cá ướp khoảng 1 giờ, mục đích để gia vị thấm đều, cá săn cứng lại và dẻo dai hơn.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Bước 4: rắc tiêu sọ và tiêu xay vào nồi rồi bật bếp đun to lửa cho cá sôi mới tiến hành chưng nước hàng. Đun đường đến khi chuyển màu cánh gián thì chế thêm vào 1 muỗng canh nước nóng, đun sôi trở lại rồi trút nước hàng đang sôi vào nồi cá (nhớ là phải là nước hàng đang sôi nhé, nếu để nguội là cá sẽ bị tanh).</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Bước 5: lúc này là cá đã gần cạn nước, các bạn vặn nhỏ lửa tiếp tục đun liu riu đến khi cá cạn sạch nước, miếng cá sẽ chuyển màu sậm hơn, khô và chắc.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Lúc này nồi cá dậy mùi thơm nức, những con cá bống đã khô cong, hòa với lớp nước màu vàng cánh gián và lớp mỡ bóng nhìn rất hấp dẫn.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Ăn với cơm nóng nghe vị cá bống mặn mòi, thơm béo, quyện với vị tiêu cay nồng nơi đầu lưỡi... quả là một bữa cơm ngon, giản dị mà thật nhiều ý nghĩa.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Mời cả nhà cá hồi kho tiêu nào Cá kho dành cho người sợ béo</span>.</p>\r\n<p style="text-align: right;"><strong>(Theo khoemoingay.com)</strong></p>', 1, 13, 0, 61, '2012-04-02 08:41:43', 63, '', '0000-00-00 00:00:00', 0, 63, '2012-04-02 08:53:40', '2012-04-02 08:41:43', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 9, 'nấu ăn, cá bống, cá bống kho tiêu', 'rổ tài nấu ăn bằng món cá bống kho tiêu cho cả nhà nào!', 0, 1, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (112, '7 món ăn từ thịt bò giúp mạnh thận ích tinh', '7-mon-an-tu-thit-bo-giup-manh-than-ich-tinh', '', '<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Các món ăn từ thịt bò bao giờ cũng hấp dẫn bởi vị ngon và bổ dưỡng. Thịt bò có nhiều axít amin, canxi, phospho, sắt và các vitamin A, B, D… có tác dụng bổ thận, dưỡng khí huyết, làm cường tráng cơ bắp, rất hiệu quả trong việc bổ sung năng lượng và tăng sức dẻo dai của cơ thể. Xin giới thiệu một số món ăn - bài thuốc để bạn đọc tham khảo.</span></strong></p>\r\n', '\r\n<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Bài 1:</span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Thịt bò 250g, phúc bồn tử 10g, dầu thực vật, muối, xì dầu, rượu cái nếp, hồi vừa đủ. Phúc bồn tử rửa nhanh tay khỏi nát, cho vào 1 thìa rượu cái. Thịt bò rửa sạch thái miếng. Bắc chảo lên bếp, cho 1 thìa dầu, đun nóng rồi cho thịt bò vào xào trong 5 phút. Đổ thịt sang nồi đất, cho phúc bồn tử vào với một ít hạt mùi, thêm nước cho ngập thịt bò, đun lửa to cho sôi rồi nhỏ lửa hầm cho thịt chín nhừ là được. Ăn trong bữa cơm, ngày 2 - 3 lần. Tác dụng: kiện tỳ ích vị, bổ thận, giảm đi tiểu nhiều lần.</span><br /><span style="font-family: verdana,geneva; font-size: 10pt;"></span></p>\r\n<p style="text-align: center;" class="caption"><span style="font-size: 10pt; font-family: verdana,geneva;"><img alt="7 món ăn từ thịt bò giúp mạnh thận ích tinh" src="images/M_images/20120402/7%20món%20ăn%20từ%20thịt%20bò%20giúp%20mạnh%20thận%20ích%20tinh.jpg" height="331" width="400" /></span></p>\r\n<p style="text-align: center;"><span style="font-family: verdana,geneva; font-size: 8pt;">Thịt bò bổ thận, cường tráng cơ bắp.</span></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Bài 2:</span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">thịt bò 250g, bạch phục linh 200g, hoài sơn 200g, tiểu hồi 200g, hạt sen 200g, táo tàu 100g. Thịt bò sấy chín vàng, tán thành bột. Tiểu hồi sao thơm, đem nghiền cùng hoài sơn, phục linh, hạt sen. Táo tàu chưng chín. Đem bột thịt bò và bột hoài sơn, hạt sen, phục linh, tiểu hồi hòa với nước táo tàu làm viên bằng hạt ngô. Mỗi ngày ăn 50g. Tác dụng: tốt cho tiêu hóa, người thận hư lưng đau, toàn thân mệt mỏi nên ăn.</span></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Bài 3: </span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Thịt bò 250g, tất bát 15g, hồ tiêu 15g, trần bì 6g, thảo quả 6g, sa nhân 6g, gừng tươi, muối, rượu vừa đủ. Thịt bò làm sạch, nhúng qua nước sôi, thái miếng. Gừng, hành ép lấy nước trộn với các vị trên tán thành bột thuốc, cho ít nước trộn đều, ướp thịt bò, cho vào lọ đậy nắp kín để 2 ngày thì lấy ra, rửa sạch bằng nước sôi, để ráo nước, cho vào lò sấy thành thịt bò khô. Ăn dần trong bữa ăn hoặc ăn riêng tùy ý. Tác dụng: trị tỳ vị hư hàn, thấp trệ, bụng sôi, ăn kém, chân tay lạnh.</span></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Bài 4: </span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Thịt bò 250g; hạt sen 30g; sơn dược, phục linh, tiểu hồi, táo tàu mỗi vị 250g; muối một ít. Thịt bò rửa sạch thái nhỏ, cho vào nồi, đổ nước vừa đủ, đun to lửa cho sôi. Khi thịt chín tới thì cho các vị trên đã rửa sạch vào, đun nhỏ lửa đến khi thịt chín nhừ là được. Ăn trong bữa cơm.Tác dụng: ích khí huyết, cường tinh, ninh thần.</span></p>\r\n<p style="text-align: center;" class="caption"><span style="font-size: 10pt; font-family: verdana,geneva;"><img style="vertical-align: middle;" alt="thit-bo-ham-ca-rot-khoai-tay" src="http://khoemoingay.com/images/stories/20120228/thit-bo-ham-ca-rot-khoai-tay.jpg" height="268" width="450" /></span></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Bài 5:</span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Thịt bò 250g, bơ 25g, khởi tử 15g, hành tây 1 củ, cà rốt 1 củ, khoai tây 1 củ. Thái thịt thành từng miếng vuông khoảng 3 - 4cm, rắc muối, hồ tiêu lên thịt đảo đều, cho bơ vào xoong, đun nóng xào thịt cho ngả màu rồi cho hành vào xào, tiếp theo cho khởi tử vào và đổ 400ml nước, đun sôi, cuối cùng cho cà rốt, khoai tây vào đun khoảng 2 giờ đồng hồ là được. Trước khi bắc nồi xuống khoảng 20 phút cho 1 thìa bột vào để nước xào sền sệt là được. Ăn trong bữa cơm. Tác dụng: trị thanh niên thận suy, tóc bạc sớm, liệt dương di tinh, phụ nữ lãnh cảm, trẻ em chậm lớn, người già thể lực suy.</span></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Bài 6: </span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">Thịt bò 250g, hoài sơn 10g, gia vị vừa đủ. Thịt bò rửa sạch, thái miếng cho vào nồi, đổ nước vừa đủ nấu chín, cho tiếp hoài sơn vào nấu chín rồi cho gia vị vào là được. Ăn trong ngày. Tác dụng: trị ăn uống kém, liệt dương, xuất tinh sớm, lãnh đạm trong sinh hoạt tình dục, chân tay rã rời.</span></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Bài 7: </span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">đuôi bò 60g, đương quy 50g, hạt tiêu, muối, gừng vừa đủ. Đuôi bò cạo lông rửa sạch, chặt khúc. Đương quy rửa sạch để ráo. Gừng rửa sạch cạo vỏ cắt sợi nhỏ. Đuôi bò, đương quy cho vào nồi, đổ nước vừa đủ rồi đun sôi, sau đun nhỏ lửa hầm khoảng 2 giờ, khi đuôi bò chín, nêm gia vị cho vừa, cho gừng, hạt tiêu bột cho thơm là được. Ăn liên tục khoảng 7 - 10 ngày. Tác dụng: chữa thận dương suy, gân cốt yếu, hai chân đau nhức.</span></p>\r\n<p style="text-align: right;"><span style="font-family: verdana,geneva; font-size: 10pt;"><strong>(Theo khoemoingay.com)</strong><br /></span></p>', 1, 13, 0, 61, '2012-04-02 09:00:15', 63, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-04-02 09:00:15', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 8, 'thịt bò, bổ dưỡng', 'Các món ăn từ thịt bò bao giờ cũng hấp dẫn bởi vị ngon và bổ dưỡng. Thịt bò có nhiều axít amin, canxi, phospho, sắt và các vitamin A, B, D… có tác dụng bổ thận, dưỡng khí huyết, làm cường tráng cơ bắp, rất hiệu quả trong việc bổ sung năng lượng và tăng sức dẻo dai của cơ thể. Xin giới thiệu một số món ăn - bài thuốc để bạn đọc tham khảo.', 0, 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (113, 'Dưa chuột xào thịt ba chỉ - cho làn da thêm mượt', 'dua-chuot-xao-thit-ba-chi-cho-lan-da-them-muot', '', '<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Vài quả dưa chuột, ít lạng thịt ba chỉ, thêm một chút vị cay cay của ớt đỏ, thơm nồng khó lẫn của tỏi phi… bạn đã có ngay món xào ngon miệng, cũng là vị thuốc tự nhiên giữ ẩm và chống nhăn cho làn da.</span></strong></p>\r\n', '\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;"><strong>Nguyên liệu:</strong></span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Vài quả dưa chuột (lựa quả càng nhỏ càng tốt)</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- 200g thịt ba chỉ, vài quả ớt đỏ, tỏi, gia vị…</span></p>\r\n<p style="text-align: center;" class="caption"><span style="font-size: 10pt; font-family: verdana,geneva;"><img alt="Dưa chuột xào thịt ba chỉ - cho làn da thêm mượt" src="images/M_images/20120402/Dưa%20chuột%20xào%20thịt%20ba%20chỉ%20-%20cho%20làn%20da%20thêm%20mượt.jpg" height="150" width="145" /></span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;"><strong>Cách làm:</strong></span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Dưa chuột sau khi rửa nên ngâm nước muối pha loãng cho sạch, để nguyên vỏ.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Thái vát dưa chuột, mỗi lát dày chừng 0,5cm.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Thịt ba chỉ thái miếng mỏng</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Ớt đỏ (loại trang trí, tỉa hoa) cắt nhỏ, tỏi băm nhỏ</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Cho ít dầu ăn vào chảo, cho tỏi vào phi thơm, tiếp tục cho thịt ba chỉ và ớt đỏ vào xào nhanh tay. Nêm ít xì dầu, gia vị cho vừa miệng.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Khi thấy thịt chín, trút dưa chuột vào đảo đều tay khoảng 3 phút là món ăn hoàn tất.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;"><strong>Mách nhỏ:</strong></span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Món dưa chuột xào thịt ba chỉ tránh để lửa quá to sẽ bị khô.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Dưa chuột nên cho vào sau cùng và đảo nhanh để không quá chín, mất độ giòn.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Món ăn này có thể xem như mỹ phẩm làm đẹp tự nhiên, có tác dụng bổ sung nước, giữ ẩm cho da trước cơn gió lạnh và chống nhăn hiệu quả.</span></p>', 1, 13, 0, 61, '2012-04-02 09:03:54', 63, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-04-02 09:03:54', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 7, '', 'Vài quả dưa chuột, ít lạng thịt ba chỉ, thêm một chút vị cay cay của ớt đỏ, thơm nồng khó lẫn của tỏi phi… bạn đã có ngay món xào ngon miệng, cũng là vị thuốc tự nhiên giữ ẩm và chống nhăn cho làn da.', 0, 1, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (114, 'Sức hút kì lạ của cánh gà om quế, hoa hồi', 'suc-hut-ki-la-cua-canh-ga-om-que-hoa-hoi', '', '<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Từ các nguyên liệu quế, hoa hồi, cọng hành lá, vài cánh gà… bạn có thể chế biến món cánh gà rim cực đỉnh khiến anh xã và các thành viên nhí nhà bạn sẽ phải mê tít.</span></strong></p>\r\n', '\r\n<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Nguyên liệu:</span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Cánh gà</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Vài miếng quế, vài hoa hồi, hành lá (chọn cọng to)</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Xì dầu, ớt bột khô, đường, gừng…</span></p>\r\n<p style="text-align: center;" class="caption"><span style="font-size: 10pt; font-family: verdana,geneva;"><img src="images/stories/sc ht k l ca cnh g om qu hoa hi.jpg" alt="" /></span></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: verdana,geneva; font-size: 10pt;">Cách làm:</span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Cánh gà sau khi rửa sạch, dùng khăn thấm khô, sau đó dùng dao sắc khứa để cánh gà vừa đẹp vừa nhanh ngấm gia vị.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Dùng chổi phết một lớp dầu ăn thật mỏng lên chảo, rồi cho cánh gà vào, rán sơ vàng đều hai mặt. Gắp để riêng ra đĩa.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Trải một lớp cọng hành lá lên chảo vừa rán cánh gà, xếp cánh gà lên trên.</span></p>\r\n<p style="text-align: justify;"><span style="font-family: verdana,geneva; font-size: 10pt;">- Lần lượt cho gừng thái miếng, quế đập dập, hoa hồi, ớt bột, đường, nước tương, hạt tiêu… và bắt đầu rim với lửa nhỏ đến khi nước sền sệt.</span></p>', 1, 13, 0, 61, '2012-04-02 09:06:32', 63, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-04-02 09:06:32', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 6, '', '', 0, 0, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (119, 'Món ngon ân tình quê Việt', 'mon-ngon-an-tinh-que-viet', '', '<p>Tuy không cao sang như món tây, nhưng những món ăn truyền thống Việt luôn thấm đượm những ân tình thâm trầm và sâu lắng. ', '</p>\r\n<p style="text-align: justify;"><strong>Gỏi sứa hoa chuối</strong></p>\r\n<p style="text-align: center;"><strong><img class="news-image" alt="Món ngon ân tình quê Việt, Ẩm thực, goi sua, bo cuon mia, sua tuoi, coi so diep, mang tay, nam dong co" src="http://anh.24h.com.vn/upload/1-2011/images/2011-01-06/1294306976-goi-sua.jpg" /></strong></p>\r\n<p style="text-align: justify;"><span style="text-decoration: underline;"><em><strong>Nguyên liệu</strong></em></span></p>\r\n<p style="text-align: justify;">150g sứa tươi<br /> 150g hoa chuối<br /> 1 trái khế, 3 trái ớt, 1 nhánh gừng, 3 tép tỏi, 10g mè, 10g đậu phộng<br /> 1 thìa café hạt nêm, 1 thìa súp nước mắm, 2 thìa café đường, 2 thìa café nước cốt chanh<br /> Kinh giới, húng quế.</p>\r\n<p style="text-align: justify;"><strong><span style="text-decoration: underline;"><em>Thực hiện</em></span></strong></p>\r\n<p style="text-align: justify;">Sứa tươi mua về rửa sạch bằng nước đun sôi để nguội có pha chút gừng, rồi chần qua nước sôi, thái sợi nhỏ.</p>\r\n<p style="text-align: justify;">Hoa chuối thái nhỏ, ngâm nước muối pha loãng, vớt ra để ráo. Khế rửa sạch gọt bỏ cạnh, xắt lát mỏng. Ớt xắt sợi. Gừng gọt vỏ, xắt chỉ. Tỏi bóc vỏ, bào mỏng. Đậu phộng rang thơm, xát bỏ vỏ lụa.</p>\r\n<p style="text-align: justify;">Rau thơm nhặt bỏ lá sâu, rửa sạch, vớt ra vẩy ráo, xắt nhỏ.</p>\r\n<p style="text-align: justify;">Trộn đều nước mắm với đường, hạt nêm. Cho sứa, rau thơm, hoa chuối, khế, ớt, gừng, tỏi vào trộn đều.</p>\r\n<p style="text-align: justify;">Cho gỏi ra đĩa, rắc mè và đậu phộng lên trên. Dùng ngay.</p>\r\n<p style="text-align: justify;"><strong>Bò cuốn mía</strong></p>\r\n<p style="text-align: center;"><strong><img class="news-image" alt="Món ngon ân tình quê Việt, Ẩm thực, goi sua, bo cuon mia, sua tuoi, coi so diep, mang tay, nam dong co" src="http://anh.24h.com.vn/upload/1-2011/images/2011-01-06/1294306976-bo-cuon-mia.jpg" /></strong></p>\r\n<p style="text-align: justify;"><span style="text-decoration: underline;"><em><strong>Nguyên liệu</strong></em></span></p>\r\n<p style="text-align: justify;">100g thịt bò<br /> 10 lọn mía<br /> 1 thìa súp tỏi, hành băm, 1 thìa café ớt băm, 10g ngò rí xay, 1 thìa súp nước mắm, ¼ thìa café muối, ½ thìa café đường, 1/5 thìa café bột ngọt<br /> Ngũ vị hương, dầu màu điều</p>\r\n<p style="text-align: justify;"><em><span style="text-decoration: underline;"><strong>Thực hiện</strong></span></em></p>\r\n<p style="text-align: justify;">Thịt bò rửa sạch, thái bản to, mỏng, ướp với tỏi, hành tím băm, ớt, ngò rí xay, nước mắm, muối, đường, bột ngọt, ngũ vị hương, màu điều thật thấm.</p>\r\n<p style="text-align: justify;">Mía đem lóc bỏ vỏ, lấy lõi. Quấn thịt bò quanh lõi mía. Đem nướng ở nhiệt độ 95oC – 100oC trong khoảng 10 phút là được.</p>\r\n<p style="text-align: justify;"><br /> <strong>Chả giò hải sản xốt mayonnaise</strong></p>\r\n<p style="text-align: center;"><strong><img class="news-image" alt="Món ngon ân tình quê Việt, Ẩm thực, goi sua, bo cuon mia, sua tuoi, coi so diep, mang tay, nam dong co" src="http://anh.24h.com.vn/upload/1-2011/images/2011-01-06/1294306976-cha-gio-hai-san.jpg" /></strong></p>\r\n<p style="text-align: justify;"><span style="text-decoration: underline;"><em><strong>Nguyên liệu</strong></em></span></p>\r\n<p style="text-align: justify;">150g tôm<br /> 150g mực<br /> 150g thịt cua, 3 lòng đỏ trứng gà, 50g nấm kim châm, 1 củ hành tây, 3 củ hành tím, 2 xấp bánh bía, 50g bột chiên xù, ½ thìa café muối, 1 thìa café đường, 1 thìa café hạt nêm.</p>\r\n<p style="text-align: justify;"><span style="text-decoration: underline;"><em><strong>Thực hiện</strong></em></span></p>\r\n<p style="text-align: justify;">Tôm, thịt cua, mực sơ chế sạch, đem luộc sơ, xắt hạt lựu</p>\r\n<p style="text-align: justify;">Nấm kim châm, hành tây rửa sạch, tất cả xắt hạt lựu. Hành tím băm nhỏ.</p>\r\n<p style="text-align: justify;">Phi thơm hành tím, cho tôm, mực vào xào săn, cho thịt cua vào xào chung cùng với nấm kim châm và hành tây. Nêm muối, nước mắm, hạt nêm, đường, tiêu xay, tắt bếp, cho ra đĩa để nguội rồi trộn cùng xốt mayonnaise.</p>\r\n<p style="text-align: justify;">Đánh tan lòng đỏ trứng gà. Trải bánh bía ra, cho một ít hỗn hợp đồ xào vào giữa, cuộn lại.</p>\r\n<p style="text-align: justify;"><strong>Cồi sò điệp xốt măng tây</strong></p>\r\n<p style="text-align: center;"><strong><img class="news-image" alt="Món ngon ân tình quê Việt, Ẩm thực, goi sua, bo cuon mia, sua tuoi, coi so diep, mang tay, nam dong co" src="http://anh.24h.com.vn/upload/1-2011/images/2011-01-06/1294306976-coi-so-diep.jpg" /></strong></p>\r\n<p style="text-align: justify;"><span style="text-decoration: underline;"><em><strong>Nguyên liệu</strong></em></span></p>\r\n<p style="text-align: justify;">150g cồi sò điệp<br /> 200g măng tây<br /> 50g nấm đông cô, 50g nấm rơm, 1 củ cà rốt, 1 thìa café dầu hào, 2 thìa súp nước tương, ½ thìa café hạt nêm, ½ thìa café đường, 1 nhánh gừng, ½ thìa café nước mắm, ½ thìa café tỏi băm. Dầu ăn.</p>\r\n<p style="text-align: justify;"><span style="text-decoration: underline;"><em><strong>Thực hiện</strong></em></span></p>\r\n<p style="text-align: justify;">Cồi sò điệp chần qua nước sôi. Phi thơm tỏi băm, cho sò điệp vào xóc chảo cho chín đều hai mặt.</p>\r\n<p style="text-align: justify;">Măng tây cắt bỏ gốc già, lấy phần ngọn non, luộc sơ. Nấm rơm, đông cô cắt bỏ chân, ngâm nước muối loãng, vớt ra để ráo, chần chín. Gừng gọt vỏ rửa sạch, đập giập, băm nhỏ. Cà rốt gọt vỏ rửa sạch, tỉa hoa.</p>\r\n<p style="text-align: justify;"><span style="text-decoration: underline;">Làm nước xốt</span>: Cho dầu hào, nước tương, nước mắm Kabin, bột ngọt, đường và gừng vào đun sôi cho kẹo lại.</p>\r\n<p style="text-align: justify;">Xếp măng tây vào đĩa, đặt sò điệp lên trên. Cho nấm vào đảo qua với nước xốt, rưới hỗn hợp này lên măng tây và sò điệp.</p>', 1, 13, 0, 61, '2012-04-02 10:21:47', 63, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-04-02 10:21:47', '0000-00-00 00:00:00', 'goi sua.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 5, '', '', 0, 2, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (121, 'Phong phú ẩm thực Việt Nam', 'phong-phu-am-thuc-viet-nam', '', '<p>Một vị khách nước ngoài sau chuyến đi từ Sài Gòn ra Huế, Hà Nội đã “cắc cớ” hỏi người hướng dẫn du lịch về cơ sở văn hoá của các món ăn ba miền Nam, Trung, Bắc. Thật ra thì đôi nét khác nhau đó chỉ thể hiện sự phong phú, đa dạng của một nền ăn uống Việt Nam trên cơ sở văn hoá chung.</p>\r\n', '\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;">Tôi nghĩ, đất Bắc là nơi còn giữ đầy đủ nhất “cái ăn” truyền thống. Suốt quá trình Nam tiến, ông bà ta đã giữ cái hồn của nền ăn uống Việt Nam và không ngừng sáng tạo, thích nghi theo điều kiện sống ở vùng đất mới, đem lại sự đa dạng, đặc sắc của các món ăn ba miền.</p>\r\n<p style="text-align: justify;"><strong>Khẩu vị miền Bắc: “nghiêm ngặt đến bảo thủ?”</strong></p>\r\n<p style="text-align: center;"><strong><img class="news-image" alt="Món ăn ba miền: Phong phú ẩm thực Việt Nam, Ẩm thực, am thuc ba mien, dac san hue, bun cha, mam tom, mon an mien nam" src="http://anh.24h.com.vn/upload/4-2010/images/2010-11-27/1290828675-bun-cha-ha-noi.jpg" /></strong></p>\r\n<p style="text-align: center;"><span style="color: #0000ff;"><em>Bún chả Hà Nội </em></span></p>\r\n<p style="text-align: justify;">Nhớ lại hồi đầu năm 1955, khi sư đoàn chúng tôi rời Bạc Liêu tập kết ra Bắc gặp lúc nạn đói đang hoành hành. Chúng tôi hành quân qua những “vựa lúa” miền Bắc thấy cảnh bà con đào củ rau má, củ chuối nấu cháo trong lúc trên đường làng rất nhiều cóc, ngoài đồng nhái, ễnh ương kêu vang, vẫn không có ai bắt ăn. Chúng tôi thử bắt cóc, làm thịt nấu cháo, bắt nhái xào với lá mướp đem mời chủ nhà nơi mình đóng quân thì đều bị từ chối. Ngay hôm đó cả làng ầm lên kháo nhau một tin “rùng rợn”: các anh bộ đội miền Nam ăn cả cóc, nhái, ễnh ương!</p>\r\n<p style="text-align: justify;">Dần dần, chúng tôi mới hiểu ra rằng, các vùng châu thổ phía Bắc là nơi tổ tiên ta sớm định cư từ lâu đời, mọi cái ăn, cái mặc đều được sàng lọc, đúc kết để trở nên chuẩn mực của làng, của nước. Dù lâm vào cảnh đói nghèo cũng không ai được làm trái “đất lề, quê thói”. Từ thuở các vua Hùng đã có hội thi nấu cơm, làm bánh, chế biến thức ăn. Nền văn minh ăn uống hình thành cùng với ý chí “Nam quốc sơn hà nam đế cư”. Qua ngàn năm Bắc thuộc, xì dầu không át được mắm tôm; tương tàu không thay được tương ta (tương bần). Hai nước giống nhau ở cái thú thịt chó, nhưng miếng thịt chó ướp tương tàu mà ta đọc thấy Trịnh Ân ngoạm nhồm nhoàm khác hẳn miếng thịt cầy ướp mắm tôm, riềng, mẻ. Quan niệm “dĩ thực vi tiên” thấp hơn “có thực mới vực được đạo”. Phong cách ăn của người Tàu lộ vẻ phô trương, bày biện trước hàng phố đông người, ăn xì xụp ồn ã. Ông bà ta dọn mâm nơi kín đáo, ăn uống khoan thai. Ăn chậm, nhai kỹ, ăn vừa đủ đã được đúc kết thành khoa học dưỡng sinh trong “Vệ sinh yếu quyết” của Hải Thượng.</p>\r\n<p style="text-align: justify;">Tôi cảm thấy khẩu vị miền Bắc nghiêm ngặt đến mức “bảo thủ” có lẽ là vì nó được “canh gác” thường trực để chống nỗi lo bị đồng hoá của người khổng lồ phương Bắc. Con gà cục tác lá chanh, Con lợn ủn ỉn mua hành cho tôi. Con chó khóc đứng khóc ngồi. Bà ơi đi chợ mua tôi đồng riềng. Người miền Trung và miền Nam trộn thịt gà với rau răm, không có rau răm thì cho thứ rau khác, ở đồng bằng sông Cửu Long cho ngọn đinh lăng vào cũng được. Ở Bắc, con gà không chấp nhận bất cứ thứ lá gì khác lá chanh. Gỏi cá thì phải là cá mè và phải có hai thứ rau chủ chốt là đinh lăng và vọng cách. Chỉ riêng món bún cũng đã có những quy định rạch ròi: bún ốc đi với nước chua và ớt băm nhuyễn; bún chả nước mắm pha và rau húng láng; bún bung với dọc mùng (còn gọi là cây sơn hà, miền Nam gọi là bạc hà); canh bún, cá rô, rau cần, bún thang nổi vị mắm tôm…</p>\r\n<p style="text-align: center;"><img class="news-image" alt="Món ăn ba miền: Phong phú ẩm thực Việt Nam, Ẩm thực, am thuc ba mien, dac san hue, bun cha, mam tom, mon an mien nam" src="http://anh.24h.com.vn/upload/4-2010/images/2010-11-27/1290828675-phong-vi-mien-bac.jpg" /><br /> <br /> <span style="color: #0000ff;"><em>Phong vị xứ bắc</em></span></p>\r\n<p style="text-align: justify;">Có lẽ chính quan điểm về cái ăn và phong cách ăn đã góp phần tạo nên những món ăn đặc sắc của xứ Bắc. Vâng, nhưng rồi những món ăn ấy lại góp phần củng cố cái phong cách ăn của cộng đồng đã tạo ra nó. Ai mà ăn vội ăn vàng được khi dùng món chả cá sông Hồng. Thức ăn đã bày lên bàn, nhưng “công đoạn” tiếp theo với rất nhiều “thao tác” là do người ăn thực hiện: gắp bún sợi đặt vào bát, hãy điểm lên vài cánh rau thơm, vài tẻ hành trắng nõn kia nữa và đừng quên một vài hột đậu phụng rang, bấy giờ mới gắp miếng cá nóng hổi kèm cọng thì là, khẽ cho vào bát mắm tôm chanh ớt sủi bọt có vị cà cuống “cất cánh…”</p>\r\n<p style="text-align: justify;">Cách chế biến tinh tế, gia vị thanh nhẹ đã khiến cho người ăn như chiêm ngưỡng, không thể vội vã và ồn ào. Nước dùng của phở, của bún thang là thứ nước nấu xương với lửa riu riu, sôi lom rom, không được đun áp quá, phải luôn tay hớt bọt lúc vừa sôi, nấu làm sao để khi dùng là một thứ nước trong vắt như nước mưa, thoảng vương hơi vàng mà chưa nổi thành màu vàng, nếm thấy ngọt lịm nơi đầu lưỡi.</p>\r\n<p style="text-align: center;"><img class="news-image" alt="Món ăn ba miền: Phong phú ẩm thực Việt Nam, Ẩm thực, am thuc ba mien, dac san hue, bun cha, mam tom, mon an mien nam" src="http://anh.24h.com.vn/upload/4-2010/images/2010-11-27/1290828675-bun-thang.jpg" /></p>\r\n<p style="text-align: center;"><span style="color: #0000ff;"><em>Bún thang</em></span></p>\r\n<p style="text-align: justify;">Bún thang là món gồm nhiều thứ: thịt gà xé nhỏ, những sợi giò lụa li ti, trứng tráng thái mỏng giống như sợi tơ hồng, ruốc tôm, hành chần, rau mùi, rau răm… Bún trắng nõn đặt vào loạt bát sứ mỏng viền vàng, các thức kể trên xếp lên từng cụm, trên cùng là hành và rau, một thìa nhỏ mắm tôm, cho nước dùng đang sôi vào và chao nghiêng cái bát một lượt để chần bún làm tăng độ nóng đến bỏng lưỡi và một chiếc tăm nhúng vào lọ tinh dầu cà cuống dầm vào bát bún… Người ta nói ăn bún thang không ai ăn hai bát, bởi làm như vậy là xúc phạm cái hồn thanh đạm của món ăn sánh như một cô gái tuyệt vời tài sắc.</p>\r\n<p style="text-align: justify;"><strong>Các món cá miền Trung và nghệ thuật ăn uống Huế</strong></p>\r\n<p style="text-align: center;"><img class="news-image" alt="Món ăn ba miền: Phong phú ẩm thực Việt Nam, Ẩm thực, am thuc ba mien, dac san hue, bun cha, mam tom, mon an mien nam" src="http://anh.24h.com.vn/upload/4-2010/images/2010-11-27/1290828675-bun-bo-hue.jpg" /><br /> <br /> <span style="color: #0000ff;"><em>Bún bò Huế </em></span></p>\r\n<p style="text-align: justify;">Quá Nghệ An, con heo đã thay thế con lợn cho đến tận Cà Mau. Từ đây phong vị của những người đi mở cõi thắm đượm trong bữa cơm. Đặc sắc địa phương cũng rõ nét ở các món ăn bởi sản vật, thời tiết và nhất là điều kiện sống. Món “cá gỗ” của Nghệ An có lẽ cũng là đặc trưng của nhiều vùng đất hẹp người đông của miền Trung. Với bờ biển dài, bề ngang hẹp của “dằng dặc khúc rụôt miền Trung”, mắm ruốc, cá kho, đã đi vào mâm cơm của số đông thay cho “tương cà gia bản” của truyền thống miền Bắc.</p>\r\n<p style="text-align: justify;">Món cá ngừ kho chan bún, bánh tráng là đặc sản của của dọc suốt duyên hải miền Trung. Nói chung món cá kho miền Trung phần nhiều là cá biển và thường kho lẫn với các loại rau quả như khế, cà chua, thơm, dưa môn, dưa cải, chuối chát, trái vả, dưa hường, mít non…</p>\r\n<p style="text-align: justify;">Món gỏi cũng phần lớn chế biến từ cá biển và các loại hải sản như tôm, mực, nổi tiếng có gỏi cá mai Phan Thiết. Suốt miền Trung cho đến miền Đông Nam Bộ có món gỏi mít non hấp dẫn. Miền Trung còn có món mì Quảng nổi tiếng. Tôi nghĩ, cũng như hủ tíu của Nam bộ, mì Quảng là biến thể của phở, đều từ bột bánh làm bằng gạo chan nước dùng.</p>\r\n<p style="text-align: justify;">Ở vùng biển, tôm cá nhiều, bò trâu ít, phở trở thành món cao cấp nhớ mà thèm, bà con ta đã sáng tạo món mì Quảng, cũng là bánh sợi bột gạo, nhưng nước dùng từ tôm, thịt heo, thịt vịt. Có lẽ để bánh bột hoà hợp được với nước chan chế biến từ hải sản, bột bánh có pha nghệ cho thơm, có màu vàng hài hoà với màu tôm đỏ và làm mất mùi tanh của nước dùng nấu từ các loại hải sản. Mì Quảng thật ngon là loại chế biến từ bột bánh bằng gạo Phú Chiêm, tôm Cửa Đại, rau thơm Trà Quế. Từ đất Quảng, món mì Quảng giành được khẩu vị của tất cả các tỉnh miền Trung và tiến vào Sài Gòn với vị trí đặc sản.</p>\r\n<p style="text-align: justify;">Một trung tâm ăn uống lớn của miền Trung là Huế. Món ăn Huế là sự chọn lọc các món từ đàng ngoài và cải tiến, nâng cao cho phù hợp với thổ nghi, sản vật Huế. Cuộc sống vua chúa với nhu cầu hưởng lạc cao, nhất là sau khi giang san thu về một mối, chính là thời cơ vàng để các món ăn Huế phát triển. Bữa tiệc có hằng trăm đĩa nhỏ đựng các loại thức ăn khác nhau, chế biến khéo léo của xứ Đàng trong còn lưu lại.</p>\r\n<p style="text-align: center;"><img class="news-image" alt="Món ăn ba miền: Phong phú ẩm thực Việt Nam, Ẩm thực, am thuc ba mien, dac san hue, bun cha, mam tom, mon an mien nam" src="http://anh.24h.com.vn/upload/4-2010/images/2010-11-27/1290828675-com-hen.jpg" /></p>\r\n<p style="text-align: center;"><span style="color: #0000ff;"><em>Cơm hến Huế</em></span></p>\r\n<p style="text-align: justify;">Trong các tập du ký của thương gia phương Tây là hình ảnh phỏng theo các bữa ăn vua chúa. Bữa ăn có rất nhiều món, mỗi món người ăn chỉ nếm một vài miếng. Có thể nói các món ăn Huế là tiêu biểu cho văn minh ăn uống Việt Nam cuối thế kỷ 18 sang thế kỷ 19.</p>\r\n<p style="text-align: justify;">Món ăn Huế được chế biến công phu, tinh tế. Nhiều người cho rằng ăn các món Huế là thưởng thức cái đẹp, cảm nhận cái hồn của Huế không còn thấy cần tìm cái sự no nê. Ngay những thứ như lòng lợn, lòng bò vào tay các bà nội trợ Huế cũng trở thành những mỹ vị cao sang. Miếng cổ hữu lợn trở thành những vòng khuyên nhỏ thơm phức. Miếng lá sách bò hoá những đoá hoa trắng phau ngọt lịm, giòn mềm. Món cá kho của bình dân ba miền khi đến Huế cũng mang màu quý phái: cá bống thê lẫn thịt ba rọi, rau răm, ớt bột, tiêu, đường, nước mắm, nước màu, cho lửa liu riu và còn bí quyết gì nữa đây mà con cá kho khi lên đĩa nhìn trong suốt như hổ phách, đặt đũa lên thấy hơi cứng không vỡ nát.</p>\r\n<p style="text-align: justify;">Có hằng trăm món Huế, nhưng phổ biến rộng khắp không chỉ là những món cao cấp. Ngày nay, cả nước đều biết tiếng mắm tôm chua Huế ăn với thịt heo phay kèm khế, vả và các loại rau thơm. Bún bò Huế, cơm hến, tré, bánh lá… là những món ăn bình dân Huế nhưng ngày nay đã là món đặc sản trong thực đơn các khách sạn sang trọng. Cứ xem những món bình dân Huế cũng thấy được sự chế biến rất công phu. Nước dùng của bún bò Huế có yêu cầu giống như nước dùng của phở phải trong veo và ngọt lịm. Cái khó của nước dùng bún bò Huế là phải làm sao cho đậm vị mắm ruốc mà không hôi ruốc, điều đó đòi hỏi phải có một công đoạn riêng cho việc chế biến nước ruốc (Sách dạy nấu ăn của sở Giáo dục TP.HCM xuất bản chỉ hướng dẫn là “lọc mắm ruốc đổ vào nồi nước dùng”).</p>\r\n<p style="text-align: justify;">Cả nước đều có món mắm tôm chua, nhưng có lẽ hương vị tôm chua Huế đặc sắc hơn là vì sự chế biến qua nhiều công đoạn và gồm nhiều thứ nguyên liệu (rượu, măng vòi, riềng, tỏi, ớt, xôi nếp, nước mắm ngon…). Bánh lá Huế là thứ bánh pha lẫn hai loại bột (bột năng và bột gạo) và hai lần đưa lên bếp (bột trộn gia vị khuấy chín rồi mới đem gói bánh với nhân tôm và đưa lên xửng hấp cách thuỷ). Tôm cho bánh lá cũng được chế biến thành hai loại: tôm chà bông làm nhân bánh và chả tôm để kẹp vào chiếc bánh lá đã hấp chín…</p>\r\n<p style="text-align: justify;">Trên đây chỉ kể sơ lược sự phức tạp của các công đoạn, các thao tác, nhưng điều chủ yếu của các món ăn mang được cái hồn của Huế thì lại lệ thuộc hoàn toàn ở nghệ thuật của các bà, các chị của xứ Huế hào hoa.</p>\r\n<p style="text-align: justify;"><strong>Hoang dã và hào phóng: món ăn miền Nam</strong></p>\r\n<p style="text-align: center;"><img class="news-image" alt="Món ăn ba miền: Phong phú ẩm thực Việt Nam, Ẩm thực, am thuc ba mien, dac san hue, bun cha, mam tom, mon an mien nam" src="http://anh.24h.com.vn/upload/4-2010/images/2010-11-27/1290828675-lau-mam.jpg" /><br /> <br /> <span style="color: #0000ff;"><em>Lẩu mắm, đặc sản Nam bộ</em></span></p>\r\n<p style="text-align: justify;">“Miếng lạ miền Nam”, ông Vũ Bằng viết: “Tôi yêu miếng lạ miền Nam nhiều là vì nó lạ - lạ đến nhiều khi không thể tưởng tượng được – và chính những cái lạ đó đã cho tôi thấy rõ hơn tính chất thực thà, bộc lộ và chất phác của người Nam”. Trong sách này, Vũ Bằng kể 8 món (canh rùa, thịt chuột, khô, đuông, cháo cóc, dơi huyết, bò kiến – thịt bò cho kiến bu, tóp mỡ ngào đường) mà ông cho là những món lạ làm cho lòng “thấy thương mến miền Nam, miếng ăn của miền Nam”.</p>\r\n<p style="text-align: justify;">Thật ra, “cái lạ” chỉ là một điểm nổi lên trên cái nền hoang dã. Những người rời quê cũ lên mở cõi, khẩn hoang cùng đất mới gặp biết bao khó khăn, thiếu thốn đâu dễ gì giữ được phong vị quê xưa. Nhớ chiếc bánh đa, nhưng không có cối xay bột bánh tráng, đành phải tìm bọng cây bỏ cơm nếp (cơm nếp mềm dễ giã hơn cơm tẻ) vào giã nhuyễn cán ra thành chiếc bánh phồng. Và chính chiếc bánh phồng hoang dã này đã đẻ ra chiếc bánh phồng tôm công nghiệp ngày nay. Không có nhà cửa khang trang đặt bàn thờ ông bà để xếp lên những chiếc bánh chưng ngày tết, thôi biến chiếc bánh vuông thành chiếc bánh tét tròn và dài, cột từng đôi treo lên chạc cây rừng ở đầu nhà.</p>\r\n<p style="text-align: justify;">Mọi thứ lá rừng, cây hoang nếm thử không ngộ độc thì đều rau ăn. Có những thứ ở Bắc không ăn, nhưng vào đây trở thành rau quý: cây rau tiêu (càng cua), rau giấp cá, rau đắng, lá cóc kèn, cây ngổ đồng, rau dừa, rau mát… Mọi con vật trên rừng, trên đồng, dưới sông, ngoài biển đều là thức ăn. Thịt gà trộn với rau răm, không phải vì người miền Nam muốn tìm một “công thức” mới mà vì lá chanh ở miền Nam không thơm như ở phía Bắc lại có vị đắng. Ốc hấp lá sả là vì lá gừng miền Nam cũng không thơm như lá gừng ở phía Bắc (các nhà hàng ở Hà Nội vào Nam dự hội thi nấu ăn đã mang lá gừng miền Bắc theo để làm món ốc hấp). Nhớ món phở lắm, nhưng thời khẩn hoang trâu bò là sức kéo quý, thôi thì hẵng tìm một công thức mới là “hủ tíu” để tận dụng những nguyên liệu đạm rất dồi dào khác: thịt heo, tôm, cá…</p>\r\n<p style="text-align: justify;">Lòng người miền Nam “ngàn năm thương nhớ đất Thăng Long”, cho nên chiếc bánh mật của Bắc hà đến Quảng Nam trở vào mang tên là bánh tổ. Những ngày Tết, ngày giỗ, người miền Nam cố giữ những món truyền thống: thịt nấu măng, thịt phay, cá kho, giò chả, nem, gỏi…</p>\r\n<p style="text-align: justify;">Tuy nhiên, trong từng món cũng đều thấy có sự sửa đổi khác hơn “bản gốc”: do dùng măng tre nên thường là măng tươi, có khi là cổ hũ dừa; thịt phay ăn với mắm thái hoặc mắm nêm; thịt kho nước dừa với cá tôm và trứng vịt luộc… hoang dã và hào phóng là đặc trưng món ăn miền Nam. Cơm tay cầm, cá kho tộ, canh chua, lẩu, lẩu mắm, bánh xèo… là những món miền Nam qua thử thách của thời gian được khẩu vị cả nước chấp nhận cho là đặc sắc.</p>\r\n<p style="text-align: justify;">Cơm nấu trong nồi đất thêm tay cầm để tiện vừa ăn vừa di chuyển, có khi là di chuyển qua kênh mương, nương rẫy. Kho cá trong tộ phản ánh cuộc sống tạm bợ của cảnh sống trên nương, trên ghe, trong những gian “nhà đá” (hôm nay ở ngày mai đá sập bỏ rồi đi).</p>\r\n<p style="text-align: justify;">Miền Bắc, miền Trung đều có món canh chua, nhưng tô canh chua miền Nam khác hẳn về chất và lượng, thể hiện sự trù phú vô cùng của miền đất mới: nước thật chua, cá cắt khúc lớn, các loại quả thơm, cà chua, giá, đậu bắp các loại rau thơm và ớt thật cay.</p>\r\n<p style="text-align: justify;">Còn lẩu mắm ngày nay đã là món ăn cao cấp, người ta đã đưa lẩu mắm như đưa “hương đồng cỏ nội” vào cao lâu, khách sạn. Trong lẩu có nhiều loại cá lại thêm thịt ba rọi, ốc, mực, đậu hũ… thể hiện đầy đủ nét hoang dã và hào phóng. Đĩa rau đếm thấy hơn 20 loại gợi nhớ đĩa rau cũng rất xum xuê đa dạng cho bữa ăn gỏi cổ truyền ở xứ Bắc. Có vài loại rau tầm thường thậm chí không dùng ở xứ Bắc khi vào Nam lại gia nhập những món ăn cao cấp như khổ qua nối thịt hầm, bông bí nhồi thịt hấp. Món mắm của dân Việt vào phía Nam mới phát triển tột bậc. Ngoài những thứ mắm đã có, thêm mắm các loại cá đồng, mắm ba khía, mắm chuột, mắm ruột (ruột và trứng cá)…</p>\r\n<p style="text-align: center;"><span style="color: #0000ff;"><em><img class="news-image" alt="Món ăn ba miền: Phong phú ẩm thực Việt Nam, Ẩm thực, am thuc ba mien, dac san hue, bun cha, mam tom, mon an mien nam" src="http://anh.24h.com.vn/upload/4-2010/images/2010-11-27/1290828675-gia-vi.jpg" /><br /> <br /> Gia vị miền Nam </em></span></p>\r\n<p style="text-align: justify;">Món mứt cũng phát triển tột bậc: mứt me, mứt chùm ruột, mứt mãng cầu, mứt dừa… Cá khô cũng phát triển rất nhiều chủng loại như thế. Miền Nam chấp nhận rộng rãi các món ăn từ nước ngoài vào nhưng cái hồn Việt vẫn sâu đậm trong mọi món ăn rất dễ dàng cảm nhận.</p>\r\n<p style="text-align: justify;">Vậy đặc điểm nổi bật, tính thống nhất của các món ăn Việt Nam là gì? Trước hết,có lẽ chúng ta nên nghe ý kiến của một người nước ngoài có hiểu biết về món ăn các nước. Ông Jacques Pepin, một bậc thầy trong làng ẩm thực của Mỹ viết: “Có một sự khéo léo nhất định, một phẩm chất nhất định trong chuyện nấu ăn của người Việt Nam, khiến cho món ăn Việt Nam có một chỗ đứng riêng biệt so với các món ăn phương Đông khác: nước dùng trong, rất ít chất béo, rau thật phong phú, và rất nhiều hương vị rất đặc biệt mà chủ soái là thứ nước mắm (tác giả dùng tiếng Việt chữ nước mắm) nổi tiếng được dùng vừa để làm tăng mùi vừa để nêm và thứ lá sả thơm lừng dùng để nấu nướng và ướp thịt cá cùng cá gia vị hỗn hợp khác”.</p>\r\n<p style="text-align: justify;">Đáng khen ông thực khách đặc biệt này đã nắm được thần thái của các món ăn Việt Nam: nước dùng, nước mắm, gia vị hỗn hợp, rau phong phú.</p>\r\n<p style="text-align: justify;">Còn gì nữa? Người Việt Nam rất chú trọng nước chấm. Thịt lợn luộc phải đi với mắm (mắm tôm, mắm chua, mắm thái…) Thịt bê thui thì với tương gừng, cá lóc nướng trui thì muối ớt hoặc nước mắm ớt, cá trê nước mắm gừng; bún chả nước mắm pha… Nước chấm thì có tương và nước mắm. Ngày nay nước mắm đã đẩy tương từ địa vị “chủ soái” (tương cà gia bản) xuống hàng thứ hai, nhưng nhiều trường hợp nó vẫn rất quan trọng (phở vào Sài Gòn có thêm ba loại tương đi kèm). Khoa ẩm thực Việt Nam đã rất sớm chú trọng khoa học cân bằng giữa các chất trong việc xếp đặt các thức đi với nhau trong cùng một món ăn hoặc trong một bữa ăn. Và món măng là một món có “vai tró thống nhất” trong bữa cơm ba miền, nó có tính cổ truyền nhưng vẫn theo kịp hiện đại, xin các vua bếp quan tâm tạo cho nó một chỗ đứng xứng đáng.</p>\r\n<p style="text-align: justify;">Ơi những món ăn ba miền, những tinh hoa Việt Nam, làm sao dám nói hết chuyện muôn đời và chuyện còn muôn sau!</p>', 1, 13, 0, 58, '2012-04-02 10:35:55', 63, 'theo 24h', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-04-02 10:35:55', '0000-00-00 00:00:00', '1290828675-bun-cha-ha-noi.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 4, '', '', 0, 1, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (122, 'Top 6 thực phẩm đẩy lùi loãng xương', 'top-6-thuc-pham-day-lui-loang-xuong', '', '<h2 class="rtejustify"><span style="font-size: 12px;">Giàu canxi, vitamin D, protein và các chất chống ôxy hoá, một số thực phẩm có khả năng ngăn ngừa bệnh loãng xương và giảm mật độ xương sau tuổi 50.', '</span></h2>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p><span class="font7"><span style="font-size: x-small;" size="2">&nbsp; <strong>Ngũ cốc</strong></span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2">Với hàm lượng protein từ 8-14%, đạm thực vật trong ngũ cốc có khả năng chống lại sự “thăm hỏi” của bệnh loãng xương - giảm mật độ xương (cứ 4 phụ nữ sau tuổi 50 lại có một người bị loãng xương còn ở đàn ông tỉ lệ là 1/8).</span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2">Hãy xen kẽ vào thói quen ăn uống hàng ngày (bánh mì, bột mì, gạo…) bằng mầm lúa mì, rau quả sấy khô. Lưu ý: 100g mầm lúa mì mang đến 26g đạm, còn một nắm lúa mạch mang đến 14g đạm.</span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2"><strong>Sản phẩm từ sữa</strong></span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2">Thực phẩm hàng đầu giúp bạn phòng tránh chứng loãng xương chính là các sản phẩm từ sữa! Vì chúng mang canxi đến cho xương – thành phần chính cẩu thành nên xương.</span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2">Sau tuổi 50, cơ thể chúng ta cần khoảng 1,2g canxi mỗi ngày, dàn đều ra trong ngày với mỗi lần tối đa 0,5g. Để đảm bảo nhu cầu canxi sau tuổi 50, cần tiêu thụ lượng sản phẩm từ sữa tương đương với 0,75lít sữa/ ngày. Ví dụ: 1 cốc sữa buổi sáng - 1 hũ sữa chua buổi trưa - 1 cốc sữa vào 5h chiều - 30 đến 50g phô mai vào bữa tối.</span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p><span class="font7"><span style="font-size: x-small;" size="2"><strong>Cá hồi</strong></span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p align="center"><span class="font7"><span style="font-size: x-small;" size="2"><img alt="Cá hồi" src="http://www.tapchiamthuc.com/upload/image/camnang/tp1.jpg" align="middle" /></span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2">Với hàm lượng vitamin D dồi dào (khoảng 12 đến 20 microgram trong 100g cá) tham gia tích cực vào sự tái tạo mật độ xương, cá hồi là 1 trong 6 thực phẩm giúp bạn phòng tránh bệnh loãng xương.</span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2">Bắt đầu từ tuổi 50, lượng vitamin D cần thiết cung cấp cho cơ thể mỗi ngày là 10 microgram. Hãy tiêu thụ cá hồi 2 lần/tuần để đảm bảo nhu cầu vitamin D cho cơ thể. Ngoài ra, phơi nắng cũng giúp cơ thể hấp thụ được một lượng nhỏ vitamin D qua da.</span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2"><strong>Giá đỗ</strong></span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2">Hấp thu thức ăn làm từ đậu nành giúp giảm quá trình loãng xương ở phụ nữ sau giai đoạn mãn kinh, khi các thay đổi trong nội tiết tố của cơ thể có thể làm xương mỏng đi nhanh chóng và gia tăng nguy cơ gãy xương. Lý do là trong đậu nành có chứa phyto-oestrogen (hóc môn oestrogen thực vật), đặc biệt là isoflavon.</span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2">Trung bình, 100g giá đỗ chứa 35mg isoflavon….Tuy nhiên, chống chỉ định tiêu thụ quá 1mg isoflavon trên 1kg trọng lượng cơ thể mỗi ngày (Ví dụ, tối đa 60mg cho một phụ nữ nặng 60kg).</span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2"><strong>Chè xanh</strong></span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2">Với hàm lượng flavonoi (chất chống ôxy hoá) phong phú trong lá chè, chè xanh góp phần giảm nguy cơ loãng xương ở người cao tuổi. Thực vậy, theo các nghiên cứu khoa học, những người uống chè xanh trên 10 năm có mật độ xương dồi dào hơn những người khác.</span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2">Tuy nhiên, tránh uống quá 3 cốc nước chè/ngày vì trong chè có nhiều théin - một chất gây kích thích. Ở một số người, uống quá nhiều nước chè còn có thể gây đau đầu, thở gấp cũng như rối loạn tầm nhìn hay khó khăn về tiêu hoá.</span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p><span class="font7"><span style="font-size: x-small;" size="2"><strong>Bắp cải</strong></span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p align="center"><span class="font7"><span style="font-size: x-small;" size="2"><img alt="Bắp cải" src="http://www.tapchiamthuc.com/upload/image/camnang/tp2.jpg" align="middle" /></span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<p style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2">Bắp cải chứa vitamin K giúp tăng mật độ xương và ngăn ngừa sự rạn xương hông. Chính nhờ loại rau này, người dân đảo Okinawa ( Nhật Bản ) nằm trong nhóm những người có tuổi thọ cao nhất trên thế giới.</span></span></p>\r\n<p><span style="font-size: 12px;"></span></p>\r\n<div style="text-align: justify;"><span class="font7"><span style="font-size: x-small;" size="2">100g bắp cải mang tới 0,2mg vitamin K trong khi lượng vitamin K hàng ngày nên nạp vào cơ thể là 0,03 đến 1mg.</span></span></div>\r\n<p><span style="font-size: 12px;"><br /></span></p>', 1, 13, 0, 58, '2012-04-02 10:39:49', 63, 'Nguồn:tapchiamthuc.com', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-04-02 10:39:49', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 3, '', '', 0, 1, 0, 0, 'robots=\nauthor=');
