INSERT INTO `jos_content` (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `hot`, `special`, `systems`, `metadata`) VALUES
(1, 'Đội hợp ca của Siu Black chia tay trong nước mắt', 'doi-hop-ca-cua-siu-black-chia-tay-trong-nuoc-mat', '', '<p>Sau một tuần bình chọn kể từ đêm thi thứ 2, đêm công bố kết quả của Hợp ca tranh tài đã diễn ra vào tối thứ 6 ngày 23/3/2012, trực tiếp trên VTV3 - Đài truyền hình Việt Nam với sự chia tay của đội Siu Back.\r\n', '\r\n</p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Thời  gian luyện tập và sống chung trong ngôi nhà Hợp ca tranh tài của các  đội ngày càng dài vì vậy việc phải chia tay một đội ngày càng khó khăn  cho chính đội “ra đi” và cho các đội còn lại. Trong đêm công bố kết quả,  dù được đánh giá là có cố gắng rất nhiều và đem đến một phần trình diễn  ấn tượng trong đêm thi thứ 2 nhưng đội hợp ca Buôn Ma Thuột của ca sĩ  Siu Black vẫn phải nói lời chia tay khán giả. Đây là đội thứ 2 chia tay  chương trình sau đội hợp ca Thanh Hóa của ca sĩ Phương Thanh.</span></p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Mở  đầu đêm công bố kết quả lần thứ 2 là một phần biểu diễn kết hợp đặc  biệt của 3 đội hợp ca Huế, thành phố Hồ Chí Minh, Hà Nội. Ca khúc “Huế,  Sài Gòn, Hà Nội” của cố nhạc sĩ Trịnh Công Sơn được giám đốc âm nhạc -  nhạc sĩ Thanh Tâm phối khí lại khá ấn tượng. Ba đội hợp ca đã đại diện  cho ba miền Bắc - Trung - Nam hát vang ca khúc để tưởng nhớ cố nhạc sĩ  Trịnh Công Sơn. </span></p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;"> </span></p>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca1_e9ad7.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">Siu Black đã thoả được "ước mơ" nâng bổng của mình</span></div>\r\n<p><span style="font-size: 10pt;"> </span></p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Sau  phần biểu diễn của 3 đội hợp ca Huế - Thành phố Hồ Chí Minh - Hà Nội là  sự xuất hiện máu lửa của đội hợp ca Buôn Ma Thuột với ca khúc <span style="font-style: italic;">Ly ca phê Ban Mê</span> của nhạc sĩ Nguyễn Cường. Đây là ca khúc đã tạo nên tên tuổi ca sĩ Siu  Black và với phần thể hiện độc đáo của đội hợp ca Buôn Ma Thuột ca khúc  này lại gây bất ngờ với khán giả một lần nữa. </span></p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Trong đêm thi thứ 2, sau phần thi của đội hợp ca Sài Gòn với ca khúc <span style="font-style: italic;">Vũ điệu hoang dã</span>,  ca sĩ Siu Black đã từng thổ lộ rất mong muốn được “nâng bổng” lên giống  một thành viên nữ trong đội hợp ca này nhưng “có lẽ tôi cần giảm cân  chứ nguyên giàn nam không thể bưng tôi nổi”. Trong tiết mục biểu diễn  ngày hôm nay Siu Black đã thực sự được thỏa mãn mong ước của mình. </span></p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Đức  Tuấn tỏ ra rất hài hước khi nhận xét về phần biểu diễn của đội Buôn Ma  Thuột: “Phải công nhận tiết mục vừa rồi thể hiện sự khỏe khoắn của núi  rừng như thế nào…chúc mừng chị Siu đã có được trong tay đội hình rất  mạnh, cả nghĩa đen lẫn nghĩa bóng”.</span></p>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;"> </span></p>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca10_65ecf.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">Nhưng đội của chị đã bị loại</span></div>\r\n<p class="MsoNormal"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Tuy  nhiên, những nhận xét tốt của các ca sĩ cũng không giúp được đội của  Siu Black ở lại với cuộc thi. Những giọt nước mắt trên gương mặt những  cô gái và những cái quệt tay gạt nước mắt của những chàng trai Hợp ca  Buôn Ma Thuột khi mỉm cười hát: “Cùng nhau ta hát, hát cho cuộc đời.  Cùng nhau ta hát, hát cho ngày mai. Cùng nhau ta hát, hát cho bao đam  mê, hát cho bao khát vọng, hát cho quê hương tôi và hát cho mọi người”  đã khép lại đêm công bố kết quả đội thứ 2.</span></p>\r\n<p class="MsoNormal" style="text-align: left;"><span style="line-height: 115%; font-family: ''Times New Roman''; font-size: 12pt;">Đội  của Siu Black đã bị loại, theo BTC thì toàn bộ số tiền mà đội hợp ca  Buôn ma Thuột có được sau 2 tuần bình chọn qua cổng thông tin điện tử  nhân đạo quốc gia sẽ được dùng làm từ thiện cho các em nhỏ mồ côi, người  già neo đơn tại chùa Bửu Thắng - Buôn Ma Thuột - quê hương của “họa mi  núi rừng” và các thành viên của đội.</span></p>\r\n<div class="MsoNormal" style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca2_c256a.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div class="MsoNormal" style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">Đội của Siu Black rất tự tin sau khi hoàn thành phần thi</span></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca5_53469.jpg" border="0" width="400" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">Trong khi chờ đợi kết quảng, Siu Black tranh thủ khiêu vũ với Đức Tuấn</span></div>\r\n<div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca9_16c72.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n</div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca6_b1860.jpg" border="0" width="400" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">Đội hợp ca Buôn Ma Thuột hồi hộp đợi kết quả</span></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca3_6dc01.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca4_c5ecc.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca7_79e2d.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca8_5b383.jpg" border="0" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">Nụ cười của đội thắng</span></div>\r\n<div style="text-align: center;"></div>\r\n<div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca11_facb7.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca12_63820.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><img src="http://dantri4.vcmedia.vn/C3rvFnMsyxiSqnq5ZUj5/Image/2012/Thang-3/Hopca13_36771.jpg" border="0" align="middle" style="margin: 5px;" /></div>\r\n<div style="text-align: center;"><span style="font-family: Tahoma; font-size: 10pt;">...và nước mắt của đội thua.</span></div>\r\n</div>\r\n<p> </p>\r\n<p class="MsoNormal" style="text-align: right;"><strong>Theo Dantri.com</strong><span style="font-weight: bold;"></span></p>', 1, 4, 0, 29, '2012-03-24 13:35:56', 62, '', '2012-03-24 13:42:11', 62, 0, '0000-00-00 00:00:00', '2012-03-24 13:35:56', '0000-00-00 00:00:00', 'hopca10_65ecf.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 7, 0, 18, '', '', 0, 14, 1, 0, 0, 'robots=\nauthor='),
(2, 'Rihanna hẹn hò với Ashton Kutcher', 'rihanna-hen-ho-voi-ashton-kutcher', '', '<p>Nữ ca sỹ da màu Rihanna đã bị bắt gặp khi rời nhà Ashton Kutcher lúc nửa đêm…\r\n', '\r\n</p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;">Cách  đây ít ngày, cánh săn tin đã bắt gặp Rihanna tới nhà nam diễn viên vừa  bỏ vợ Ashton Kutcher từ tối và rời đi một cách bí mật vào lúc 4 giờ sáng  hôm sau.</span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;">Nhiều  nguồn tin cho hay, Rihanna và nam diễn viên điển trai 34 tuổi đã bí mật  hẹn hò với nhau 8 tuần nay và Rihanna mêm mẩn ngôi sao phim Yêu là  cưới.</span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></p>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;">“Rihanna  và Ashton gặp gỡ trong một bữa tiệc. Họ cho nhau số điện thoại và sau  đó thì nhắn tin và gọi điện cho nhau thường xuyên. Cách đây vài tuần, họ  hẹn hò với nhau tại một khách sạn ở Santa Monica. Họ chỉ đơn giản là  hai người độc thân bắt đầu tìm hiểu nhau,” một nguồn tin cho hay.</span></div>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"></div>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/3/4/riha2431210_e76a5.jpg" border="0" /><br /><span style="font-family: Tahoma; font-size: 10pt;">Rihanna hẹn hò bí mật với Ashton Kutcher<br /></span></span></div>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;">“Rihanna  nói với bạn bè rằng Ashton Kutcher là người hài hước và thú vị. Tuy  nhiên, lúc này cả hai người ấy đều quá bận rộn nên chưa tính tới chuyện  nghiêm túc hơn. Rihanna cũng đang bắt đầu theo đạo Kabbalah cùng Ashton  Kutcher.”</span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;">Một  nguồn tin khác thân cận với Ashton Kutcher lại nói, nam diễn viên 34  tuổi chưa muốn công khai chuyện tình rộng rãi vì không muốn sức khỏe của  vợ cũ Demi Moore bị ảnh hưởng khi mà cô chỉ vừa trở về từ trung tâm cai  nghiện và điều trị sức khỏe.</span></p>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></p>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;">Demi  Moore suy sụp nặng nề sau khi Ashton Kutcher bị tố ngoại tình. Kể từ  đó, cô phải đi điều trị tâm lý và sức khỏe sau vụ đi cấp cứu hồi tháng  1.</span></div>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"></div>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/3/4/riha2431211_8c1cb.jpg" border="0" /></span></div>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: Tahoma; font-size: 10pt;">Ashton - Demi ngày còn hạnh phúc</span></div>\r\n<div class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></div>\r\n<p class="MsoNormal" style="line-height: normal; margin: 0in 0in 0pt;"><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt;"> </span></p>\r\n<p style="text-align: right;"><strong>Theo Dantri.com</strong><span style="font-family: ''Times New Roman'',''serif''; font-size: 12pt; font-weight: bold;"></span></p>', 1, 4, 0, 29, '2012-03-24 13:50:34', 62, '', '2012-03-24 13:53:13', 62, 0, '0000-00-00 00:00:00', '2012-03-24 13:50:34', '0000-00-00 00:00:00', 'riha2431210_e76a5.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 17, '', '', 0, 2, 1, 0, 0, 'robots=\nauthor='),
(3, 'Candice Swanepoel khoe vóc dáng siêu quyến rũ', 'candice-swanepoel-khoe-voc-dang-sieu-quyen-ru', '', '<p>Thiên thần của Victoria''s Secret Candice Swanepoel mặc gì cũng đẹp...\r\n', '\r\n</p>\r\n<div><span style="font-family: Tahoma; font-size: 10pt;"> </span></div>\r\n<div><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331210_d25d8.jpg" border="0" /><br />Candice Swanepoel quảng cáo sản phẩm mới của Victoria''s Secret tại Dallas </span></div>\r\n<div><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331211_df151.jpg" border="0" /><br />Siêu mẫu Nam Phi diện váy trắng hiệu Herve Leger </span></div>\r\n<div><span style="font-family: Tahoma; font-size: 10pt;"> </span></div>\r\n<div><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331212_53254.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></span></div>\r\n<div><span style="font-family: Tahoma; font-size: 10pt;">Chân dài 24 tuổi dành nhiều thời gian chụp hình với người hâm mộ</span></div>\r\n<div><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331213_60580.jpg" border="0" /><br />Candice Swanepoel khoe vóc dáng siêu quyến rũ</span></div>\r\n<div><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331214_b7e5d.jpg" border="0" /><br />Siêu mẫu tóc vàng tự tin và tạo dáng rất thoải mái</span></div>\r\n<div><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331215_1b5dd.jpg" border="0" /><br /><br /><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331216_8d004.jpg" border="0" /><br />Candice Swanepoel khoe những sản phẩm mới của Victoria''s Secret <br /></span></div>\r\n<div><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331217_4d6d9.jpg" border="0" width="450" align="middle" style="margin: 5px;" /></span></div>\r\n<div><span style="font-family: Tahoma; font-size: 10pt;">Trước đó ít ngày Candice Swanepoel và Erin Heatherton quảng cáo sản phẩm mới của Victoria''s Secret tại New York </span></div>\r\n<div><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331218_c3a05.jpg" border="0" /><br />Hai chân dài đình đám diện váy đen </span></div>\r\n<div><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331219_ebb4c.jpg" border="0" /><br />Candice Swanepoel với vẻ đẹp ngọt ngào</span></div>\r\n<div><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331220_9edec.jpg" border="0" /><br />Candice Swanepoel rực rỡ trong ảnh quảng cáo mới của Victoria''s Secret </span></div>\r\n<p><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331221_d11e2.jpg" border="0" /><br /><br /><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/candi2331222_fbf47.jpg" border="0" /></span></p>\r\n<p style="text-align: right;"><span style="font-family: Tahoma; font-size: 10pt;"><strong>Theo Dantri.com</strong><br /></span></p>\r\n<p> </p>', 1, 4, 0, 29, '2012-03-24 13:57:24', 62, '', '2012-03-24 13:59:06', 62, 0, '0000-00-00 00:00:00', '2012-03-24 13:57:24', '0000-00-00 00:00:00', 'candi2331210_d25d8.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 16, '', '', 0, 0, 1, 0, 0, 'robots=\nauthor='),
(4, 'Vòng quanh thế giới giải trí ngày 24/3', 'vong-quanh-the-gioi-giai-tri-ngay-243', '', '<p>Victoria Beckham khoe tóc mới khi đi mua sắm tại đại lộ Melrose; Miranda Kerr tại sân bay Los Angeles; 1 ngày sau khi bị ném bột vào người, cô Kim Kardashian lại xuất hiện chỉn chu tại Woodland Hills...', '</p>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/vic2431210_31582.jpg" /></span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Victoria Beckham khoe tóc mới khi đi mua sắm tại đại lộ Melrose ở Hollywood</span></div>\r\n<div align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/vic2431211_83a8a.jpg" /><br />Siêu mẫu Irina Shayk đi tắm biển cùng bạn bè tại Miami, Florida </span></div>\r\n<div align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/vic2431212_0d1e1.jpg" /><br />Siêu mẫu Alessandra Ambrosio khoe vóc dáng hoàn hảo dù đang mang thai khi đi quảng cáo cho sản phẩm mới của Philips tại Los Angeles, California </span></div>\r\n<div align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/vic2431213_2cf01.jpg" /><br />Gwen Stefani đón con trai Kingston từ trường học về nhà tại Beverly Hills, California </span></div>\r\n<div align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/vic2431214_79569.jpg" /><br />1 ngày sau khi bị ném bột vào người, cô Kim Kardashian lại xuất hiện chỉn chu tại Woodland Hills </span></div>\r\n<div align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/vic2431215_36e51.jpg" /><br />Kelly Brook xinh tươi trên đường phố London</span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/vic2431216_f0c09.jpg" /></span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Jennifer Lawrencemua cà phê tại cửa hiệu Whole Foods ở Santa Monica</span></div>\r\n<div align="center"><br /><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/vic2431217_94e9b.jpg" /><br />Miranda Kerr tại sân bay Los Angeles và bế theo con trai cưng Flynn</span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/03/vic2431219_ad2b7.jpg" /><br />Lily Cole, đại diện mới của The Body Shop, quảng cáo cho các sản&nbsp;phẩm của hãng ở London</span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/hBru1dLDczzwdZ6qEvP0/Image/2012/3/4/vic2431220_27782.jpg" /><br />outzen Kroes cùng chồng DJ Sunnery James và con trai Phyllon đi dạo trên bãi biển Miami <br /></span></div>\r\n<div align="right"><span style="font-family: Tahoma; font-size: 10pt; font-weight: bold;">Theo Dantri.com<br /></span></div>', 1, 4, 0, 29, '2012-03-24 14:02:26', 62, '', '2012-03-24 14:04:27', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:02:26', '0000-00-00 00:00:00', 'vic2431220_27782.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 15, '', '', 0, 0, 0, 0, 0, 'robots=\nauthor='),
(5, '“Gái hai con” Lưu Đào trẻ trung tới bất ngờ!', 'gai-hai-con-luu-dao-tre-trung-toi-bat-ngo', '', '<p>Nhận lời làm MC cho buổi tiệc gây quỹ từ thiện giúp đỡ những người nghèo gặp hoàn cảnh khó khăn tại Bắc Kinh, nữ diễn viên Lưu Đào khiến tất cả mọi người phải ngỡ ngàng bởi sự trẻ trung và đáng yêu của mình.', '</p>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24031_70909.jpg" /></span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Lưu Đào là một trong những nhân vật trung tâm của buổi tiệc gây quỹ từ thiện tại Bắc Kinh, hôm 23/3.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24032_4f43c.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24033_1368b.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Cô ăn mặc và làm tóc rất trẻ trung, khiến nhiều đồng nghiệp và giới truyền thông bất ngờ.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24034_b3b49.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Lưu Đào kêu gọi mọi người quan tâm tới những đứa trẻ mồ côi, sinh trưởng trong điều kiện khó khăn bởi "trẻ em vốn là những thiên thần nhỏ và xứng đáng được hưởng những điều tốt đẹp nhất trong cuộc sống này".</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24036_1a708.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Bà mẹ hai con cho biết, với cô, hai đứa con là những món quà quý giá nhất mà cuộc sống ban tặng cho vợ chồng cô.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24037_b32a2.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Chính bởi vậy, vì các con, cô sẵn sàng hi sinh sự nghiệp riêng để không bỏ lỡ bất kỳ bước trưởng thành nào của các con.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24038_3682b.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao24039_98bbb.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Trước đó, Lưu Đào từng xuất hiện rất gợi cảm với váy khoe lưng trần tại một sự kiện khác ở Thượng Hải.</span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240310_01e6e.jpg" /></span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240311_99053.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">"Nàng A Châu" của <span style="font-style: italic;">Thiên long bát bộ</span>ngày một đẹp và đằm thắm hơn!</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240312_029f8.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Lưu Đào bất ngờ kết hôn vào năm 2008 với một trong những đại gia của Trung Quốc đại lục.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240313_80d82.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240314_8018b.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Kể từ khi lập gia đình, cô tạm gác sự nghiệp diễn xuất riêng để tập trung chăm sóc người thân và sinh liền cho ông xã hai đứa con, một gái và một trai.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240315_ffe02.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240316_35052.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240317_caa1b.jpg" /></span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><span style="font-family: Tahoma; font-size: 10pt;">Bắt đầu từ năm 2011, Lưu Đào bắt đầu trở lại với một số hoạt động của làng giải trí và góp mặt trong một số bộ phim truyền hình.</span></span>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240318_7ac11.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240319_10ec7.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240320_0d6c2.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240321_f191a.jpg" /></p>\r\n<p><img src="http://dantri4.vcmedia.vn/kvQpnUqsv918uIS3qnCP/Image/2012/03/f2/dao240322_aa106.jpg" /></p>\r\n</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Tuy nhiên, Lưu Đào luôn khẳng định, sự nghiệp điện ảnh giờ không còn là ưu tiên số một của cô, bởi gia đình nhỏ nơi có những đứa con xinh xắn, đáng yêu và một ông xã tốt bụng hết lòng chiều chuộng vợ mới là điều cô trân trọng nhất. Cuộc sống gia đình viên mãn khiến Lưu Đào lúc nào cũng tươi tắn và trẻ trung.</span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<div align="right"><span style="font-family: Times New Roman; font-size: 12pt; font-weight: bold;">Theo Dantri.com<br /></span></div>', 1, 4, 0, 29, '2012-03-24 14:06:06', 62, '', '2012-03-24 14:10:58', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:06:06', '0000-00-00 00:00:00', 'dao24039_98bbb.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 14, '', '', 0, 5, 1, 0, 0, 'robots=\nauthor='),
(7, 'Cận cảnh nhan sắc Việt', 'can-canh-nhan-sac-viet', '', '<p>Ánh mắt, bờ môi, sóng mũi trên gương mặt của Hoa hậu Diễm Hương, diễn viên múa Linh Nga, ca sĩ Mai Khôi... được nhiếp ảnh Hà Sơn Minh ''zoom'' thật sát. Màu đen trắng được sử dụng càng làm các bức ảnh thêm bắt mắt.', '</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/diem-huong.jpg" border="1" height="317" width="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nhiếp ảnh gia Hà Sơn Minh (bút danh minHa) kết hợp stylist Nguyễn Bình Yên thực hiện nhiều bộ ảnh cho các người đẹp Việt suốt một năm qua. Dịp cuối năm, cả hai quyết định tổng hợp một số gương mặt mà họ chộp được những góc "xuất thần". Trong ảnh là một góc lạ của Hoa hậu Thế giới Người Việt Diễm Hương.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/phung-ngoc-yen.jpg" border="1" height="350" width="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Màu ảnh trắng đen giúp nổi bật những nét thanh tú trên gương mặt của các nhan sắc Việt. Một vài bức ảnh được êkíp chụp tự nhiên, không hề sắp đặt tạo nên độ biểu cảm lớn. Bức ảnh ghi lại một góc nhìn "sắc lẹm" của người mẫu Phùng Ngọc Yến.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/ngoc-quyen.jpg" border="1" height="345" width="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Người mẫu Ngọc Quyên.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/truong-thi-may.jpg" border="1" height="589" width="420" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Vẻ ưu tư của Á hậu dân tộc Trương Thị May.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/mai-khoi.jpg" border="1" height="607" width="420" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ca sĩ Mai Khôi luôn toát lên vẻ gợi cảm trong mọi tư thế.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/kim-thu.jpg" border="1" height="610" width="420" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một bức ảnh "chộp" bà chủ nhà hàng Biển Nhớ bất ngờ. Tóc bay tự nhiên, miệng cười mỉm cũng tự nhiên làm Kim Thư trông thật đằm thắm.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/hien-thuc.jpg" border="1" height="327" width="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ca sĩ Hiền Thục.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/44/CE/linh-nga.jpg" border="1" height="340" width="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Diễn viên múa Linh Nga.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style="text-align: right;"><strong>Theo Vnexpress.net</strong></p>', 1, 4, 0, 29, '2012-03-24 14:19:48', 62, '', '2012-03-24 14:21:05', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:19:48', '0000-00-00 00:00:00', 'linh-nga.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 12, '', '', 0, 4, 1, 0, 0, 'robots=\nauthor='),
(8, 'Thế giới bóng đá tiếp tục chia tay một cầu thủ vì…đột quỵ', 'the-gioi-bong-da-tiep-tuc-chia-tay-mot-cau-thu-vidot-quy', '', '<p>Khi mà những thông tin về vụ đột quỵ của tiền vệ CLB Bolton, Muamba vẫn còn rất nóng hồi thì thế giới bóng đá tiếp tục phải đón nhận tin buồn khi cầu thủ Venkatesh (Bangalore Mars) đã qua đời sau cơn đau tim ngay trên sân cỏ.', '</p>\r\n<p>Tiền vệ Muamba (Bolton) đang phải chiến đấu với sự sống nhưng anh còn may mắn hơn rất nhiều người đồng nghiệp Venkatesh (Bangalore Mars) ở đất nước Ấn Độ. Theo báo giới ở đất nước này, Venkatesh đã qua đời ngay trên sân bóng sau một cơn đau tim.</p>\r\n<p>&nbsp;</p>\r\n<div style="text-align: center;"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/uajiKupQ6reCuKKDlVlG/Image/2012/03/an-do_b23df.jpg" _fl="" width="450" align="middle" /><br /><span style="font-family: Tahoma; font-size: 10pt;">Venkatesh đã không may mắn như Muamba</span></div>\r\n<p>Nói Muamba may mắn là bởi lẽ anh được sự chăm sóc nhiệt tình, chuyên nghiệp của những nhân viên y tế ở trên sân, còn Venkatesh thì không. Với tình trạng thiết bị nghèo nàn lạc hậu cũng như trình độ bác sĩ ở Ấn Độ đã không thể giúp Venkatesh vượt qua cơn nguy kịch.</p>\r\n<p>Venkatesh đã qua đời ngay trên chiếc xe cứu thương thô sơ, quá tồi tàn. Cái chết của cầu thủ người Ấn Độ này tiếp tục là thông tin buồn với bóng đá thế giới, chỉ trong vòng chưa đầy 1 tuần đã xảy ra tới 2 vụ đột quỵ vô cùng nghiêm trọng.</p>\r\n<p>Venkatesh không phải cầu thủ đầu tiên qua đời ngay trên sân cỏ Ấn Độ, còn nhớ, 7 năm trước họ đã đau buồn tiễn đưa cầu thủ người Brazil, Cristioano Junior của CLB Dempo Sports Club. Năm ngoái thủ thành Arun Kumar cũng qua đời sau chấn thương trong 1 buổi tập.</p>\r\n<p>Nói về sự kiện này, một CĐV Ấn Độ đau xót đề cập tới trang thiết bị của những sân bóng quê nhà: “Sự việc này đã phơi bày tình trạng thảm hại của những sân bóng Ấn Độ, không phải tới sau khi Venkatesh qua đời, người ta mới biết đến điều này. Không có một bác sĩ ở trên sân, cũng không xe cứu thương, mọi chuyện đâu chỉ đơn giản là túi đá trườm và thuốc giảm đau”.</p>\r\n<p style="text-align: right; font-weight: bold;">Theo Dantri.com</p>\r\n<input id="gwProxy" type="hidden" /><!--Session data--><input jscode="leoInternalChangeDone()" onclick="if(typeof(jsCall)==''function''){jsCall();}else{setTimeout(''jsCall()'',500);}" id="jsProxy" type="hidden" />\r\n<div id="refHTML">&nbsp;</div>', 1, 7, 0, 38, '2012-03-24 14:23:08', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-03-24 14:23:08', '0000-00-00 00:00:00', 'an-do_b23df.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 5, '', '', 0, 0, 0, 0, 0, 'robots=\nauthor='),
(6, 'Phan Huỳnh Điểu xúc động vì thí sinh Tiếng hát mãi xanh', 'phan-huynh-dieu-xuc-dong-vi-thi-sinh-tieng-hat-mai-xanh', '', '<p>Làm giám khảo cuộc thi hát cho người trung, cao niên, Phan Huỳnh Điểu suýt khóc khi nghe thí sinh Nguyễn Thị Ngọc Mỹ thể hiện ''Tình ca cho em'', bài hát khiến ông thấy nhạc sĩ quá cố Nguyễn Nam kề bên.', '</p>\r\n<p class="Normal">Đêm chung kết đầu tiên, chủ đề "Dòng nhạc trữ tình, lãng mạn" của cuộc thi Tiếng hát mãi xanh diễn ra tối 23/3 tại Nhà hát truyền hình, TP HCM với sự tham gia của hơn 500 khán giả.</p>\r\n<p class="Normal">Khi thí sinh Nguyễn Thị Ngọc Mỹ, 37 tuổi, đến từ Bến Tre hoàn thành nhạc phẩm <em>Tình ca cho em</em> của Nguyễn Nam, nhạc sĩ Phan Huỳnh Điểu rưng rưng nói lời cảm ơn vì chị hát rất cảm xúc, rất đạt bài hát trữ tình. Phan Huỳnh Điểu nói, khi nghe Ngọc Mỹ hát, ông thấy như Nguyễn Nam vẫn còn đang ngồi đây, kề bên mọi người. Tháng 10/2011, tác giả <em>Tình ca cho em</em> <a href="http://vnexpress.net/gl/van-hoa/guong-mat-nghe-sy/2011/10/nhac-si-nguyen-nam-qua-doi-vi-ung-thu/" class="Normal">qua đời ở tuổi 60</a> vì bệnh ung thư, để lại niềm thương nhớ trong bạn bè và khán giả.</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3b/bd/47/65/ngoc-myto.jpg" alt="Thí sinh Ngọc Mỹ có hát &quot;Tình ca cho em&quot; khiến mọi người đều xúc động. Ảnh: Lý Võ Phú Hưng." border="1" height="682" width="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Thí sinh Ngọc Mỹ hát "Tình ca cho em" (nhạc Nguyễn Nam, lời Nguyễn Nam - Phan Vũ)khiến mọi người đều xúc động. Ảnh: <em>Lý Võ Phú Hưng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">NSND Trần Hiếu cũng dành nhiều lời khen ngợi Ngọc Mỹ. Ông nhận xét, thí sinh này hát rất có hồn, trau chuốt. Nhờ chiếm cảm tình của khán giả và giám khảo, chị đã đoạt giải "Thí sinh thể hiện dòng nhạc trữ tình - lãng mạn xuất sắc" ở đêm này. Khi kết quả được công bố, không chỉ người nhà của thí sinh bày tỏ sự vui mừng mà mọi người trong khán phòng đều vỗ tay tán thưởng.</p>\r\n<p class="Normal"><span style="color: #3f3f3f;" color="#3f3f3f"><strong>Nhiều thí sinh khác của đêm chung kết một cũng để lại cho khán giả và ban giám khảo cảm xúc, ấn tượng khó quên.</strong></span></p>\r\n<p class="Normal">Cùng bảng C (độ tuổi từ 35 đến 50) với Ngọc Mỹ là Thái Thanh Hiệp (37 tuổi) và Trang Thị Mai Hương (49 tuổi). Khi lên sân khấu, họ hát hết sức mình, đúng với tiêu chí của chương trình là "hát cho thỏa đam mê".</p>\r\n<p class="Normal">Khi Thái Thanh Hiệp xuất hiện, anh thu hút khán giả vừa bởi vóc dáng điển trai, cao ráo, vừa với chất giọng khỏe khi trình bày ca khúc <em>Anh còn nợ em </em>(thơ Phan Thanh Tài, nhạc Anh Bằng). Giám khảo Văn Thị Minh Hương, Giám đốc Nhạc viện TP HCM nói đùa, nghe thí sinh này hát là biết anh còn nợ nần quá nhiều. Tiến sĩ Văn Thị Minh Hương sẵn sàng nhận Thái Thanh Hiệp vào khoa Thanh nhạc của Nhạc viện dù anh quá độ tuổi để được nhận vào trường. Theo chị, khi anh vừa cất tiếng hát, cả ban giám khảo đều bất ngờ với chất giọng khỏe khoắn và ngọt ngào...</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3b/bd/47/65/thai-thanh-hiepto.jpg" alt="Thí sinh Thái Thanh Hiệp là người có điểm số cao nhất đêm chung kết một cuộc thi &quot;Tiếng hát mãi xanh&quot;: 19,73 điểm." border="1" height="655" width="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Thí sinh Thái Thanh Hiệp là người nhận điểm số trung bình từ ban giám khảo cao nhất đêm chung kết một cuộc thi Tiếng hát mãi xanh: 19,73 điểm. Ảnh: <em>Lý Võ Phú Hưng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Bên cạnh lời khen, các giám khảo cũng có những góp ý chân tình để người thi hoàn thiện mình hơn. Với bài hát <em>Mơ về nơi xa lắm</em> (Phú Quang) do thí sinh Trang Thị Mai Hương, 49 tuổi, đến từ An Giang thể hiện, NSƯT Ánh Tuyết cho rằng, chị đã hát nhạc phẩm này với sự trìu mến nhưng chất giọng hơi bị căng thẳng. "Đây là một bài hát trữ tình, tình cảm không nên quá nặng nề mà nên bay bổng, nhẹ nhàng hơn", nữ giám khảo nhận xét.</p>\r\n<p class="Normal">Bảng A (từ 66 tuổi trở lên) cụ Nguyễn Thị Ngọc Thanh, 75 tuổi, khuấy động đêm thi khi nhảy tango, hát <em>Bóng chiều tà</em> của Anh Bằng trên sân khấu, còn khán giả ở dưới thì vỗ tay làm nhịp át cả ban nhạc. Giám khảo, nhạc sĩ Phan Huỳnh Điểu (năm nay 88 tuổi) vui vẻ khen: "Giá mà bà xã tôi hát hay như cô thì tôi còn sáng tác được nhiều bài hay lắm". Thành viên khác của ban giám khảo là NSƯT Ánh Tuyết cũng không tiếc lời: “Khi cô bước ra trông đã thật đẹp rồi, lúc cất giọng thì hay quá, đến khi nhảy thì lại càng duyên dáng và tuyệt vời”.</p>\r\n<p class="Normal">Khi thể hiện ca khúc <em>Lệ đá</em> (thơ Hà Huyền Chi, nhạc Trần Trịnh), thí sinh Dương Văn Vá (68 tuổi), cũng được giám khảo, NSND Trần Hiếu chúc mừng: "Giọng anh rất sướng, không phải giọng người già mà là giọng trẻ!". Còn giám khảo Văn Thị Minh Hương thì xuýt xoa: “Chất giọng của chú những người chuyên nghiệp còn ước ao mà có... Xin chú cứ tự tin ở những đêm thi tiếp theo”.</p>\r\n<table style="width: 1px;" border="0" cellpadding="3" cellspacing="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3b/bd/47/65/9-thi-sinhto.jpg" alt="9 thí sinh vòng chung kết cuộc thi &quot;Tiếng hát mãi xanh&quot; mang lại cho khán giả đêm thi nhiều cảm xúc." border="1" height="296" width="495" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">9 thí sinh vòng chung kết cuộc thi Tiếng hát mãi xanh mang lại cho khán giả đêm thi nhiều cảm xúc. <em>Ảnh: Lý Võ Phú Hưng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ở độ tuổi "trung lập" của cuộc thi, các thí sinh bảng B (từ 51 đến 65 tuổi) cũng không kém cạnh. Chị Trần Thị Kim Thoa, 53 tuổi, xuất thân từ gia đình có cụ thân sinh là nghệ sĩ kèn. Không là ca sĩ chuyên nghiệp, nhưng nhờ ảnh hưởng từ cha, chị có dáng dấp một nghệ sĩ và giọng hát mượt mà, đằm thắm. Chị thể hiện cảm xúc ca khúc <em>Cô đơn</em> (Nguyễn Ánh 9). Dù vậy, có lẽ do căng thẳng, chị hát chưa thật đúng phong độ của mình như ở các vòng bán kết. Nhiều khán giả hy vọng, ở các đêm thi tới, chị Kim Thoa sẽ bứt phá hơn. Hai thí sinh Trần Thị Thu Thủy (54 tuổi) và Dương Xuân Chánh (57 tuổi) cũng để lại nhiều rung cảm tươi trẻ nơi người xem khi lần lượt thể hiện ca khúc <em>Lâu đài tình ái</em> (Trần Thiện Thanh) và <em>Phượng hồng</em> (nhạc Vũ Hoàng, thơ Đỗ Trung Quân).</p>\r\n<p class="Normal">Sau đêm chung kết một, các thí sinh sẽ đến với đêm thi thứ hai - dòng nhạc truyền thống cách mạng và các ca khúc mang âm hưởng dân ca, diễn ra vào 20h30 ngày 30/3 và đêm ba - đêm các thí sinh tự thể hiện mình, diễn ra vào lúc 20h30 ngày 6/4, diễn ra tại Nhà hát truyền hình HTV, truyền hình trực tiếp trên HTV7.</p>\r\n<p style="text-align: right;" class="Normal"><strong>Theo Vnexpress.net</strong></p>', 1, 4, 0, 29, '2012-03-24 14:12:33', 62, '', '2012-03-24 14:17:14', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:12:33', '0000-00-00 00:00:00', 'ngoc-myto.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 13, '', '', 0, 0, 1, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `hot`, `special`, `systems`, `metadata`) VALUES
(9, 'Gọi tên Messi', 'goi-ten-messi', '', '<p>Trong các môn thể thao, những người chơi giỏi vào hàng kiệt xuất sẽ được gọi là vĩ đại. Thông thường thì để được gọi là vĩ đại, người chơi sẽ phải tích lũy trong các một quá trình dài, nhưng điều đáng ngạc nhiên Messi mới 24 tuổi đã đạt được kết quả ấy...', '</p>\r\n<div style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Lionel Messi - Bóng đá</span></div>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Messi_7a936.jpg" _fl="" width="450" align="middle" /></div>\r\n<div style="margin: 0cm 0cm 0pt;" align="left">&nbsp;24 tuổi, Messi đã trở thành người ghi bàn nhiều nhất trong lịch sử Barcelona với 234 bàn thắng. Nhìn những gì mà Messi giành được, từ các giải thưởng cùng CLB đến cá nhân thì người hâm mộ chẳng thể nói anh là một cầu thủ xuất sắc nữa, mà chỉ có thể nói rằng giờ đây anh đã là một huyền thoại sống. Và rất may hiện nay những người hâm mộ bóng đá vẫn được chứng kiến cầu thủ vĩ đại này thi đấu.</div>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Muhammad Ali - Quyền Anh</span></p>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center"><img src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Ali_4fe11.jpg" _fl="" width="450" align="middle" /></div>\r\n<p style="margin: 0cm 0cm 0pt;">Muhammad Ali chẳng giống một ngôi sao quyền anh nào trong thời đại của ông. Ông trở thành tay đấm vĩ đại của môn thể thao này nhờ cú hat-trick vô địch quyền anh hạng nặng thế giới, thời còn thi đấu Ali đã tham gia rất nhiều trận đấu nổi tiếng và thu hút được đông đảo sự quan tâm của dư luận. Tầm ảnh hưởng của Ali đã vượt qua môn quyền anh và giúp ông có tiếng nói cho phong trào đòi dân quyền (cho người da đen) tại Mỹ ở thập niên 60 thế kỷ trước.</p>\r\n<div style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Usain Bolt - Điền kinh </span></div>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Bolt_5ecba.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<p style="margin: 0cm 0cm 0pt;">Những gì mà Bolt thể hiện trên đường chạy 100 mét khiến cả thế giới phải “kinh hoàng”, bởi không ai nghĩ rằng con người có thể đạt tới điều đó. Hiện nay, Bolt đang nắm giữ cả 2 kỷ lục tại đường chạy 100m (9 giây 58) và 200m (19 giây 19), những kỷ lục mà có lẽ chẳng biết đến bao giờ mới có người phá vỡ. Quả thật với tuổi 25, cũng khá trẻ như Messi, Bolt đã khiến cả thế giới phải ngưỡng mộ anh.</p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Roger Federrer - Tennis</span></p>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Federer_25e0f.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<div style="margin: 0cm 0cm 0pt;">Trong làng banh nỉ, có rất nhiều&nbsp;người chơi giỏi, giành nhiều danh hiệu. Nhưng chắc chắn nếu nói đến các tay vợt nam, người hâm mộ sẽ nghĩ đến ngay Roger Federrer, người đang có được 16 danh hiệu Grand Slam và 41 danh hiệu Master. Bởi thế người hâm mộ mới đặt cho anh biệt danh “Tàu tốc hành”</div>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Wayne Gretzky - Hockey trên băng</span></p>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/GRETZKY_3f40e.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<div style="margin: 0cm 0cm 0pt;">Chẳng phải ngẫu nhiên mà biệt danh của Wayne Gretzky lại là “Người vĩ đại”. Sự nghiệp của cầu thủ Canada này kéo dài tới 2 thập kỷ và trong quãng thời gian thi đấu, ông đã xô đổ mọi kỷ lục ở môn thể thao này. Dù đã giải nghệ vào cuối thập niên 90 thế kỷ trước, nhưng hiện tại kỷ lục ghi nhiều bàn thắng nhất tại giải NHL của ông vẫn chưa có người xô đổ được.</div>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Michael Jordan - Bóng rổ</span></p>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Jordan_dabfa.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<div style="margin: 0cm 0cm 0pt;">Tên tuổi của Jordan chắc chắn đã vượt qua ngoài môn bóng rổ. Với sự xuất sắc trong lối chơi, giành 6 cúp vô địch NBA (giải bóng rổ nhà nghề Mỹ) và 5 lần được NBA MVP vinh danh, Jordan khiến cả thế giới phải ngưỡng mộ anh.</div>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Jonad Lomu – Bóng bầu dục</span></p>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Lomu_a1e3c.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<div style="margin: 0cm 0cm 0pt;">Người đàn ông cao 1m96 nặng 125 kilogam trở thành ngôi sao toàn cầu đầu tiên của môn bóng bầu dục, sự trình diễn của anh tại giải Vô địch thế giới năm 1995 được tổ chức tại New Zealand được mô tả khiến tất cả người xem phải bật dậy. Trong Lomu là sự pha trộn giữa cả sức mạnh và tốc độ, nên người hâm mộ đặt cho anh biệt danh là “quái vật”.</div>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Michael Phelp - Bơi</span></p>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Phelp_559c3.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<div style="margin: 0cm 0cm 0pt;">Phelp là tay bơi vĩ đại nhất trong lịch sử Olympic, khi anh giành tổng cộng đến 14 huy chương vàng tại các giải đấu của sân chơi này. Ở Athens Phelp giành tới 6 chiếc huy chương vàng, 4 năm sau tại Bắc Kinh anh giành tới 8 chiếc huy chương vàng, phá vỡ kỷ lục của Mark Spitz trước kia giành 7 HCV tại kỳ ĐH Olympic. Với Olympic 2012 sắp diễn ra, người hâm mộ đang chờ đợi Phelp phá kỷ lục người giành nhiều HCV Olympic nhất là 18 tấm của VĐV thể dục người Liên Xô trước đây, Larisa Latynina.</div>\r\n<div style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Michael Schumicher - F1</span></div>\r\n<div style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;"></span>&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Schumi_94677.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<p style="margin: 0cm 0cm 0pt;">Với 7 chiếc cúp vô địch trên đường đua F1, trong đó có một loạt 5 chức vô địch liên tiếp từ năm 2000 - 2004 đã đủ nói lên sự thống trị của tay đua người Đức khi còn gắn bó với Ferrari, con số trên đã đủ đưa Schumi lên hàng tay đua vĩ đại nhất trong lịch sử môn thể thao này.</p>\r\n<div style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Sachin Tendulkar - Cricket</span></div>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Tendukar_c9383.jpg" _fl="" width="450" align="middle" /></div>\r\n<div style="margin: 0cm 0cm 0pt;" align="left">Biệt danh của Tendulkar là “tiểu sư phụ”. Anh có trận ra mắt đầu tiên ở đội tuyển Ấn độ khi mới chỉ 16 tuổi và giờ đây Tendulkar chuẩn bị bước sang tuổi 39. Phá vỡ mọi kỷ lục trong quãng thời gian dài thi đấu, và chỉ mới tuần trước Tendulkar trở thành VĐV trên thế giới đạt mốc 100 trận thi đấu quốc tế.</div>\r\n<div style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Tiger Woods - Golf</span></div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Woods_271bc.jpg" _fl="" width="450" align="middle" /></div>\r\n</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="left">Sự thống trị của Tiger Woods ở môn Golf suốt một thập kỷ qua khiến anh trở thành tay golf vĩ đại nhất thế giới của môn thể thao này. Và có lẽ nếu không có những scandal tình ái đã lấy đi mất phong độ của Woods thì không hiểu anh sẽ còn thống trị vị trí số 1 của môn thể thao này đến bao giờ.</div>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<p style="text-align: right;"><span style="font-weight: bold;">Theo Dantri.com<br /></span></p>', 1, 7, 0, 38, '2012-03-24 14:25:25', 62, '', '2012-03-24 14:26:26', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:25:25', '0000-00-00 00:00:00', 'messi_7a936.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 4, '', '', 0, 0, 1, 0, 0, 'robots=\nauthor='),
(10, 'Chùm ảnh biếm họa Thierry Henry: “Tượng đài sống”', 'chum-anh-biem-hoa-thierry-henry-tuong-dai-song', '', '<p>Vừa mới đây Henry bất ngờ trở Anh từ Mỹ để thăm người đồng đội cũ Muamba bị đột quị, điều đó cho thấy nhân cách lớn trong con người của Titti, một tượng đài bóng đá thực sự trong lòng những người hâm mộ Arsenal và nước Pháp.', '</p>\r\n<div align="left"><span style="font-weight: bold;">Tượng đài sống của "Pháo thủ"</span></div>\r\n<div align="left">&nbsp;</div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA7_a66c7.jpg" _fl="" width="450" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA2_f43dd.jpg" _fl="" width="375" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA3_496ba.jpg" _fl="" width="300" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA4_bcd7a.jpg" _fl="" width="300" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA5_f58a7.jpg" _fl="" width="299" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA6_50ea3.jpg" _fl="" align="middle" /></div>\r\n<div align="center"><img src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA1_3941a.jpg" _fl="" width="300" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryA9_9105e.jpg" _fl="" width="350" align="middle" /></div>\r\n<div align="left">&nbsp;</div>\r\n<div align="left"><span style="font-weight: bold;">Cùng Barcelona chinh phục các đỉnh cao mới như Champions League</span></div>\r\n<div align="center">&nbsp;</div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryB1_b2702.jpg" _fl="" width="350" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryB2_4a27c.jpg" _fl="" width="200" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryB3_5a8a8.jpg" _fl="" width="350" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryB4_cb7af.jpg" _fl="" width="350" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryB5_1bc43.jpg" _fl="" width="340" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryB6_53553.jpg" _fl="" width="450" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryB7_5ec46.jpg" _fl="" width="350" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryB8_8d183.jpg" _fl="" width="400" align="middle" /></div>\r\n<div align="center">&nbsp;</div>\r\n<div align="left"><span style="font-weight: bold;">Trong màu áo tuyển Pháp</span></div>\r\n<div align="left"><span style="font-weight: bold;"></span>&nbsp;</div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryP1_12d61.jpg" _fl="" width="360" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryP2_f5a43.jpg" _fl="" width="400" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryP3_761a1.jpg" _fl="" width="350" align="middle" /></div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryP4_52ff8.jpg" _fl="" width="350" align="middle" /></div>\r\n<div align="center">\r\n<div><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryP6_e9aa9.jpg" _fl="" width="350" align="middle" /></div>\r\n</div>\r\n<div align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/HenryP5_304ce.jpg" _fl="" width="250" align="middle" /></div>\r\n<div align="right"><span style="font-weight: bold;">Theo Dantri.com<br /></span></div>', 1, 7, 0, 38, '2012-03-24 14:30:53', 62, '', '2012-03-24 14:32:18', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:30:53', '0000-00-00 00:00:00', 'henrya7_a66c7.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 0, 0, 0, 0, 'robots=\nauthor='),
(11, 'Nhà cái đặt cửa cho trận chung kết kinh điển Real-Barca', 'nha-cai-dat-cua-cho-tran-chung-ket-kinh-dien-realbarca', '', '<p>Với xu hướng đánh giá cao Real và Barca tại Champions League mùa giải năm nay, cùng với việc hai gã khổng lồ của bóng đá TBN nằm ở hai nhánh khác nhau, do đó các nhà cái đều dự báo khả năng xảy ra một trận chung kết "siêu kinh điển" tại Allianz Arena.', '</p>\r\n<p>Sau khi kết quả bốc thăm chia cặp tại tứ kết Champions League kết thúc, tỉ lệ đặt cược mà các nhà cái dành cho các 8 đội bóng góp mặt gần như không có nhiều sự thay đổi so với trước đó. Xu hướng đánh giá cao hai gã khổng lồ của bóng đá Tây Ban Nha là Barcelona và Real Madrid tiếp tục được duy trì bên cạnh việc đứng cửa trên trước hai đối thủ tại tứ kết lần lượt là AC Milan và APOEL.</p>\r\n<div>&nbsp;</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/RIZNWFsXL4Thvv6ub1QL/Image/2012/03/Ro17031202_7f1b8.jpg" _fl="" width="450" align="middle" /></span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Các nhà cái dự báo một trận chung kết “siêu kinh điển” sẽ diễn ra tại Allizan Arena<span style="font-style: italic; font-family: Tahoma; font-size: 10pt;"></span></span></div>\r\n<p>Tính riêng các trận cặp đấu tại tứ kết thì ngoài Real Madrid, Barcelona thì Bayern Munich được các nhà cái đánh giá cao hơn hẳn đối thủ. Tất nhiên, chỉ cần nhìn vào các cặp đấu thì cũng dễ dàng nhận ra cặp Real Madrid-APOEL chính là trận tứ kết có tương quan về lực lượng chênh lệch nhất. Chính vì vậy, không có gì là khi các nhà cái đặt ra tỉ lệ chỉ là 1 ăn 1.03 cho những ai đặt cửa chiến thắng cho thầy trò Jose Mourinho.</p>\r\n<p>Đáng ngạc nhiên là cặp đấu tưởng chừng như cân bằng và thu hút được nhiều sự chú ý nhất tại từ kết giữa Barcelona và AC Milan, các nhà cái lại đánh giá rất cao Barcelona và đặt cửa thắng cho Barca chỉ sau mỗi Real Madrid, đội bóng chỉ phải gặp tí hon APOEL. Trong khi tỉ lệ trung bình mà các nhà cái đưa ra cho Barca chỉ là 1.15 thì những ai đặt cửa cho AC Milan thì có thể nhân 5 số tiền cược của mình nếu <span style="font-style: italic;">Rossoneri </span>chiến thắng.</p>\r\n<p>Trong khi đó, việc chỉ phải gặp Marseille cũng giúp “hùm xám xứ Bavaria” được đánh giá rất cao tại tứ kết với tỉ lệ trung bình 1 ăn 1.25. Tại cặp đấu cuối cùng, sự sa sút của Chelsea thời gian gần đây khiến các nhà cái chỉ đánh giá <span style="font-style: italic;">The Blues </span>chính là đội bóng cửa trên được đánh giá thấp nhất với tỉ lệ trung bình 1 ăn 1.50.</p>\r\n<p>Về tỉ lệ trung bình mà các nhà cái đưa ra cho cơ hội vô địch Champions League của 8 đội bóng góp mặt tại tứ kết, với việc Real Madrid và Barcelona được đánh giá cao nhất xuyên suốt cả mùa giải năm nay đồng thời nằm ở hai nhánh khác nhau sau lễ bốc thăm, điều đó cũng đồng nghĩa các nhà cái đánh giá rất cao khả năng xảy ra một trận “siêu kinh điển” trong trận chung kết Champions League 2011/12 diễn ra tại Allizan Arena.</p>\r\n<div>&nbsp;</div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/RIZNWFsXL4Thvv6ub1QL/Image/2012/03/Ro17031201_87912.jpg" _fl="" width="450" align="middle" /></span></div>\r\n<div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Tuy&nbsp;độc chiếm ngôi đầu tại La Liga nhưng ở Champions League, Real Madrid vẫn bị đánh giá thấp hơn đại kình địch Barcelona</span></div>\r\n<div>&nbsp;</div>\r\n<div>Bằng chứng là các nhà cái đặt tỉ lệ 1.50 cho cơ hội Barcelona lọt vào chung kết, trong khi Real Madrid chỉ kém hơn một ít với tỉ lệ 1.55. Tuy nhiên, về cơ hội đăng quang thì thầy trò Pep Guardiola được các nhà cái tin tưởng hơn hẳn đại kình địch của mình với tỉ lệ 1 ăn 2.25 so với 1 ăn 3.0 của <span style="font-style: italic;">Los Blancos.</span></div>\r\n<div>&nbsp;</div>\r\n<div>Đội bóng được đánh giá cao nhất sau hai gã khổng lồ của bóng đá TBN chính là đội chủ sân Allizan Arena Bayern Munich với tỉ lệ 1 ăn 2.50 cho việc lọt vào chung kết và 1 ăn 6.50 nếu đăng quang. Trong khi đó, vì phải đụng các nhà ĐKVĐ Barcelona tại tứ kết nên các nhà cái không đánh giá cao cửa vô địch cho AC Milan khi đặt hẳn tỉ lệ trung bình lên tới 1 ăn 20 cho những người đặt cửa <span style="font-style: italic;">Rossoneri </span>nếu họ đăng quang tại Allizan Arena.</div>\r\n<p><span style="font-style: italic; font-weight: bold;">Tỉ lệ cược trung bình cửa thắng cho các cặp đấu tại tứ kết Champions League 2011/12:</span></p>\r\n<p>APOEL - Real Madrid<br />1: APOEL 11.00<br />2: Real Madrid 1.03</p>\r\n<p>AC Milan - Barcelona:<br />1: AC Milan 5.00<br />2: FC Barcelona 1.15</p>\r\n<p>Marseille - Bayern Munchen:<br />1: Marseille 3.75<br />2: Bayern Munchen 1.25</p>\r\n<p>Benfica - Chelsea<br />1: Benfica 2.50<br />2: Chelsea 1.50</p>\r\n<p><span style="font-style: italic; font-weight: bold;">Tỉ lệ cược trung bình cho đội vô địch Champions League 2011/12:</span></p>\r\n<p>1: Barcelona 2.25<br />2: Real Madrid 3.00<br />3: Bayern Munich 6.50<br />4: Chelsea 10.00<br />5: AC Milan 20.00</p>\r\n<p style="text-align: right;"><span style="font-weight: bold;">Theo Dantri.com<br /></span></p>', -2, 7, 0, 39, '2012-03-24 14:33:23', 62, '', '2012-03-24 14:34:44', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:33:23', '0000-00-00 00:00:00', 'ro17031202_7f1b8.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 0, '', '', 0, 0, 0, 0, 0, 'robots=\nauthor='),
(12, 'Chung kết nội bộ hai cúp châu Âu cho người Tây Ban Nha?', 'chung-ket-noi-bo-hai-cup-chau-au-cho-nguoi-tay-ban-nha', '', '<p>Kết quả bốc thăm giúp các đại diện TBN không phải chạm trán nhau ở TK Champions League, Europa League và cơ hội về các trận chung kết nội bộ La Liga có thể xảy ra ở hai cúp châu Âu, khi 5 đại diện của họ đang được đánh giá khá cao.', '</p>\r\n<div style="margin: 0in 0in 0pt;">Bóng đá Tây Ban Nha thực sự có được chỗ đứng đáng nể ở châu Âu kể từ năm 2000 đến nay. Chỉ sau 12 năm, các đại diện La Liga đã giành 5 danh hiệu Champions League (Barcelona 2006, 2009, 2011, Real Madrid 2000, 2002) và 4 danh hiệu Europa League (Valencia 2004, Sevilla 2006, 2007 và Atletico 2010).</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/UjQgQgjU2baoHmB2Oa5Y/Image/2012/03/Bilbao1_f56d0.jpg" _fl="" width="450" align="middle" /><br />Real Madrid hoàn toàn có cơ sở tiến vào chung kết Champions League<br /></span></div>\r\n<p style="margin: 0in 0in 0pt;">Thành tích vô địch ở Champions League của người TBN vượt trội hơn hẳn Anh (2 lần), Italia (3 lần), Đức (1 lần), Bồ Đào Nha (1 lần) trong 12 năm gần đây nhất. Không những vậy, trong quá khứ bóng đá TBN còn không ít lần thể hiện sức mạnh tuyệt đối bằng số lượng đại diện đông đảo ở các vòng bán kết.</p>\r\n<p style="margin: 0in 0in 0pt;">Ở Champions League 1999-2000, Real Madrid, Valencia và Barcelona cùng góp mặt ở bán kết. Real Madrid loại Bayern Munich và đánh bại Valencia ở trận đấu cuối cùng. Tại UEFA Cup (đổi tên là Europa League từ năm 2010) 2006-07, 3 đại diện Liga có mặt ở bán kết là Osasuna, Espanyol và Sevilla, sau đó Sevilla lên ngôi vô địch.</p>\r\n<p style="margin: 0in 0in 0pt;">Năm nay, ngoại trừ Villarreal và Sevilla bị “bật bãi” sớm thì cả 5 đại diện còn lại là Real Madrid, Barcelona (Champions League), Valencia, Bilbao, Atletico (Europa League) cũng đều hiện diện ở tứ kết. Lá thăm không khiến họ phải giáp mặt nhau và cơ hội để người TBN bảo toàn lực lượng cho vòng bán kết là khá cao.</p>\r\n<p style="margin: 0in 0in 0pt;">Real Madrid chỉ phải gặp APOEL Nicosia, đối thủ yếu nhất ở tứ kết và với bản lĩnh của mình, cơ hội đi tiếp của “Kền kền trắng” là rất lớn. Thậm chí, thầy trò HLV Jose Mourinho còn có thể giành những thắng lợi tưng bừng để tạo đà cho các trận bán kết hứa hẹn là khốc liệt.</p>\r\n<div style="margin: 0in 0in 0pt;">Barcelona thì gặp khó khăn hơn, bởi đối thủ của họ là đại diện số 1 nước Ý AC Milan. Nhưng quá khứ và thực tại của Barca đều cho thấy Barca được đánh giá nhỉnh hơn khá nhiều. Hơn nữa, nếu thầy trò Guardiola biết phân phối sức đúng mực thì họ sẽ thẳng tiến vào bán kết.</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/UjQgQgjU2baoHmB2Oa5Y/Image/2012/03/Bilbao2_a89fe.jpg" _fl="" width="450" align="middle" /><br />Barcelona vẫn được đánh giá nhỉnh hơn Milan<br /></span></div>\r\n<p style="margin: 0in 0in 0pt;">La Liga còn phô trương lực lượng mạnh mẽ hơn ở Europa League khi họ chiếm tới 3/8 đại diện ở tứ kết. Lá thăm may mắn khiến họ không phải đụng độ nhau ở vòng 8 đội và nhìn vào tương qua các cặp đấu, người TBN hoàn toàn có thể bảo toàn lực lượng trước vòng bán kết.</p>\r\n<p style="margin: 0in 0in 0pt;">Valencia từng loại được PSV Eindhoven ấn tượng ở vòng 1/8, thì họ chẳng có lý do gì mà phải e ngại đại diện Hà Lan khác AZ Alkmaar. Với những cầu thủ đang vào phom như Soldado, Topal, Banega, Los Che được đánh giá nhỉnh hơn hẳn về mọi mặt và họ hoàn toàn có cơ hội bước tiếp.</p>\r\n<p style="margin: 0in 0in 0pt;">Atletico dù đã mất đi hai ngôi sao lớn Aguero-Forlan, nhưng họ chẳng hề suy yếu qua việc loại được Lazio khá sừng sỏ. Việc gặp Hannover 96, đối thủ yếu ở giải Đức là cơ hội để nhà vô địch Europa League 2001 tiến vào bán kết, khi CLB thành Madrid được đánh giá nhỉnh hơn về mọi mặt.</p>\r\n<div style="margin: 0in 0in 0pt;">Bilbao gặp nhiều khó khăn hơn khi phải giáp mặt Schalke 04, đây là cuộc đại chiến Đức-TBN thứ hai ở tứ kết Europa League năm nay. Schalke là 1 trong 4 thế lực lớn nhất Bundesliga hiện nay, họ cũng đặt nhiều tham vọng ở giải đấu này nhằm tránh mùa giải trắng tay.</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/UjQgQgjU2baoHmB2Oa5Y/Image/2012/03/Bilbao3_b8d58.jpg" _fl="" width="450" align="middle" /><br />Bilbao đang gây tiếng vang lớn sau khi loại được MU<br /></span></div>\r\n<p style="margin: 0in 0in 0pt;">Nhưng khi đã loại được MU sau hai trận thắng thuyết phục, Bilbao thậm chí còn được coi là ứng cử viên số 1 tại Europa League năm nay. Với dàn cầu thủ chất lượng như Llorente, De Marcos, Muniain hay Susaeta, dưới sự dẫn dắt của HLV tài ba như Bielsea, CLB xứ Basque là một tập thể khó bị đánh bại.</p>\r\n<p style="margin: 0in 0in 0pt;">Nếu vượt qua được chính mình, người TBN hoàn toàn có thể mơ đến viễn cảnh Real, Barca (Champions League), Atletico, Valencia, Bilbao (Europa League). Hơn nữa, nếu nhìn vào tương quan so với các đối thủ còn lại, các đại diện TBN còn được coi là các ứng cử viên sáng giá nhất cho chức vô địch.</p>\r\n<p style="margin: 0in 0in 0pt;">Trong lịch sử cúp châu Âu, chưa một quốc gia nào đóng góp cả 4 đại diện cho hai trận chung kết. Nhưng cơ hội đang thuộc về người TBN và người dân nước này có cơ sở để mơ về hai trận chung kết Real Madrid-Barcelona (Champions League) hay Bilbao-Atletico hoặc Valencia (Europa League).</p>\r\n<p style="text-align: right;"><span style="font-weight: bold;">Theo Dantri.com<br /></span></p>', -2, 7, 0, 39, '2012-03-24 14:35:23', 62, '', '2012-03-24 14:36:42', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:35:23', '0000-00-00 00:00:00', 'bilbao1_f56d0.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 0, '', '', 0, 0, 1, 0, 0, 'robots=\nauthor='),
(13, 'Cái dớp Tây Ban Nha tiếp tục ám ảnh "Quỷ đỏ" MU', 'cai-dop-tay-ban-nha-tiep-tuc-am-anh-quy-do-mu', '', '<p>Trận thua cay đắng Bilbao 2-3 ngay tại Old Trafford đã đe dọa nhiều đến hy vọng góp mặt tại tứ kết Europa League của MU. Một lần nữa thầy trò HLV Alex Ferguson lại lao đảo trước khắc tinh đến từ Tây Ban Nha.', 'Bilbao là một trong 4 đội bóng mạnh nhất TBN hiện nay, nhưng họ vẫn chưa đạt đến đẳng cấp của Real Madrid hay Barcelona. CLB xứ Basque là tập thể đoàn kết, có lối chơi chặt chẽ đến cứng rắn và quan trọng hơn, họ vẫn giữ được “chất TBN” kết hợp với lối đá bóng dài và chơi đầu tốt.</p>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/UjQgQgjU2baoHmB2Oa5Y/Image/2011/11/MU1_b5fff.jpg" _fl="" width="450" align="middle" /><br />MU gục ngã cay đắng dưới tay Bilbao ngay tại Old Trafford</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<p style="margin: 0in 0in 0pt;">Trước Bilbao, MU đã bị ngợp trước một đối thủ thực dụng và đầy tinh quá. Ngoài Rooney và De Gea, những vị trí còn lại của họ lép vé hoàn toàn so với đối thủ. Nhưng De Marcos, Susaeta, Munain hay Llorente đã chơi cực kỳ xuất thần và đánh gục “Quỷ đỏ” ngay tại Old Trafford.</p>\r\n<p style="margin: 0in 0in 0pt;">Bilbao không phải đối thủ quá mạnh, nhưng một lần nữa thầy trò Alex Ferguson bị ngợp khi đối diện người Tây Ban Nha. Sự khác biệt về phong cách khiến “Quỷ đỏ” đánh mất chính mình, trong khi Bilbao đã thành công trong việc khoét sâu vào các điểm yếu của MU.</p>\r\n<p style="margin: 0in 0in 0pt;">Thầy trò Alex Ferguson vẫn còn 90 phút lượt về để quyết định số phận, nhưng sức ép lớn tại San Mames chưa khi nào dễ chịu với các đội bóng lớn. Và Fergie hoàn toàn có thể âu lo về cơ hội có mặt tại tứ kết, bởi trong quá khứ họ luôn bị đo ván trong các cuộc giáp chiến với người TBN.</p>\r\n<p style="margin: 0in 0in 0pt;">MU dù ở thời kỳ đỉnh cao nhất, họ vẫn luôn bị dớp khi đối mặt các CLB Tây Ban Nha. Lật lại lịch sử năm 2000, MU đang là ĐKVĐ và được đánh giá cao hơn hẳn Real Madrid có phần suy yếu, nhưng rút cuộc “Quỷ đỏ” vẫn nhận thất bại đầy cay đắng cả về tỷ số lẫn lối chơi.</p>\r\n<div style="margin: 0in 0in 0pt;">Ở lượt đi tứ kết tại Bernabeu, MU bị ép sân hoàn toàn nhưng họ vẫn đứng vững khi hòa 0-0 và cơ hội đi tiếp trở nên rộng mở. Tuy nhiên, khi trở về Old Trafford “Quỷ đỏ” lại chơi sa sút thậm tệ, họ bị dẫn trước đến 3-0 (Redondo, Raul tỏa sáng) và hai bàn gỡ sau đó chỉ mang ý nghĩa danh dự, khi “Kền kền trắng” làm chủ hoàn toàn cục diện.</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/UjQgQgjU2baoHmB2Oa5Y/Image/2011/11/MU2_509a3.jpg" _fl="" width="450" align="middle" /><br />Nguy cơ vắng mặt ở tứ kết Europa League đang hiển hiện trước mắt MU</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<p style="margin: 0in 0in 0pt;">3 năm sau, cũng ở tứ kết Champions League một lần nữa MU lại ôm hận trước Real Madrid. CLB Hoàng gia dưới sự dẫn dắt của Del Bosque thực sự là thế lực lớn thời điểm đó, khi thắng MU 3-1 ở lượt đi và dù thất bại 3-4 tại Old Trafford (Ronaldo béo lập hattrick), Los Blancos vẫn giành quyền đi tiếp.</p>\r\n<p style="margin: 0in 0in 0pt;">Trận thua trước Real Madrid là giai đoạn bước ngoặt khép lại chu kỳ thành công của MU thế hệ Beckham, Roy Keane buộc Alex Ferguson phải thay máu lực lượng. Sau đó, “Quỷ đỏ” không còn là thế lực ở nước Anh khi liên tiếp nhìn Arsenal rồi Chelsea lên ngôi vô địch.</p>\r\n<p style="margin: 0in 0in 0pt;">Đến thế hệ Rooney, MU lại ôm hận trước đội bóng Tây Ban Nha khác là Barcelona. Trong hai trận chung kết Champions League 2009 và 2011, MU thể hiện hình ảnh kém cỏi hơn hoàn toàn, bị cuốn vào lối chơi của đối thủ, đồng thời không tạo ra được bất kỳ sự phản kháng nào.</p>\r\n<div style="margin: 0in 0in 0pt;">HLV Alex Ferguson luôn thừa nhận MU không ngán bất kỳ đối thủ nào, nhưng Real Madrid lẫn Barcelona đều là khắc tinh khiến “Quỷ đỏ” e sợ. Hai thế lực lớn ở xứ sở bò tót có phong cách tấn công quyến rũ, kiểm soát thế trận khá tốt và cách chơi đó thực sự khắc chế MU.</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;">&nbsp;</div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/UjQgQgjU2baoHmB2Oa5Y/Image/2011/11/MU3_b8a94.jpg" _fl="" width="450" align="middle" /><br />Messi và Barca nhiều lần khiến MU phải ôm hận</span></div>\r\n<div style="margin: 0in 0in 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;"></span>&nbsp;</div>\r\n<p style="margin: 0in 0in 0pt;">Ở các vòng knock-out, MU cũng hai lần loại được cái đại diện Tây Ban Nha nhưng họ được giúp sức khá nhiều may mắn. Trong thắng lợi chung cuộc 5-2 trước Deportivo ở tứ kết mùa bóng 2001-02, đó là thời điểm MU có được sự hưng phấn cao độ, còn Deportivo bắt đầu có dấu hiệu đi xuống.</p>\r\n<p style="margin: 0in 0in 0pt;">Còn trong chiến thắng 1-0 trước Barca ở bán kết mùa giải 2007-08, đó là quãng thời gian tồi tệ nhất của CLB xứ Catalan trong những ngày cuối triều đại Rijkaard. Ronaldinho sa sút thê thảm, trong khi Lionel Messi mới hồi phục chấn thương. Kể cả khi Barca đi xuống, họ vẫn ép sân toàn diện MU trong cả 2 trận đấu, họ chỉ thua bởi sự xuất thần của Paul Scholes và có phần kém may mắn.</p>\r\n<p style="margin: 0in 0in 0pt;">Không chỉ Real Madrid và Barcelona, lúc này một đội bóng cổ hủ như Bilbao hoàn toàn có thể là khắc tinh với MU. Nếu không vượt qua được chính mình tại xứ Basque, có lẽ nỗi đau mang tên Tây Ban Nha sẽ vẫn còn ám ảnh “Quỷ đỏ” thành Manchester.</p>\r\n<p style="text-align: right;"><span style="font-weight: bold;">Theo Dantri.com<br /></span></p>', -2, 7, 0, 39, '2012-03-24 14:37:19', 62, '', '2012-03-24 14:38:43', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:37:19', '0000-00-00 00:00:00', 'mu1_b5fff.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 0, '', '', 0, 0, 0, 0, 0, 'robots=\nauthor='),
(14, 'Arsene Wenger: “Sự tự tin đang trở lại với Arsenal”', 'arsene-wenger-su-tu-tin-dang-tro-lai-voi-arsenal', '', '<p>Chiến thắng quan trọng trước Everton đã giúp The Gunners vượt qua Tottenham để chiếm lấy vị trí thứ 3 trên BXH Premiership. Và theo “giáo sư” Wenger, Arsenal của ông hoàn toàn có thể tiếp tục duy trì phong độ cao một khi đã lấy lại sự tự tin cần thiết.', '</p>\r\n<div>Dù chắc chắn sẽ phải chấp nhận cảnh trắng tay mùa giải thứ 7 liên tiếp song các CĐV tại Emirates vẫn đang có lí do để mà lạc quan khi chứng kiến đà hồi sinh mạnh mẽ thời gian qua của Arsenal. Các “pháo thủ” thành London đã kéo dài chuỗi ngày vui của mình lên con số 5 bằng việc đánh bại Everton 1-0 ngay tại Goodison Park.</div>\r\n<div>&nbsp;</div>\r\n<p align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/lnqpQeBYCt45LSBySE/Image/2012/03/wenger_882bf.jpg" _fl="" width="450" align="middle" /></p>\r\n<p align="center">HLV Wenger đang rất lạc quan về tình hình của Arsenal</p>\r\n<p>3 điểm giành được trước The Toffees đã đưa Robin Van Persie cùng các đồng đội vượt mặt “gã hàng xóm” Tottenham để chen chân vào Top 3 tại Premier League. Kết quả này khiến HLV Wenger cảm thấy rất hài lòng và củng cố thêm niềm tin cho thầy trọ họ trong chặng đường còn lại của mùa bóng.</p>\r\n<p>Trả lời phỏng vấn trên Sky Sport, chiến lược gia lão luyện người Pháp cho biết: “Khi sự tự tin đã quay lại với đội bóng, các bạn có thể thấy rằng rất nhiều cầu thủ của chúng tôi (ngoài Robin Van Persie) có thể ghi bàn. Tôi cảm nhận được sự biến chuyển trong tinh thần của Arsenal và nó đến sau những kết quả tích cực gần đây. Chúng tôi đã thực hiện một vài cú lội ngược dòng và điều đó tạo nên niềm tin mới”.</p>\r\n<p>Việc chiếm được vị trí thứ 3 từ tay Tottenham cũng giúp ông thầy 62 tuổi tỏ ra đầy tự tin vào viễn cảnh tương sáng của The Gunners trong phần còn lại của mùa giải này: “Với chúng tôi, mỗi khi nhìn vào lịch thi đấu và biết rằng những trận đấu sắp tới sẽ rất khó khăn thì việc vượt qua được chúng càng củng cố niềm tin cho CLB.</p>\r\n<p>Chúng tôi biết được rằng cần duy trì sự tập trung cho mỗi trận đấu kể từ nay cho đến hết mùa bóng. Arsenal đã từng trải qua một giai đoạn rất khó khăn và tôi tin rằng toàn đội đã học được nhiều điều quý giá từ nó”, “giáo sư” kết luận.</p>\r\n<div align="right"><span style="font-weight: bold;">Theo Dantri.com<br /></span></div>\r\n<p><span style="font-family: Tahoma; font-size: 10pt;"></span></p>', -2, 7, 0, 40, '2012-03-24 14:39:23', 62, '', '2012-03-24 14:40:35', 62, 0, '0000-00-00 00:00:00', '2012-03-24 14:39:23', '0000-00-00 00:00:00', 'wenger_882bf.jpg', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 0, '', '', 0, 1, 1, 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `hot`, `special`, `systems`, `metadata`) VALUES
(15, 'Stoke - Man City: “Pháo đài” Britannia đang đợi...', 'stoke-man-city-phao-dai-britannia-dang-doi', '', '<p>Sau trận thắng nghẹt thở trước Chelsea ở giữa tuần qua, Man City sẽ phải hành quân tới Britannia đêm nay. Và chuyến làm khách trước Stoke tại vòng 30 này đang được đánh giá là đầy dẫy những khó khăn chờ đợi thày trò Mancini.', '</p>\r\n<div style="margin: 0cm 0cm 0pt;">Dù có được chiến thắng cuối tuần qua trước Chelsea, nhưng Man City cũng khiến các cổ động viên của đội bóng này đứng ngồi không yên trong suốt cả quãng thời gian dài của trận đấu. Và cảm giác lo âu ấy chỉ được trút bỏ trong đúng 12 phút cuối trận, khi Man City có được 2 bàn thắng để lội ngược dòng trước Chelsea. Rõ ràng đã chiến thắng song vẫn còn chút lo lắng cho lối chơi của Man City.</div>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/StokeMancity_f2d3d.jpg" _fl="" width="450" align="middle" /></div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Tevez đã trở lại, nhưng anh cần thời gian để tìm lại phong độ đỉnh cao</span></div>\r\n<p style="margin: 0cm 0cm 0pt;">Dường như đội chủ sân Etihad đang có vấn đề với hàng công. Những Aguero, Dzeko, Balotelli từng “làm mưa, làm gió” trong các trận hồi đầu mùa thì hai trận đấu gần nhất tại Premier League lại tỏ ra khá vô duyên trước khung thành đối phương. Có lẽ một cuộc khủng hoảng nhỏ về phong độ đang diễn ra ở trên hàng công của đội bóng này.&nbsp;&nbsp;</p>\r\n<table style="width: 193px; border-collapse: collapse; background: none repeat scroll 0% 0% whitesmoke; height: 265px;" border="1" cellpadding="0" cellspacing="0" align="right">\r\n<tbody>\r\n<tr style="height: 11.85pt;">\r\n<td style="border-width: 1pt; border-style: inset; border-color: #005a87; padding: 2.25pt; width: 147.15pt; background: none repeat scroll 0% 0% #005a87; height: 11.85pt;" width="196">\r\n<p style="text-align: center; margin: 0cm 0cm 0pt;" align="center"><span style="font-family: Tahoma; color: white; font-size: 10pt; font-weight: bold;">V</span><span style="font-family: Tahoma; color: white; font-size: 10pt; font-weight: bold;">òng </span><span style="font-family: Tahoma; color: white; font-size: 10pt; font-weight: bold;">30 Premier League</span><span style="font-family: Tahoma; color: white; font-size: 10pt;"></span></p>\r\n</td>\r\n</tr>\r\n<tr style="height: 124.1pt;">\r\n<td style="border-width: medium 1pt 1pt; border-style: none inset inset; border-color: #ece9d8 #005a87 #005a87; padding: 2.25pt; width: 147.15pt; background: none repeat scroll 0% 0% transparent; height: 124.1pt;" width="196">\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">Thứ 7, 24/3 </span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">Chelsea - Tottenham <span style="font-style: italic;">19h45</span></span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">Arsenal - Aston Villa <span style="font-style: italic;">22h</span></span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">Bolton - Blackburn</span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">Liverpool - Wigan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">Norwich</span><span style="font-family: Tahoma; font-size: 11pt;"> - Wolves </span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">Sunderland</span><span style="font-family: Tahoma; font-size: 11pt;"> - QPR </span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">Swansea</span><span style="font-family: Tahoma; font-size: 11pt;"> - Everton</span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">Chủ nhật, 25/3</span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">Stoke - Man City <span style="font-style: italic;">0h30</span></span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">West Brom</span><span style="font-family: Tahoma; font-size: 11pt;"> - Newcastle <span style="font-style: italic;">23h</span></span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">Thứ 3, 27/3 </span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-family: Tahoma; font-size: 11pt;">Man Utd - Fulham <span style="font-style: italic;">3h</span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style="margin: 0cm 0cm 0pt;">Mancini chắc chắn sẽ rất nhiều lo lắng dành cho tuyến cao nhất của đội. Kể cả bây giờ ông đã có Tevez trở lại. Tiền đạo người Argentina là người tung ra đường chuyền quyết định để Nasri ghi bàn đem lại chiến thắng 2-1 trước Chelsea, nhưng ngoài tình huống đó, anh tỏ ra khá nặng nề trong các tình huống di chuyển. Bản thân Mancini cũng thừa nhận, Tevez cần thêm nhiều trận đấu nữa để tìm lại phong độ đỉnh cao.</p>\r\n<div style="margin: 0cm 0cm 0pt;">Man City vừa làm nên một kỷ lục mới tại Premier League, với chuỗi 20 trận toàn thắng trên sân nhà. Nhưng đó là tại thánh địa Etihad, còn những trước làm khách Man City lại không duy trì được phong độ tốt như thế. Chuyến làm khách gần nhất, Man City phải nếm trải thất bại đầy cay đắng và uất hận trước Swansea.</div>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/Britiania_88eba.jpg" _fl="" width="450" align="middle" /></div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Britania đang trở thành "Pháo đài" khiến các đội bóng lớn mất điểm</span></div>\r\n<p style="margin: 0cm 0cm 0pt;">Lần đến Britannia này cũng không dễ dàng gì đối với Man City. Đội chủ nhà là một đội bóng có lối chơi khá khó chịu với các đội bóng lớn. Đoàn quân của Tony Pulis thể hiện chiến thuật rất rõ ràng trong khi phải đối đầu với các “đại gia” đó là phòng ngự chặt chẽ và chờ đợi cơ hội để phản công. Lối chơi đeo bám sát của đội chủ nhà đem lại cho họ những kết quả tương đối tốt.</p>\r\n<p style="margin: 0cm 0cm 0pt;">Nhìn vào bảng thành tích từ đầu mùa của Stoke City trước đại gia của Premier League thật đáng nể. Họ giành được 4 điểm/2 trận trước Tottenham và tương tự là trước Liverpool, họ cũng khiến Chelsea hay Man Utd đánh rơi 2 điểm ở trận lượt đi và ở trận lượt về với Chelsea vừa diễn ra cách đây 2 vòng, rất vất vả <span style="font-style: italic;">The Blues</span> mới có thể thắng 1-0 trước 10 người của Stoke.</p>\r\n<div style="margin: 0cm 0cm 0pt;">Nói chung Stoke rất dễ khiến các đội bóng lớn mất điểm, đặc biệt tại sân nhà, Man Utd, Chelsea, Liverpool và cả Tottenham đều không thể giành chiến thắng tại “Pháo đài” Britannia. Chắc chắn nhìn vào bảng thành tích này Man City không thể coi thường đối thủ của họ ở vòng đấu này. Đoàn quân của Mancini đến với trận đấu này với chỉ một mục tiêu duy nhất đó là 3 điểm.</div>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center"><img style="margin: 5px;" src="http://dantri4.vcmedia.vn/elVaF199bomfqIyKul5/Image/2012/03/StokeMancity1_7a210.jpg" _fl="" width="450" align="middle" /></div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center"><span style="font-family: Tahoma; font-size: 10pt;">Không có được 3 điểm coi như Man City đã thua</span></div>\r\n<p style="margin: 0cm 0cm 0pt;">Nếu không có được điểm số tối đa đó thì trận đấu sẽ được xem như thất bại của Man City. Giành được trọn 3 điểm sẽ giúp Man City trở lại ngôi đầu bảng và gây áp lực ngược lại cho Man Utd, đội sẽ có trận đấu muộn nhất vòng với Fulham tại Old Trafford. Và trận thắng lợi 3-0 ở trận lượt đi có thể sẽ là điểm tựa tâm lý tốt cho Man City bước vào trận đấu đêm nay.</p>\r\n<p style="margin: 0cm 0cm 0pt;">Tại chuyến đi này Mancini sẽ yên tâm hơn về hàng thủ khi đội trưởng Kompany đã trở lại sau chấn thương. Như vậy cầu thủ người Bỉ sẽ hợp với Toure để trở thành cặp trung vệ của Man City tại trận đấu vòng 30 này. Thiếu vắng duy nhất của đội khách đó là Joe Lescott, người vẫn chưa thể bình phục chấn thương đồi gối, nhưng dù sao Kompany trở lại cũng giúp Mancini hoàn toàn gạt bỏ lo âu về tuyến phòng ngự.</p>\r\n<p style="margin: 0cm 0cm 0pt;">Trong khi đó chấn thương của Salif Diao gặp phải ở trận đấu giữa tuần qua với Tottenham khiến anh sẽ bỏ lỡ trận đấu này, nhưng Whelan đã trở lại và Pulis có thể hoàn toàn yên tâm ở hàng tiền vệ. Fuler, tội đồ khiến Stoke phải chơi 75 phút ít hơn Chelsea 1 người cũng sẽ tiếp tục ngồi ngoài trận này do án treo giò 3 trận.</p>\r\n<p style="margin: 0cm 0cm 0pt;">7 trận gần đây, ngoại trừ chuyến làm khách tới Chelsea, các trận còn lại Stoke đều ghi bàn. Và đêm nay, cùng lợi thế được thi đấu trên sân nhà họ sẽ trở thành một thách thức thực sự đối với Man City.</p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Đội hình ra sân dự kiến</span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">&nbsp;</span></p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Stoke City</span>: Begovic; Wilkinson, Huth, Shawcross, Wilson; Shotton, Whelan, Whitehead, Etherington; Crouch, Walters</p>\r\n<p style="margin: 0cm 0cm 0pt;"><span style="font-weight: bold;">Man</span><span style="font-weight: bold;">City</span>: Hart; Richards, Kompany, K Toure, Clichy; Y Toure, Barry; Silva, Aguero, Nasri; Balotelli</p>\r\n<div style="margin: 0cm 0cm 0pt;">&nbsp;</div>\r\n<div style="margin: 0cm 0cm 0pt;" align="center">\r\n<table style="width: 445px; border-collapse: collapse;" border="0" cellpadding="0" cellspacing="0">\r\n<tbody>\r\n<tr style="height: 14.25pt;">\r\n<td style="border-width: 1pt; border-style: inset; border-color: #016a99; padding: 1.5pt; width: 100%; background: none repeat scroll 0% 0% #016a99; height: 14.25pt;" colspan="8" width="100%">\r\n<p style="text-align: center; margin: 0in 0in 0pt;" align="center"><span style="font-family: Verdana; color: white; font-size: 10pt; font-weight: bold;">Bảng xếp hạng Premier League 2011/12</span><span style="font-family: Verdana; color: black; font-size: 10pt;"></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border-width: medium 1pt 1pt; border-style: none inset inset; border-color: #ebe9ed #4a84b5 #4a84b5; padding: 1.5pt; width: 5.18%; background: none repeat scroll 0% 0% #c0dbf2;" width="5%">\r\n<p style="text-align: center; margin: 0in 0in 0pt;" align="center"><span style="font-family: Verdana; color: black; font-size: 10pt; font-weight: bold;">TT</span><span style="font-family: Verdana; color: black; font-size: 10pt;"></span></p>\r\n</td>\r\n<td style="border-width: medium 1pt 1pt medium; border-style: none inset inset none; border-color: #ebe9ed #4a84b5 #4a84b5 #ebe9ed; padding: 1.5pt; width: 35.22%; background: none repeat scroll 0% 0% #c0dbf2;" width="35%">\r\n<p style="text-align: center; margin: 0in 0in 0pt;" align="center"><span style="font-family: Verdana; color: black; font-size: 10pt; font-weight: bold;">Đội</span><span style="font-family: Verdana; color: black; font-size: 10pt;"></span></p>\r\n</td>\r\n<td style="border-width: medium 1pt 1pt medium; border-style: none inset inset none; border-color: #ebe9ed #4a84b5 #4a84b5 #ebe9ed; padding: 1.5pt; width: 11.66%; background: none repeat scroll 0% 0% #c0dbf2;" width="11%">\r\n<p style="text-align: center; margin: 0in 0in 0pt;" align="center"><span style="font-family: Verdana; color: black; font-size: 10pt; font-weight: bold;">Điểm</span><span style="font-family: Verdana; color: black; font-size: 10pt;"></span></p>\r\n</td>\r\n<td style="border-width: medium 1pt 1pt medium; border-style: none inset inset none; border-color: #ebe9ed #4a84b5 #4a84b5 #ebe9ed; padding: 1.5pt; width: 8.58%; background: none repeat scroll 0% 0% #c0dbf2;" width="8%">\r\n<p style="text-align: center; margin: 0in 0in 0pt;" align="center"><span style="font-family: Verdana; color: black; font-size: 10pt; font-weight: bold;">Trận</span><span style="font-family: Verdana; color: black; font-size: 10pt;"></span></p>\r\n</td>\r\n<td style="border-width: medium 1pt 1pt medium; border-style: none inset inset none; border-color: #ebe9ed #4a84b5 #4a84b5 #ebe9ed; padding: 1.5pt; width: 9.72%; background: none repeat scroll 0% 0% #c0dbf2;" width="9%">\r\n<p style="text-align: center; margin: 0in 0in 0pt;" align="center"><span style="font-family: Verdana; color: black; font-size: 10pt; font-weight: bold;">T</span><span style="font-family: Verdana; color: black; font-size: 10pt;"></span></p>\r\n</td>\r\n<td style="border-width: medium 1pt 1pt medium; border-style: none inset inset none; border-color: #ebe9ed #4a84b5 #4a84b5 #ebe9ed; padding: 1.5pt; width: 6.04%; background: none repeat scroll 0% 0% #c0dbf2;" width="6%">\r\n<p style="text-align: center; margin: 0in 0in 0pt;" align="center"><span style="font-family: Verdana; color: black; font-size: 10pt; font-weight: bold;">H</span><span style="font-family: Verdana; color: black; font-size: 10pt;"></span></p>\r\n</td>\r\n<td style="border-width: medium 1pt 1pt medium; border-style: none inset inset none; border-color: #ebe9ed #4a84b5 #4a84b5 #ebe9ed; padding: 1.5pt; width: 7.96%; background: none repeat scroll 0% 0% #c0dbf2;" width="7%">\r\n<p style="text-align: center; margin: 0in 0in 0pt;" align="center"><span style="font-family: Verdana; color: black; font-size: 10pt; font-weight: bold;">B</span><span style="font-family: Verdana; color: black; font-size: 10pt;"></span></p>\r\n</td>\r\n<td style="border-width: medium 1pt 1pt medium; border-style: none inset inset none; border-color: #ebe9ed #4a84b5 #4a84b5 #ebe9ed; padding: 1.5pt; 