INSERT INTO `jos_jobboard_emailmsg` (`id`, `type`, `subject`, `body`) VALUES
(1, 'userrejected', 'Your job application for: [jobtitle]', 'Dear [toname],\r\n\r\nThank you for expressing an interest in applying for a position with [fromname].\r\n\r\nIt is with regret that we inform you that your application was not successful. We will however keep your resume details on our database and contact you should any suitable vacancies arise.  \r\n\r\nWe wish you everything of the best for the future.\r\n\r\nYours Sincerely\r\n[fromname]'),
(2, 'adminnew', 'Job post [jobtitle] created', 'The following job post has been created.\r\n\r\nJob Title: [jobtitle]\r\n\r\nJob Department: [department]\r\nJob Location: [location]\r\nJob Status: [status]\r\n\r\nCreated by [appladmin]'),
(10, 'userapproved', 'Job Application [jobtitle] approved', 'Dear [toname],\r\n\r\nIt is with great pleasure to inform you that you have been awarded the position of [jobtitle] with [fromname].\r\n\r\nYou will be contacted regarding further details.\r\n\r\nYours Sincerely\r\n[fromname]\r\n'),
(13, 'adminupdate_application', 'Job application for [toname] [tosurname] updated', 'The following job application has been updated:\r\n\r\nApplicant Name: [toname] [tosurname]\r\nStatus: [applstatus]\r\nJob Title: [jobtitle]\r\nJob ID: [jobid]\r\nJob Department: [department]\r\n\r\nUpdated by [appladmin]'),
(3, 'adminsms', 'Job post: [jobtitle] updated', 'Title:[jobtitle]\r\nLocation:[location]\r\n\r\nRegards,\r\n[fromname]'),
(4, 'adminupdate', 'Job post [jobtitle] updated', 'The following job post has been updated:\r\n\r\nJob Title: [jobtitle]\r\nJob ID: [jobid]\r\nJob Department: [department]\r\nJob Location: [location]\r\nJob Status: [status]\r\n\r\nUpdated by [appladmin]\r\n'),
(14, 'adminnew_application', 'New job application for [applname] [applsurname]', 'The following job application has been created:\r\n\r\nApplicant Name: [applname] [applsurname]\r\nStatus: [applstatus]\r\nJob Title: [jobtitle]\r\nJob ID: [jobid]\r\nJob Department: [department]\r\n-----------------------------\r\nCV/Resume Title: [appltitle]\r\n\r\nCover Note:\r\n***********\r\n[applcovernote]\r\n***********\r\n\r\nSubmitted by [appladmin]'),
(15, 'adminupdate_unsolicited', 'Unsolicited application for [toname] [tosurname] updated', 'The following unsolicited application has been updated:\r\n\r\nApplicant Name: [toname] [tosurname]\r\nApplicant ID: [applicantid]\r\n\r\nUpdated by [appladmin]'),
(16, 'adminnew_unsolicited', 'New unsolicited cv/resume', 'A new unsolicited CV/Resume has been submitted.\r\n\r\nApplicant Name: [toname] [tosurname]\r\nCV/Resume Title: [cvtitle]\r\n\r\n------------------------------------\r\n[fromname]'),
(5, 'unsolicitednew', '[toname], your CV ([cvtitle])has been received', 'Dear [toname],\r\n\r\nThank you for submitting your CV to [fromname]. \r\n\r\nYour application will be reviewed and we will get in touch with you if a suitable position becomes available.\r\n\r\nYours sincerely,\r\n[fromname]\r\n\r\nPlease do not respond to this message. It is automatically generated and is for information purposes only.'),
(6, 'usernew', 'Job application for [jobtitle]-[location] received', 'Dear [toname],\r\n\r\nThank you for applying for the following position with [fromname]:\r\n[jobtitle] \r\n\r\nYour application is on file and will be reviewed.\r\n\r\nShould you not hear from us within 14 days, please consider your application unsuccessful.\r\n\r\nThank you,\r\n[fromname]\r\n\r\nPlease do not respond to this message. It is automatically generated and is for information purposes only.'),
(7, 'sharejob', 'Online job recommendation...', 'Hello,\r\n\r\nI found this great job and thought you would be interested in viewing the full Job advert online...'),
(8, 'sharejpriv', 'Online job recommendation...', '\r\n\r\n[jobtitle] - [location] \r\n\r\nClick on the following link to view the job details \r\n'),
(9, 'usersms', 'Job Application [jobtitle] received', 'Title:[jobtitle]\r\nLocation:[location]\r\n\r\nRegards,\r\n[fromname]');
